		/// <summary>
		/// Contains default values to be used throughout the code.
		/// </summary>
		public static class DefaultValues
		{
			/// <summary>
			/// Represents the date format to be used in DateTime.ToString methods of the faults XML data.
			/// </summary>
			public const string TimeFormat = "yyyy/MM/dd HH:mm:ss";
		}

		/// <summary>
		/// Responsible for serialization operations in QActions.
		/// </summary>
		public static class SerializationEngine
		{
			/// <summary>
			/// Takes a text representation of XML, and attempts to convert it into an object of type T.
			/// </summary>
			/// <typeparam name="T">The type of object to be deserialized into.</typeparam>
			/// <param name="protocol">Link with SLProtocol process. Used for logging.</param>
			/// <param name="text">The text representation of XML to be converted into an object of type T.</param>
			/// <returns>The converted object, or the default value of type T in case of failure.</returns>
			public static T DeserializeIntoObject<T>(SLProtocolExt protocol, string text)
			{
				try
				{
					XmlSerializer serializingEngine = new XmlSerializer(typeof(T));

					using (TextReader reader = new StringReader(text))
					{
						return (T)serializingEngine.Deserialize(reader);
					}
				}
				catch (Exception e)
				{
					LogHandler.LogException(protocol, e);
					LogHandler.LogInformation(protocol, "Problematic data: " + text);
					return default(T);
				}
			}
		}

		/// <summary>
		/// Represents the base node of the faults XML data received from the faults command.
		/// </summary>
		[Serializable]
		[XmlType(AnonymousType = true)]
		[XmlRoot(ElementName = "faults", Namespace = "", IsNullable = false)]
		public class Faults
		{
			[XmlElement("status")]
			public FaultsStatus[] Status { get; set; }
		}

		/// <summary>
		/// Represents each individual XML fault received from the device.
		/// </summary>
		[Serializable]
		[XmlType(AnonymousType = true)]
		public class FaultsStatus
		{
			[XmlElement("name")]
			public string Name { get; set; }

			[XmlElement("details")]
			public string Details { get; set; }

			[XmlElement("setsince")]
			public string SetSinceText
			{
				get
				{
					return SetSince != null ? SetSince.Value.ToString(DefaultValues.TimeFormat) : default(DateTime).ToString(DefaultValues.TimeFormat);
				}

				set
				{
					DateTime result;

					if (DateTime.TryParse(value, out result))
					{
						SetSince = result;
					}
					else
					{
						SetSince = default(DateTime);
					}
				}
			}

			[XmlElement("type")]
			public FaultType Type { get; set; }

			/// <summary>
			/// Gets or sets the DateTime format of the SetSince XML tag. This is generated from the SetSinceText property.
			/// </summary>
			[XmlIgnore]
			public DateTime? SetSince { get; set; }
		}

		/// <summary>
		/// This is the base node XML data received through the ECC command.
		/// </summary>
		[Serializable]
		[XmlType(AnonymousType = true)]
		[XmlRoot(ElementName = "device", Namespace = "", IsNullable = false)]
		public class EccData
		{
			[XmlElement("ecc")]
