			[XmlElement("displayBoardName")]
			public string DisplayBoardName { get; set; }

			[XmlElement("location")]
			public string Location { get; set; }

			[XmlElement("displayItemName")]
			public string DisplayItemName { get; set; }

			[XmlElement("type")]
			public EccType Type { get; set; }

			[XmlElement("value")]
			public string Value { get; set; }

			[XmlElement("cardslot")]
			public string CardSlot { get; set; }

			[XmlElement("displayOrderFlag")]
			public int DisplayOrderFlag { get; set; }
		}

		/// <summary>
		/// Represents a group of Radio Frequency Inputs that the Cisco D9800 has available.
