//---------------------------------
// LogHandler.cs
//---------------------------------
namespace Skyline.Protocol.Logging
{
	using System;
	using Skyline.DataMiner.Scripting;

	/// <summary>
	/// Responsible for normalizing log-writing on the device's log.
	/// </summary>
	public static class LogHandler
	{
		/// <summary>
		/// Logs the exception information using a default look.
		/// </summary>
		/// <param name="protocol">Link with SLProtocol process.</param>
		/// <param name="e">The exception object to log.</param>
		public static void LogException(this SLProtocol protocol, Exception e)
		{
			LogException(protocol, e, "Run");
		}

		/// <summary>
		/// Logs the exception information using a default look.
		/// </summary>
		/// <param name="protocol">Link with SLProtocol process.</param>
		/// <param name="e">The exception object to log.</param>
		/// <param name="location">Which method in the QAction was this exception generated. By default, it's in the Run method.</param>
		public static void LogException(this SLProtocol protocol, Exception e, string location)
		{
			string result = string.Format("QA{0}|{1}| There was an exception while running this QAction.\n{2}\n{3}", protocol.QActionID, location, e.Message, e.StackTrace);

			if (e.InnerException != null)
			{
				result += string.Format("\nInner exception: {0}\n{1}", e.InnerException.Message, e.InnerException.StackTrace);
			}

			protocol.Log(result, LogType.Error, LogLevel.NoLogging);
		}

		/// <summary>
		/// Logs an information message on the logs, using a standardized message format. It writes the message with the Run method location.
		/// </summary>
		/// <param name="protocol">Link with Skyline DataMiner.</param>
		/// <param name="message">The message to be written on the logs.</param>
		public static void LogInformation(SLProtocolExt protocol, string message)
		{
			LogInformation(protocol, message, "Run");
		}

		/// <summary>
		/// Logs an information message on the logs, using a standardized message format.
		/// </summary>
		/// <param name="protocol">Link with Skyline DataMiner.</param>
		/// <param name="message">The message to be written on the logs.</param>
		/// <param name="location">The method name where the message was generated.</param>
		public static void LogInformation(SLProtocolExt protocol, string message, string location)
		{
			protocol.Log(string.Format("QA{0}|{1}| {2}", protocol.QActionID, location, message), LogType.Information, LogLevel.NoLogging);
		}
	}
}
