//---------------------------------
// PollingManager\GenericAPI\Enums\Trigger.cs
//---------------------------------
namespace Skyline.DataMiner.PollingManager
{
	using System;

	using Skyline.DataMiner.Scripting;

	/// <summary>
	/// Represents triggers of the <see cref="PollingmanagerQActionTable"/>.
	/// </summary>
	public enum Trigger
	{
		Interval = Parameter.Write.pollingmanager_interval_60053,
		IntervalType = Parameter.Write.pollingmanager_intervaltype_60055,
		Poll = Parameter.Pollingmanager.Pid.Write.pollingmanager_poll_60059,
		State = Parameter.Write.pollingmanager_state_60060,
	}

	/// <summary>
	/// Extension class for <see cref="Trigger"/>.
	/// </summary>
	public static class TriggerExtensions
	{
		/// <summary>
		/// Converts <see cref="Trigger"/> to its corresponding <see cref="Column"/> value.
		/// </summary>
		/// <param name="trigger">Trigger to be converted.</param>
		/// <returns>Column that corresponds to the trigger.</returns>
		/// <exception cref="InvalidOperationException">Throws if trigger has no corresponding column.</exception>
		public static Column ToColumn(this Trigger trigger)
		{
			switch (trigger)
			{
				case Trigger.Interval:
					return Column.Interval;

				case Trigger.IntervalType:
					return Column.IntervalType;

				case Trigger.Poll:
					return Column.Poll;

				case Trigger.State:
					return Column.State;

				default:
					throw new ArgumentException($"Unsupported IntervalType '{trigger}'.");
			}
		}
	}
}

//---------------------------------
// PollingManager\GenericAPI\Pollable\IPollable.cs
//---------------------------------
namespace Skyline.DataMiner.PollingManager
{
	using System;
	using System.Collections.Generic;

	using Skyline.DataMiner.Scripting;

	/// <summary>
	/// Represents base for a row in the <see cref="PollingmanagerQActionTable"/>.
	/// </summary>
	public interface IPollable
	{
        SLProtocol Protocol { get; set; }

        string Name { get; set; }

        double Interval { get; set; }

        double DefaultInterval { get; set; }

        IntervalType IntervalType { get; set; }

        DateTime LastPoll { get; set; }

        Status Status { get; set; }

        string Reason { get; set; }

        State State { get; set; }

        List<IPollable> Parents { get; set; }

        List<IPollable> Children { get; set; }

        Dictionary<int, Dependency> Dependencies { get; set; }

        bool Poll();

        void Disable();

        bool CheckDependencies();

        void AddDependency(int paramId, Dependency dependency);

        void AddParent(IPollable parent);

        void AddParents(params IPollable[] parents);

        void AddChild(IPollable child);

        void AddChildren(params IPollable[] children);
    }
}

//---------------------------------
// PollingManager\GenericAPI\Pollable\PollableBase.cs
//---------------------------------
namespace Skyline.DataMiner.PollingManager
{
	using System;
	using System.Collections.Generic;

	using Skyline.DataMiner.Scripting;

	/// <summary>
	/// Base class that implements <see cref="IPollable"/>.
	/// </summary>
	public abstract class PollableBase : IPollable
	{
		/// <summary>
		/// Initializes a new instance of the <see cref="PollableBase"/> class.
		/// </summary>
		/// <param name="protocol">Link with SLProtocol process.</param>
		/// <param name="name">Name of the PollingManager table row.</param>
		/// <param name="interval">Default polling interval.</param>
		public PollableBase(SLProtocol protocol, string name, TimeSpan interval)
		{
			Protocol = protocol;
			Name = name;
			Interval = interval.TotalSeconds;
			DefaultInterval = interval.TotalSeconds;
			IntervalType = IntervalType.Default;
			LastPoll = default;
			Status = Status.NotPolled;
			Reason = string.Empty;
			State = State.Enabled;
		}

		public SLProtocol Protocol { get; set; }

		public string Name { get; set; }

		public double Interval { get; set; }

		public double DefaultInterval { get; set; }

		public IntervalType IntervalType { get; set; }

		public DateTime LastPoll { get; set; }

		public Status Status { get; set; }

		public string Reason { get; set; }

		public State State { get; set; }

		public List<IPollable> Parents { get; set; } = new List<IPollable>();

		public List<IPollable> Children { get; set; } = new List<IPollable>();

		public Dictionary<int, Dependency> Dependencies { get; set; } = new Dictionary<int, Dependency>();

		/// <summary>
		/// Method to be implemented by extending class. This method gets called by <see cref="PollingManager"/>.
		/// </summary>
		/// <returns>Returns true for success and false for failed poll.</returns>
		/// <remarks>Implementation of <see cref="Poll"/> should never throw.</remarks>
		public abstract bool Poll();

		/// <summary>
		/// Method to be implemented by extending class. This method gets called by <see cref="PollingManager"/> context menu when disabling or force disabling the item.
		/// </summary>
		public abstract void Disable();

		/// <summary>
		/// Updates current state of <see cref="PollableBase"/>.
		/// </summary>
		/// <param name="row">Row on which to base the update.</param>
		/// <exception cref="ArgumentException">Throws if <paramref name="row"/> has length less then 9.</exception>
		public void Update(object[] row)
		{
			if (row.Length < 9)
			{
				throw new ArgumentException($"Parameter '{nameof(row)}' must have at least 9 elements, but has '{row.Length}'.");
			}

			var name = Convert.ToString(row[(int)Column.Name]);
			if (String.IsNullOrEmpty(name))
			{
				// BugFix: when a new poll item is added, the LoadRows() method call this Update()-method to overwrite the in memory row (newly added) with a not yet existing row -> null values.
				// This stops the element from polling completely and forces the user to recreate the element.
				// Checking if this value is not empty prevents unwanted behavior by overwriting.
				return;
			}

			Name = name;
			Interval = Convert.ToDouble(row[(int)Column.Interval]);
			DefaultInterval = Convert.ToDouble(row[(int)Column.DefaultInterval]);
			IntervalType = (IntervalType)Convert.ToDouble(row[(int)Column.IntervalType]);
			LastPoll = DateTime.FromOADate(Convert.ToDouble(row[(int)Column.LastPoll]));
			Status = (Status)Convert.ToDouble(row[(int)Column.Status]);
			Reason = Convert.ToString(row[(int)Column.Reason]) ?? string.Empty;
			State = (State)Convert.ToDouble(row[(int)Column.State]);
		}

		/// <summary>
		/// Gets dependent parameters and compares their values with dependencies. Sets <see cref="Reason"/> to first condition not satisfied.
		/// </summary>
		/// <returns>False if any condition is not satisfied, otherwise true.</returns>
		public bool CheckDependencies()
		{
			try
			{
				foreach (KeyValuePair<int, Dependency> dependency in Dependencies)
				{
					object parameter = Protocol.GetParameter(dependency.Key)
						?? throw new InvalidOperationException($"Parameter with ID '{dependency.Key}' doesn't exist.");

					if (dependency.Value.Value is double)
					{
						if (dependency.Value.ShouldEqual)
						{
							if (!CheckDoubleParameter(parameter, dependency.Value.Value))
							{
								Reason = dependency.Value.Message;
								return false;
							}
						}
						else
						{
							if (CheckDoubleParameter(parameter, dependency.Value.Value))
							{
								Reason = dependency.Value.Message;
								return false;
							}
						}
					}
					else if (dependency.Value.Value is string)
					{
						if (dependency.Value.ShouldEqual)
						{
							if (!CheckStringParameter(parameter, dependency.Value.Value))
							{
								Reason = dependency.Value.Message;
								return false;
							}
						}
						else
						{
							if (CheckStringParameter(parameter, dependency.Value.Value))
							{
								Reason = dependency.Value.Message;
								return false;
							}
						}
					}
				}
			}
			catch (Exception ex)
			{
				Protocol.Log($"QA{Protocol.QActionID}|{Protocol.GetTriggerParameter()}|PollableBase.CheckDependencies|Exception thrown:{Environment.NewLine}{ex}", LogType.Error, LogLevel.NoLogging);

				Reason = "Something went wrong. Please check logs.";
				return false;
			}

			Reason = string.Empty;
			return true;
		}

		/// <summary>
		/// Adds a dependency.
		/// </summary>
		/// <param name="paramId">Id of parameter on which this row is depending.</param>
		/// <param name="dependency">Dependency object.</param>
		public void AddDependency(int paramId, Dependency dependency)
		{
			Dependencies.Add(paramId, dependency);
		}

		/// <summary>
		/// Adds parent without creating two way relation between elements. This shouldn't be used directly. Use <see cref="AddParents"/> instead.
		/// </summary>
		/// <param name="parent">Parent element.</param>
		/// <exception cref="InvalidOperationException">Throws if <paramref name="parent"/> is already this elements child.</exception>
		void IPollable.AddParent(IPollable parent)
		{
			if (Children.Contains(parent))
			{
				throw new InvalidOperationException($"Circular dependency, '{parent.Name}' is already a child of '{Name}'.");
			}

			if (Parents.Contains(parent))
			{
				return;
			}

			Parents.Add(parent);
		}

		/// <summary>
		/// Adds parents to this element, and adds this element as a child of each parent passed as parameter.
		/// </summary>
		/// <param name="parents">Parent elements.</param>
		/// <exception cref="InvalidOperationException">Throws if any <paramref name="parents"/> element is already this elements child.</exception>
		public void AddParents(params IPollable[] parents)
		{
			foreach (IPollable parent in parents)
			{
				if (Children.Contains(parent))
				{
					throw new InvalidOperationException($"Circular dependency, '{parent.Name}' is already a child of '{Name}'.");
				}

				if (Parents.Contains(parent))
				{
					return;
				}

				parent.AddChild(this);
				Parents.Add(parent);
			}
		}

		/// <summary>
		/// Adds child without creating two way relation between elements. This shouldn't be used directly. Use <see cref="AddChildren"/> instead.
		/// </summary>
		/// <param name="child">Child element.</param>
		/// <exception cref="InvalidOperationException">Throws if <paramref name="child"/> is already this elements parent.</exception>
		void IPollable.AddChild(IPollable child)
		{
			if (Parents.Contains(child))
			{
				throw new InvalidOperationException($"Circular dependency, '{child.Name}' is already a parent of '{Name}'.");
			}

			if (Children.Contains(child))
			{
				return;
			}

			Children.Add(child);
		}

		/// <summary>
		/// Adds children to this element, and adds this element as a parent of each child passed as parameter.
		/// </summary>
		/// <param name="children">Child elements.</param>
		/// <exception cref="InvalidOperationException">Throws if any <paramref name="children"/> element is already this elements parent.</exception>
		public void AddChildren(params IPollable[] children)
		{
			foreach (IPollable child in children)
			{
				if (Parents.Contains(child))
				{
					throw new InvalidOperationException($"Circular dependency, '{child.Name}' is already a parent of '{Name}'.");
				}

				if (Children.Contains(child))
				{
					return;
				}

				child.AddParent(this);
				Children.Add(child);
			}
		}

		/// <summary>
		/// Compares values of boxed double types.
		/// </summary>
		/// <param name="parameter">Parameter object.</param>
		/// <param name="value">Object to compare value against.</param>
		/// <returns>True if the boxed values are the same, otherwise false.</returns>
		/// <exception cref="ArgumentException">Throws if boxed <paramref name="parameter"/> type is not double.</exception>
		private static bool CheckDoubleParameter(object parameter, object value)
		{
			return parameter is double d
				? d == (double)value
				: throw new ArgumentException($"{nameof(parameter)} is not of type double.");
		}

		/// <summary>
		/// Compares values of boxed string types.
		/// </summary>
		/// <param name="parameter">Parameter object.</param>
		/// <param name="value">Object to compare value against.</param>
		/// <returns>True if the boxed values are the same, otherwise false.</returns>
		/// <exception cref="ArgumentException">Throws if boxed <paramref name="parameter"/> type is not string.</exception>
		private static bool CheckStringParameter(object parameter, object value)
		{
			return parameter is string s
				? s == (string)value
				: throw new ArgumentException($"{nameof(parameter)} is not of type string.");
		}
	}
}

//---------------------------------
// PollingManager\GenericAPI\Pollable\PollingManagerConfigurationBase.cs
//---------------------------------
namespace Skyline.DataMiner.PollingManager
{
	using System.Collections.Generic;
	using System.Linq;

	using Skyline.DataMiner.Scripting;

	public abstract class PollingManagerConfigurationBase
	{
		public PollingManagerConfigurationBase(SLProtocol protocol) => Protocol = protocol;

		public SLProtocol Protocol { get; set; }

		public List<PollableBase> ListRows => Rows.Select(row => row.Value).ToList();

		protected abstract Dictionary<string, PollableBase> Rows { get; set; }

		protected abstract List<Dependency> Dependencies { get; set; }

		public void Create()
		{
			CreateRelations();
			CreateDependencies();
		}

		protected abstract void CreateRelations();

		protected abstract void CreateDependencies();
	}
}

//---------------------------------
// PollingManager\GenericAPI\PollingManager.cs
//---------------------------------
namespace Skyline.DataMiner.PollingManager
{
	using System;
	using System.Collections.Generic;
	using System.Linq;

	using Skyline.DataMiner.Scripting;
	using Skyline.Protocol.PollingManager;

	/// <summary>
	/// <see cref="PollingManager"/> container class used to provide singleton on the element level.
	/// </summary>
	public static class PollingManagerContainer
	{
		/// <summary>
		/// Gets the <see cref="PollingManager"/> instance for the element.
		/// </summary>
		/// <param name="protocol">Link with SLProtocol process.</param>
		/// <param name="isInit">Is initialization.</param>
		/// <returns><see cref="PollingManager"/> instance with updated <see cref="PollableBase.Protocol"/>.</returns>
		/// <exception cref="InvalidOperationException">Throws if <see cref="PollingManager"/> for this element is not initialized.</exception>
		public static PollingManager GetManager(SLProtocol protocol, bool isInit = false)
		{
			var configuration = new PollingManagerConfiguration(protocol);
			configuration.Create();
			return new PollingManager(protocol, Parameter.Pollingmanager.tablePid, configuration.ListRows, isInit);
		}
	}

	/// <summary>
	/// Handler for <see cref="PollingmanagerQActionTable"/>.
	/// </summary>
	public class PollingManager
	{
		private readonly int tablePid;
		private readonly SLProtocol protocol;
		private readonly bool isAuthenticated;
		private readonly Dictionary<string, PollableBase> rows = new Dictionary<string, PollableBase>();

		/// <summary>
		/// Initializes a new instance of the <see cref="PollingManager"/> class.
		/// </summary>
		/// <param name="protocol">Link with SLProtocol process.</param>
		/// <param name="tablePid">Polling manager table instance.</param>
		/// <param name="rows">Rows to add to the <paramref name="tablePid"/>.</param>
		/// <param name="isInit">Is initialization.</param>
		/// <exception cref="ArgumentException">Throws if <paramref name="rows"/> contains duplicate names.</exception>
		/// <exception cref="ArgumentException">Throws if <paramref name="rows"/> contains null values.</exception>
		public PollingManager(SLProtocol protocol, int tablePid, List<PollableBase> rows, bool isInit = false)
		{
			this.protocol = protocol;
			this.tablePid = tablePid;
			isAuthenticated = Convert.ToBoolean(Convert.ToInt32(protocol.GetParameter(Parameter.loginflag_4)));

			HashSet<string> names = new HashSet<string>();

			for (int i = 0; i < rows.Count; i++)
			{
				if (!names.Add(rows[i].Name))
				{
					throw new ArgumentException($"Duplicate name: {rows[i].Name}.");
				}

				this.rows.Add((i + 1).ToString(), rows[i] ?? throw new ArgumentException("Rows parameter can't contain null values."));
			}

			if (protocol.RowCount(tablePid) != 0)
			{
				LoadRows();
			}

			if (isInit)
			{
				FillTable(this.rows);
			}
		}

		/// <summary>
		/// Checks <see cref="PollingmanagerQActionTable"/> for rows that are ready to be polled and polls them.
		/// </summary>
		/// <exception cref="ArgumentException">
		/// Throws if <see cref="PollableBase.IntervalType"/> is not <see cref="IntervalType.Default"/> or <see cref="IntervalType.Custom"/>.
		/// </exception>
		public void CheckForUpdate()
		{
			bool requiresUpdate = false;

			foreach (KeyValuePair<string, PollableBase> row in rows)
			{
				PollableBase currentRow = row.Value;

				if (currentRow.State == State.Disabled)
				{
					continue;
				}

				bool readyToPoll;

				switch (currentRow.IntervalType)
				{
					case IntervalType.Default:
						readyToPoll = CheckLastPollTime(currentRow.DefaultInterval, currentRow.LastPoll);
						break;

					case IntervalType.Custom:
						readyToPoll = CheckLastPollTime(currentRow.Interval, currentRow.LastPoll);
						break;

					default:
						throw new ArgumentException($"Unsupported PeriodType '{currentRow.IntervalType}'.");
				}

				if (row.Value.Status == Status.Failed || readyToPoll)
				{
					requiresUpdate = PollRow(currentRow);
				}
			}

			if (requiresUpdate)
			{
				FillTableNoDelete(rows);
			}
		}

		/// <summary>
		/// Handles sets on the <see cref="PollingmanagerQActionTable"/>.
		/// </summary>
		/// <param name="rowKey">Row key.</param>
		/// <param name="column">Column on which set was performed.</param>
		/// <exception cref="ArgumentException">Throws if <paramref name="rowKey"/> doesn't exist in the table.</exception>
		/// <exception cref="ArgumentException">
		/// Throws if <paramref name="column"/> is not <see cref="Column.Interval"/>, <see cref="Column.IntervalType"/> or <see cref="Column.Poll"/>.
		/// </exception>
		public void HandleRowUpdate(string rowKey, Column column)
		{
			if (!rows.ContainsKey(rowKey))
			{
				throw new ArgumentException($"Row key '{rowKey}' doesn't exist in the Polling Manager table.");
			}

			PollableBase tableRow;

			switch (column)
			{
				case Column.Interval:
					tableRow = LoadRow(rowKey);
					tableRow.IntervalType = IntervalType.Custom;
					break;

				case Column.IntervalType:
					double interval = rows[rowKey].Interval;
					tableRow = LoadRow(rowKey);
					if (tableRow.IntervalType == IntervalType.Custom)
					{
						tableRow.Interval = interval;
					}

					break;

				case Column.Poll:
					PollRow(rows[rowKey]);
					break;

				case Column.State:
					LoadRow(rowKey);
					break;

				default:
					throw new ArgumentException($"Unsupported Column '{column}'.");
			}

			FillTableNoDelete(rows);
		}

		/// <summary>
		/// Handles context menu actions for the <see cref="PollingmanagerQActionTable"/>.
		/// </summary>
		/// <param name="contextMenu">Object that contains information related to the context menu.</param>
		/// <exception cref="ArgumentException">Throws if <paramref name="contextMenu"/> is not of type string[].</exception>
		/// <exception cref="ArgumentException">
		/// Throws if second element of converted <paramref name="contextMenu"/> can't be parsed as int.
		/// </exception>
		/// <exception cref="ArgumentException">Throws if <paramref name="contextMenu"/> is missing row keys.</exception>
		/// <exception cref="ArgumentException">Throws if row key doesn't exist in the table.</exception>
		public void HandleContextMenu(object contextMenu)
		{
			var input = contextMenu as string[]
				?? throw new ArgumentException($"Parameter '{nameof(contextMenu)}' can't be converted to string[].");

			if (!int.TryParse(input[1], out int value))
			{
				throw new ArgumentException($"Unable to parse selected option '{input[1]}' from '{nameof(contextMenu)}'.");
			}

			var option = (ContextMenuOption)value;

			if (HasRowKeys(option) && input.Length <= 2)
			{
				throw new ArgumentException("Parameter is missing row keys.");
			}

			switch (option)
			{
				case ContextMenuOption.PollAll:
					PollRows();
					break;

				case ContextMenuOption.Disable:
					if (input.Length == 3)
					{
						UpdateState(rows[input[2]], State.Disabled);
						break;
					}

					foreach (string rowId in input.Skip(2).ToArray())
					{
						UpdateState(rows[rowId], State.ForceDisabled);
					}

					break;

				case ContextMenuOption.Enable:
					if (input.Length == 3)
					{
						UpdateState(rows[input[2]], State.Enabled);
						break;
					}

					foreach (string rowId in input.Skip(2).ToArray())
					{
						UpdateState(rows[rowId], State.ForceEnabled);
					}

					break;

				case ContextMenuOption.ForceDisable:
					foreach (string rowId in input.Skip(2).ToArray())
					{
						UpdateState(rows[rowId], State.ForceDisabled);
					}

					break;

				case ContextMenuOption.ForceEnable:
					foreach (string rowId in input.Skip(2).ToArray())
					{
						UpdateState(rows[rowId], State.ForceEnabled);
					}

					break;

				case ContextMenuOption.DisableAll:
					foreach (KeyValuePair<string, PollableBase> row in rows)
					{
						if (row.Value.State != State.Disabled)
						{
							UpdateState(row.Value, State.ForceDisabled);
						}
					}

					break;

				case ContextMenuOption.EnableAll:
					foreach (KeyValuePair<string, PollableBase> row in rows)
					{
						if (row.Value.State != State.Enabled)
						{
							UpdateState(row.Value, State.ForceEnabled);
						}
					}

					break;

				default:
					throw new ArgumentException($"Unsupported ContextMenuOption '{option}'.");
			}

			FillTableNoDelete(rows);
		}

		/// <summary>
		/// Checks whether option with row keys was selected in context menu.
		/// </summary>
		/// <param name="option">Context menu option.</param>
		/// <returns>True if option with row keys was selected, false otherwise.</returns>
		private bool HasRowKeys(ContextMenuOption option)
		{
			switch (option)
			{
				case ContextMenuOption.PollAll:
					return false;

				case ContextMenuOption.DisableAll:
					return false;

				case ContextMenuOption.EnableAll:
					return false;

				default:
					return true;
			}
		}

		/// <summary>
		/// Updates row state.
		/// </summary>
		/// <param name="row">Row to update.</param>
		/// <param name="state">State to update to.</param>
		private void UpdateState(IPollable row, State state)
		{
			switch (state)
			{
				case State.Disabled:
					if (row.Children.Any(child => child.State == State.Enabled))
					{
						ShowChildren(row);
						return;
					}

					row.Disable();
					row.State = State.Disabled;
					row.Status = Status.Disabled;
					return;

				case State.Enabled:
					if (row.Parents.Any(parent => parent.State == State.Disabled))
					{
						ShowParents(row);
						return;
					}

					row.State = State.Enabled;
					row.Status = Status.NotPolled;
					return;

				case State.ForceDisabled:
					row.Disable();
					row.State = State.Disabled;
					row.Status = Status.Disabled;
					UpdateStates(row.Children, State.ForceDisabled);
					return;

				case State.ForceEnabled:
					row.State = State.Enabled;
					row.Status = Status.NotPolled;
					UpdateStates(row.Parents, State.ForceEnabled);
					return;
			}
		}

		/// <summary>
		/// Updates states of the related rows.
		/// </summary>
		/// <param name="collection">List of rows to update.</param>
		/// <param name="state">State to update rows to.</param>
		private void UpdateStates(List<IPollable> collection, State state)
		{
			foreach (IPollable item in collection)
			{
				UpdateState(item, state);
			}
		}

		/// <summary>
		/// Shows information message with parent rows of the row passed as parameter.
		/// </summary>
		/// <param name="row">Row for which to show parents.</param>
		private void ShowParents(IPollable row)
		{
			string parents = string.Join(Environment.NewLine, row.Parents.Where(parent => parent.State == State.Disabled).Select(parent => parent.Name));

			string message = $"Unable to enable '{row.Name}' because it depends on the following rows:{Environment.NewLine}" +
				$"{parents}{Environment.NewLine}" +
				$"Please enable them first or use [Force Enable].";

			protocol.ShowInformationMessage(message);
		}

		/// <summary>
		/// Shows information message with child rows of the row passed as parameter.
		/// </summary>
		/// <param name="row">Row for which to show children.</param>
		private void ShowChildren(IPollable row)
		{
			string children = string.Join(Environment.NewLine, row.Children.Where(child => child.State == State.Enabled).Select(child => child.Name));

			string message = $"Unable to disable '{row.Name}' because the following rows are dependent on it:{Environment.NewLine}" +
				$"{children}{Environment.NewLine}" +
				$"Please disable them first or use [Force Disable].";

			protocol.ShowInformationMessage(message);
		}

		/// <summary>
		/// Checks whether poll period has elapsed.
		/// </summary>
		/// <param name="interval">Poll period.</param>
		/// <param name="lastPoll">Last poll timestamp.</param>
		/// <returns>True if poll period has elapsed, false otherwise.</returns>
		private bool CheckLastPollTime(double interval, DateTime lastPoll)
		{
			return (DateTime.Now - lastPoll).TotalSeconds > interval;
		}

		/// <summary>
		/// Polls a row.
		/// </summary>
		/// <param name="row">Row to poll.</param>
		/// <returns>True if poll did occur, false otherwise.</returns>
		private bool PollRow(PollableBase row)
		{
			if (row.State == State.Disabled)
			{
				return false;
			}

			if (!row.CheckDependencies())
			{
				row.Status = Status.Failed;
				return false;
			}

			row.LastPoll = DateTime.Now;

			if (row is Pollable_Authentication || isAuthenticated)
			{
				if (row.Poll())
				{
					row.Status = Status.Succeeded;
				}
				else
				{
					row.Status = Status.Failed;
				}
			}
			else
			{
				row.Status = Status.Failed;
				row.Reason = "Not Authenticated!";
			}

			return true;
		}

		/// <summary>
		/// Polls all rows by calling <see cref="PollRow"/> for every row.
		/// </summary>
		private void PollRows()
		{
			foreach (KeyValuePair<string, PollableBase> row in rows)
			{
				PollRow(row.Value);
			}
		}

		/// <summary>
		/// Loads <see cref="PollingmanagerQActionRow"/> and updates internal row with the same <paramref name="rowPk"/>.
		/// </summary>
		/// <param name="rowPk">Row to load and update.</param>
		/// <returns>Updated internal row.</returns>
		/// <exception cref="ArgumentException">Throws if <paramref name="rowPk"/> doesn't exist in the table.</exception>
		private PollableBase LoadRow(string rowPk)
		{
			if (!rows.ContainsKey(rowPk))
			{
				throw new ArgumentException($"Row key '{rowPk}' doesn't exist in the Polling Manager table.");
			}

			object[] tableRow = (object[])protocol.GetRow(tablePid, rowPk);

			rows[rowPk].Update(tableRow);

			return rows[rowPk];
		}

		/// <summary>
		/// Loads all <see cref="PollingmanagerQActionRow"/> and updates internal rows respectively.
		/// </summary>
		private void LoadRows()
		{
			foreach (KeyValuePair<string, PollableBase> row in rows)
			{
				LoadRow(row.Key);
			}
		}

		/// <summary>
		/// Creates the <see cref="PollingmanagerQActionRow"/>.
		/// </summary>
		/// <param name="rowKey">Row key.</param>
		/// <param name="value">Row to create.</param>
		/// <returns>Instance of <see cref="PollingmanagerQActionRow"/>.</returns>
		private PollingmanagerQActionRow CreateTableRow(string rowKey, PollableBase value)
		{
			return new PollingmanagerQActionRow
			{
				Pollingmanager_id = rowKey,
				Pollingmanager_name = value.Name,
				Pollingmanager_interval = value.IntervalType == IntervalType.Custom ? value.Interval : value.DefaultInterval,
				Pollingmanager_defaultinterval = value.DefaultInterval,
				Pollingmanager_intervaltype = value.IntervalType,
				Pollingmanager_lastpoll = value.LastPoll == default ? Convert.ToDouble(Status.NotPolled) : value.LastPoll.ToOADate(),
				Pollingmanager_status = value.State == State.Disabled ? Status.Disabled : value.Status,
				Pollingmanager_reason = value.Reason,
				Pollingmanager_state = value.State,
			};
		}

		/// <summary>
		/// Creates the array of the <see cref="PollingmanagerQActionRow"/>.
		/// </summary>
		/// <param name="rows">Rows to create.</param>
		/// <returns>Array of the <see cref="PollingmanagerQActionRow"/>.</returns>
		private PollingmanagerQActionRow[] CreateTableRows(Dictionary<string, PollableBase> rows)
		{
			List<PollingmanagerQActionRow> tableRows = new List<PollingmanagerQActionRow>();

			foreach (KeyValuePair<string, PollableBase> row in rows)
			{
				tableRows.Add(CreateTableRow(row.Key, row.Value));
			}

			return tableRows.ToArray();
		}

		/// <summary>
		/// Sets the content of the table to the provided content.
		/// </summary>
		/// <param name="rows">Rows to fill the table with.</param>
		private void FillTable(Dictionary<string, PollableBase> rows)
		{
			PollingmanagerQActionRow[] tableRows = CreateTableRows(rows);

			protocol.FillArray(tablePid, tableRows.Select(r => r.ToObjectArray()).ToList(), NotifyProtocol.SaveOption.Full);
		}

		/// <summary>
		/// Add the provided rows to the table.
		/// </summary>
		/// <param name="rows">Rows to add to the table.</param>
		private void FillTableNoDelete(Dictionary<string, PollableBase> rows)
		{
			PollingmanagerQActionRow[] tableRows = CreateTableRows(rows);

			protocol.FillArray(tablePid, tableRows.Select(r => r.ToObjectArray()).ToList(), NotifyProtocol.SaveOption.Partial);
		}
	}
}
//---------------------------------
// PollingManager\GenericAPI\Structs\Dependency.cs
//---------------------------------
namespace Skyline.DataMiner.PollingManager
{
	using System;

	/// <summary>
	/// Represents parameter dependency.
	/// </summary>
	public struct Dependency
	{
		/// <summary>
		/// Initializes a new instance of the <see cref="Dependency"/> struct.
		/// </summary>
		/// <param name="value">Value to check against.</param>
		/// <param name="shouldEqual">Whether <paramref name="value"/> should be equal or different from the value of parameter.</param>
		/// <param name="message">Message to show when condition is not met.</param>
		/// <exception cref="ArgumentException">Throws if <paramref name="value"/> is not of type double or string.</exception>
		public Dependency(object value, bool shouldEqual, string message)
		{
			if (value is double || value is string)
			{
				Value = value;
			}
			else if (value is int)
			{
				Value = Convert.ToDouble(value);
			}
			else
			{
				throw new ArgumentException("Value is not of type int, double nor string.");
			}

			ShouldEqual = shouldEqual;
			Message = message;
		}

		public object Value { get; set; }

		public bool ShouldEqual { get; set; }

		public string Message { get; set; }
	}
}

