namespace Skyline.Protocol.PollingManager
{
	using System;
	using QAction_3;
	using Skyline.DataMiner.PollingManager;
	using Skyline.DataMiner.Scripting;

	public class Pollable_Cfg_Output_ASI2 : PollableBase
	{
		public Pollable_Cfg_Output_ASI2(SLProtocol protocol, string name, TimeSpan interval) : base(protocol, name, interval)
		{
		}

		public override void Disable()
		{
		}

		public override bool Poll()
		{
			if (!ProtocolExtensions.IsDeviceTypeMultiStream(Protocol))
			{
				Reason = "Device type is not Multi-Stream!";
				return false;
			}

			Protocol.CheckTrigger((int)ProtocolTriggers.Cfgoutputasi2_1018);
			Reason = String.Empty;
			return true;
		}
	}
}
