namespace Skyline.Protocol.PollingManager
{
	using System;
	using QAction_3;
	using Skyline.DataMiner.PollingManager;
	using Skyline.DataMiner.Scripting;

	public class Pollable_Status_ASI_PID : PollableBase
	{
		public Pollable_Status_ASI_PID(SLProtocol protocol, string name, TimeSpan interval) : base(protocol, name, interval)
		{
		}

		public override void Disable()
		{
		}

