//---------------------------------
// PollingManager\PollingManagerConfiguration.cs
//---------------------------------
namespace Skyline.Protocol.PollingManager
{
	using System;
	using System.Collections.Generic;

	using Skyline.DataMiner.PollingManager;
	using Skyline.DataMiner.Scripting;

	public class PollingManagerConfiguration : PollingManagerConfigurationBase
	{
		private const string NameCfgCABiss = "CONFIG/CA/BISS";
		private const string NameCfgCaCi = "CONFIG/CA/CI";
		private const string NameCfgDecodeAr = "CONFIG/Decode/Aspect Ratio";
		private const string NameCfgDecodeAudio = "CONFIG/Decode/Audio";
		private const string NameCfgDecodeAudio1 = "CONFIG/Decode/Audio 1";
		private const string NameCfgDecodeAudio2 = "CONFIG/Decode/Audio 2";
		private const string NameCfgDecodeAudio3 = "CONFIG/Decode/Audio 3";
		private const string NameCfgDecodeAudio4 = "CONFIG/Decode/Audio 4";
