using System;
using QAction_3;
using Skyline.DataMiner.Scripting;

public static class QAction
{
	/// <summary>
	///     Login.
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner.</param>
	public static void Run(SLProtocol protocol)
	{
		object[] objectParametersLogin = (object[])protocol.GetParameters(new uint[] { Parameter.username_10, Parameter.password_20 });
		string username = Convert.ToString(objectParametersLogin[0]);
		string password = Convert.ToString(objectParametersLogin[1]);

		if (String.IsNullOrWhiteSpace(username) && String.IsNullOrWhiteSpace(password))
		{
			return;
		}

		string xmlRequest = $"<HDR><LOGIN><UID>{username}</UID><USERPASS>{password}</USERPASS></LOGIN></HDR>";
		protocol.SetParameter(Parameter.loginxmlrequest_301, xmlRequest);
		protocol.CheckTrigger((int)ProtocolTriggers.WhenLoginButtonPressed_1);
	}
}

using System;
using System.Linq;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public static class QAction
{
	/// <summary>
	///     Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.loginxmlresponsecontent_201)); // XML Login Response Content
			if (!String.IsNullOrEmpty(sXML))
			{
				XDocument xDoc = XDocument.Parse(sXML);
				if (xDoc.Descendants("HDR").Any())
				{
					string sSessionid = xDoc.Descendants("HDR").Descendants("LOGIN_INFO").Descendants("SESSION_ID").FirstOrDefault().Value ?? String.Empty;
					protocol.SetParameters(new[] { Parameter.loginflag_4, Parameter.sessionid_10000 }, new object[] { 1, sSessionid }); // Assign the session ID to param 10000
				}
				else
				{
					protocol.Log("QA" + protocol.QActionID + "|Run|Session can not be started", LogType.Error, LogLevel.NoLogging);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Set ASI URL.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			int deviceType = Convert.ToInt32(protocol.GetParameter(Parameter.devicetype_6));

			var paramsToSet = new Dictionary<int, object>
			{
				{ Parameter.asipidtableurl_24, deviceType == 1 ? "ws/v2/status/output/asi/pid" : "ws/v2/status/output/asi1/pid" },
				{ Parameter.outputasiurl_26, deviceType == 1 ? "ws/v2/service_cfg/output/asi" : "ws/v2/service_cfg/output/asi1" },
			};
			protocol.SetParameters(paramsToSet.Keys.ToArray(), paramsToSet.Values.ToArray());
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Identity Status.
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.identitystatusresponse_5));
			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);
				Dictionary<string, string> dicLicenses = new Dictionary<string, string>
				{
			{ "HD Transcode License Count", "-1" }, { "SD Transcode License Count", "-1" },
			{ "HEVC Transcode License Count", "-1" }, { "Tuner License Count", "-1" },
			{ "Decrypt License", "-1" }, { "DVB_S2 License", "-1" },
			{ "Tuner APSK License", "-1" }, { "AVC Decode License", "-1" },
			{ "HEVC Decode License", "-1" }, { "HD Output License", "-1" },
			{ "Full HD Output License", "-1" }, { "UHD Output License", "-1" }, { "AUD3_4 License", "-1" },
				};
				if (xDoc.Descendants("ident").Any())
				{
					object sHostname = xDoc.Descendants("ident").Descendants("hostname").Any() ? xDoc.Descendants("ident").Descendants("hostname").FirstOrDefault().Value : "-1";
					object sTrackingid = xDoc.Descendants("ident").Descendants("trackingid").Any() ? xDoc.Descendants("ident").Descendants("trackingid").FirstOrDefault().Value : "-1";
					object sMasterua = xDoc.Descendants("ident").Descendants("masterua").Any() ? xDoc.Descendants("ident").Descendants("masterua").FirstOrDefault().Value : "-1";
					object sCtlmacaddr = xDoc.Descendants("ident").Descendants("ctlmacaddr").Any() ? xDoc.Descendants("ident").Descendants("ctlmacaddr").FirstOrDefault().Value : "-1";
					object sHwOptionsMpoipOut = xDoc.Descendants("ident").Descendants("hwoptions").Descendants("mpoipout").Any() ? xDoc.Descendants("ident").Descendants("hwoptions").Descendants("mpoipout").FirstOrDefault().Value : "-1";
					object sHwOptionsMpoipIn = xDoc.Descendants("ident").Descendants("hwoptions").Descendants("mpoipin").Any() ? xDoc.Descendants("ident").Descendants("hwoptions").Descendants("mpoipin").FirstOrDefault().Value : "-1";
					object sHwOptionsMoipfec = xDoc.Descendants("ident").Descendants("hwoptions").Descendants("moipfec").Any() ? xDoc.Descendants("ident").Descendants("hwoptions").Descendants("moipfec").FirstOrDefault().Value : "-1";
					object sHwOptinsSdi = xDoc.Descendants("ident").Descendants("hwoptions").Descendants("sdi").Any() ? xDoc.Descendants("ident").Descendants("hwoptions").Descendants("sdi").FirstOrDefault().Value : "-1";
					if (xDoc.Descendants("ident").Descendants("licenses").Any())
					{
						foreach (XElement element in xDoc.Descendants().Where(p => p.Name.LocalName == "licenses"))
						{
							dicLicenses[Convert.ToString(element.Descendants("feature").FirstOrDefault().Value)] = element.Descendants("enabled").FirstOrDefault().Value;
						}
					}

					int[] parametersId = new[] { Parameter.identitystatushostname_16501, Parameter.identitystatustrackingid_16502, Parameter.identitystatusmasterua_16503, Parameter.identitystatusctlmacaddr_16504, Parameter.identitystatusmpoipout_16505, Parameter.identitystatusmpoipin_16506, Parameter.identitystatusmoipfec_16507, Parameter.identitystatussdi_16508, Parameter.identitystatushdtranscodelicensecount_16509, Parameter.identitystatussdtranscodelicensecount_16510, Parameter.identitystatushevctranscodelicensecount_16511, Parameter.identitystatustunerlicensecount_16512, Parameter.identitystatusdecryptlicense_16514, Parameter.identitystatusdvb_s2license_16515, Parameter.identitystatustunerapsklicense_16516, Parameter.identitystatusavcdecodelicense_16517, Parameter.identitystatushevcdecodelicense_16518, Parameter.identitystatushdoutputlicense_16519, Parameter.identitystatusfullhdoutputlicense_16520, Parameter.identitystatusuhdoutputlicense_16521, Parameter.identitystatusaud3_4license_16522};
					object[] parametersValues = new object[] { sHostname, sTrackingid, sMasterua, sCtlmacaddr, sHwOptionsMpoipOut, sHwOptionsMpoipIn, sHwOptionsMoipfec, sHwOptinsSdi, dicLicenses["HD Transcode License Count"], dicLicenses["SD Transcode License Count"], dicLicenses["HEVC Transcode License Count"], dicLicenses["Tuner License Count"], dicLicenses["Decrypt License"], dicLicenses["DVB_S2 License"], dicLicenses["Tuner APSK License"], dicLicenses["AVC Decode License"], dicLicenses["HEVC Decode License"], dicLicenses["HD Output License"], dicLicenses["Full HD Output License"], dicLicenses["UHD Output License"], dicLicenses["AUD3_4 License"]};
					protocol.SetParameters(parametersId, parametersValues);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception e)
		{
			protocol.Log("QA" + protocol.QActionID + "|MethodName|eX: " + e, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;


/// <summary>
/// DataMiner QAction Class: Parse CI Data.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			object[] paramValues = (object[])protocol.GetParameters(new uint[] { Parameter.xmlcisetupget_205, Parameter.xmlcisetupgetcode_204 });
			string response = Convert.ToString(paramValues[0]);
			string code = Convert.ToString(paramValues[1]);
			if (String.IsNullOrEmpty(response) || !code.Contains("200"))
			{
				return;
			}

			XDocument xDoc = XDocument.Parse(response);

			if (!xDoc.Descendants("ci").Any())
			{
				return;
			}

			XElement ci = xDoc.Descendants("ci").First();
			int camQuerySupp = String.Equals(ci.Descendants("sendquery").First().Value, "Enable") ? 1 : 2;
			int camAutoReset = String.Equals(ci.Descendants("autoreset").First().Value, "Enable") ? 1 : 2;
			int mngType = String.Equals(ci.Descendants("listmgmt").First().Value, "AddDel") ? 1 : 2;
			int autoRecovery = String.Equals(ci.Descendants("autorec").First().Value, "Enable") ? 1 : 2;
			int tsOnetChk = String.Equals(ci.Descendants("tsonetchk").First().Value, "Enable") ? 1 : 2;
			int tsId = Convert.ToInt32(ci.Descendants("tsid").First().Value);
			int onetId = Convert.ToInt32(ci.Descendants("onetid").First().Value);
			int camTsHandling = String.Equals(ci.Descendants("tsrouting").First().Value, "EntireTS") ? 1 : 2;

			int[] paramsToSet = new[]
			{
				Parameter.cicamquerysupport_306,
				Parameter.cicamautoreset_307,
				Parameter.cilistmanagementtype_308,
				Parameter.cicamautorecovery_309,
				Parameter.citsonidcheck_310,
				Parameter.citransportid_311,
				Parameter.cionetid_312,
				Parameter.cicamtshandling_313,
			};
			object[] valsToSet = new object[]
			{
				camQuerySupp,
				camAutoReset,
				mngType,
				autoRecovery,
				tsOnetChk,
				tsId,
				onetId,
				camTsHandling,
			};

			protocol.SetParameters(paramsToSet, valsToSet);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

