public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iPos = 5001;
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlasi_206)); // XML Response for ASI Setting Parameters
			if (string.IsNullOrEmpty(sXML))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
				return;
			}

			sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

			XDocument xDoc = XDocument.Parse(sXML);
			if (xDoc.Descendants("asi").Any())
			{
				string sAsilock = xDoc.Descendants("asi").Descendants("asilock").Any() ? xDoc.Descendants("asi").Descendants("asilock").FirstOrDefault().Value : "-1";
				string sInputrate = xDoc.Descendants("asi").Descendants("inputrate").Any() ? xDoc.Descendants("asi").Descendants("inputrate").FirstOrDefault().Value : "-1";
				string sScrambmode = xDoc.Descendants("asi").Descendants("scrambmode").Any() ? xDoc.Descendants("asi").Descendants("scrambmode").FirstOrDefault().Value : "-1";
				string sAsipktsize = xDoc.Descendants("asi").Descendants("asipktsize").Any() ? xDoc.Descendants("asi").Descendants("asipktsize").FirstOrDefault().Value : "-1";
				string sAsilinkerr = xDoc.Descendants("asi").Descendants("asilinkerr").Any() ? xDoc.Descendants("asi").Descendants("asilinkerr").FirstOrDefault().Value : "-1";
				int[] paramsIds = new int[5];
				for (int i = 0; i < paramsIds.Length; i++)
				{
					paramsIds[i] = iPos + i;
				}

				object[] allValues = { sAsilock, sInputrate, sScrambmode, sAsipktsize, sAsilinkerr };
				protocol.SetParameters(paramsIds, allValues);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlmoip_serviceconfiguration_207));  // XML Response for MOIP-Service
			if (String.IsNullOrEmpty(sXML))
			{
				return;
			}

			XDocument xDoc = XDocument.Parse(sXML);
			if (!xDoc.Descendants("moip").Any())
			{
				return;
			}

			List<XElement> elements = xDoc.Descendants("moip").ToList();
			List<QActionTableRow> rows = new List<QActionTableRow>();
			SetMoipRows(protocol, elements, rows);

			if (rows.Count > 0)
			{
				protocol.moipconfiguration.FillArray(rows);
			}

			string sSrcfltmodeList = "None;White List;Black List";

			protocol.SetParameter(Parameter.sourcefilteringlist_30005, sSrcfltmodeList);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void SetMoipRows(SLProtocolExt protocol, List<XElement> elements, List<QActionTableRow> rows)
	{
		foreach (var moip in elements)
		{
			string card = Convert.ToString(moip.Descendants("card").FirstOrDefault().Value ?? String.Empty);
			string port = Convert.ToString(moip.Descendants("port").FirstOrDefault().Value ?? String.Empty);
			string stream = Convert.ToString(moip.Descendants("stream").FirstOrDefault().Value ?? String.Empty);

			if (String.IsNullOrEmpty(card) || String.IsNullOrEmpty(port) || String.IsNullOrEmpty(stream))
			{
				protocol.Log("QA" + protocol.QActionID + "|SetMoipRows|Could not process MOIP, identifier is Empty", LogType.Error, LogLevel.NoLogging);
				continue;
			}

			MoipconfigurationQActionRow row = CreateRow(moip, card, port, stream);

			rows.Add(row);
		}
	}

	private static MoipconfigurationQActionRow CreateRow(XElement moip, string card, string port, string stream)
	{
		string key = String.Format("{0}/{1}/{2}", card, port, stream);

		MoipconfigurationQActionRow row = new MoipconfigurationQActionRow
		{
			Moipconfigurationinstance_6021 = key,
			Moipconfigurationmoipport_6000 = port,
			Moipconfigurationmoipactivateinput_6001 = moip.Descendants("act").FirstOrDefault().Value,
			Moipconfigurationsourceipselectionmode_6002 = moip.Descendants("ipmode").FirstOrDefault().Value,
			Moipconfigurationfecmode_6003 = moip.Descendants("fecmode").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationenablemulticast_6004 = moip.Descendants("usemcast").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationmulticastaddress_6005 = moip.Descendants("mcastaddr").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationtsdestinationport_6006 = moip.Descendants("tsdestport").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationfeccolumnsport_6007 = moip.Descendants("feccolport").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationfecrowsport_6008 = moip.Descendants("fecrowport").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationsourcefiltering_6009 = moip.Descendants("srcfltmode").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationde_jitterconfigurationalgorithm_6010 = moip.Descendants("dejalg").FirstOrDefault().Value ?? String.Empty,
			Moipconfigurationinterface_6011 = moip.Descendants("ipiport").FirstOrDefault().Value ?? String.Empty,
		};
		return row;
	}
}

