public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int intPos = 5050;

		try
		{
			// XML Response for ASI service configuration
			string stringXML = Convert.ToString(protocol.GetParameter(Parameter.xmlasi_serviceconfiguration_209));

			if (!string.IsNullOrEmpty(stringXML))
			{
				XDocument doc = XDocument.Parse(stringXML);

				if (doc.Descendants("asi").Any())
				{
					string port = doc.Descendants("asi").Descendants("port").Any() ? doc.Descendants("asi").Descendants("port").FirstOrDefault().Value : "-1";
					string act = doc.Descendants("asi").Descendants("act").Any() ? doc.Descendants("asi").Descendants("act").FirstOrDefault().Value : "-1";
					int[] objectIntParamsIds = new int[2];

					for (int i = 0; i < objectIntParamsIds.Length; i++)
					{
						objectIntParamsIds[i] = intPos + i;
					}

					object[] allvalues = { port, act };
					protocol.SetParameters(objectIntParamsIds, allvalues);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Linq;
using Skyline.DataMiner.Scripting;

public static class QAction
{
	/// <summary>
	///     StartUpLogin.
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner.</param>
	public static void Run(SLProtocol protocol)
	{
		string ip = Convert.ToString(protocol.GetParameter(27));
		string newIP = ip.Split(';')[0];
		string port = newIP.Split(':').Last();
		if (port == "443")
		{
			if (!newIP.Contains("https://"))
			{
				newIP = "https://" + newIP;
			}
		}
		else
		{
			if (!newIP.Contains("https://") && !newIP.Contains("http://"))
			{
				newIP = "http://" + newIP;
			}
		}

		protocol.SetParameter(Parameter.getelementip_28, newIP);
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using QAction_3;
using Skyline.DataMiner.Scripting;
using XP2Api;

namespace XP2Api
{
	public static class XmlLinqExtensions
	{
		public static string GetAttributeValue(this XElement element, string attributeName, int exceptionValue)
		{
			string value = Convert.ToString(exceptionValue);

			if (element == null)
			{
				return value;
			}

			XAttribute attribute = element.Attribute(attributeName);

			return attribute != null ? attribute.Value : value;
		}

		public static string GetElementValue(this XElement element, string elementName)
		{
			string value = string.Empty;

			if (element == null)
			{
				return value;
			}

			XElement child = element.Element(elementName);
			value = child.GetElementValue();
			return value;
		}

		private static string GetElementValue(this XElement element)
		{
			return element != null ? element.Value : string.Empty;
		}
	}
}

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string stringXML = string.Empty;

			stringXML = Convert.ToString(protocol.GetParameter(Parameter.xmlrfsettings_port1_202)); // XML response for Ports

			object[] data = new object[4];

			// object[] satLock = new object[4];
			if (!string.IsNullOrEmpty(stringXML))
			{
				stringXML = Regex.Replace(stringXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument doc = XDocument.Parse(stringXML);

				if (doc.Descendants("input").Any())
				{
					ParseXmlResponse(data, doc);

					// 1001 1002 1003 1004 1005 1006 1007 1008 1009 1010 1011 1012 1013 1014 1015 1016
					// 2001 2002 2003 2004 2005 2006 2007 2008 2009 2010 2011 2012 2013 2014 2015 2016
					// 3001 3002 3003 3004 3005 3006 3007 3008 3009 3010 3011 3012 3013 3014 3015 3016
					// 4001 4002 4003 4004 4005 4006 4007 4008 4009 4010 4011 4012 4013 4014 4015 4016
					protocol.SetParameters(Enumerable.Range(1000, 17).ToArray(), (object[])data[0]);
					protocol.SetParameters(Enumerable.Range(2000, 17).ToArray(), (object[])data[1]);
					protocol.SetParameters(Enumerable.Range(3000, 17).ToArray(), (object[])data[2]);
					protocol.SetParameters(Enumerable.Range(4000, 17).ToArray(), (object[])data[3]);

					// protocol.SetParameters(new int[] { Parameter.rf1SATInput_1500, Parameter.rf2SATInput_2500, Parameter.rf3SATInput_3500, Parameter.rf4SATInput_4500 }, satLock);
					protocol.CheckTrigger((int)ProtocolTriggers.RunQa26Action_226);
				}
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void ParseXmlResponse(object[] data, XDocument doc)
	{
		int postition = 0;
		object[] radioFreqParamsValues = new object[17];
		IEnumerable<XElement> radioFreqXML = doc.Descendants("radioFreq");
		if (!radioFreqXML.Any())
			radioFreqXML = doc.Descendants("rf");

		foreach (XElement radioFreq in radioFreqXML)
		{
			radioFreqParamsValues[0] = radioFreq.GetElementValue("port");
			radioFreqParamsValues[1] = radioFreq.GetElementValue("dnlkfreq");
			radioFreqParamsValues[2] = radioFreq.GetElementValue("symrate");
			radioFreqParamsValues[3] = radioFreq.GetElementValue("fec");
			radioFreqParamsValues[4] = radioFreq.GetElementValue("mod");
			radioFreqParamsValues[5] = radioFreq.GetElementValue("rolloff");
			radioFreqParamsValues[6] = radioFreq.GetElementValue("iq");
			radioFreqParamsValues[7] = radioFreq.GetElementValue("afcrange");
			radioFreqParamsValues[8] = radioFreq.GetElementValue("sel22khz");
			radioFreqParamsValues[9] = radioFreq.GetElementValue("pwr");
			radioFreqParamsValues[10] = radioFreq.GetElementValue("lo1");
			radioFreqParamsValues[11] = radioFreq.GetElementValue("lo2");
			radioFreqParamsValues[12] = radioFreq.GetElementValue("xover");
			radioFreqParamsValues[13] = radioFreq.GetElementValue("pol");
			radioFreqParamsValues[14] = radioFreq.GetElementValue("orbpos");
			radioFreqParamsValues[15] = radioFreq.GetElementValue("ewflag");
			radioFreqParamsValues[16] = radioFreq.GetElementValue("act") != string.Empty ? radioFreq.GetElementValue("act") : "-1";

			// satLock[postition] = radioFreq.GetElementValue("satlock");
			data[postition] = radioFreqParamsValues;
			radioFreqParamsValues = new object[17];
			postition++;
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Text;

using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Defined if MS or SS.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string catalogNum = Convert.ToString(protocol.GetParameter(Parameter.catalog_16607)).Trim();

			string[] catNumSplit = catalogNum.Split('-');
			if (catNumSplit.Length != 3)
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|The Catalog Number has an unexpected format, please check it...", LogType.Error, LogLevel.NoLogging);
				return;
			}

			// MS or SS
			string devFunction = catNumSplit[1].Trim();
			int intDevFunction = 0;
			if (devFunction.Equals("SS"))
			{
				intDevFunction = 1;
			}

			// MS
			else
			{
				intDevFunction = 2;
			}

			protocol.SetParameter(Parameter.devicetype_6, intDevFunction);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Xml.Linq;

using Skyline.DataMiner.Scripting;

using SLNetMessages = Skyline.DataMiner.Net.Messages;

/// <summary>
/// DataMiner QAction Class: Process SRT Data.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			object[] paramValues = (object[])protocol.GetParameters(new uint[] { Parameter.xmlsrtresponse_109, Parameter.xmlsrtstatuscode_110 });
			string response = Convert.ToString(paramValues[0]);
			string code = Convert.ToString(paramValues[1]);

			if (String.IsNullOrEmpty(response) || !code.Contains("200"))
			{
				return;
			}

			XDocument xDoc = XDocument.Parse(response);

			if (!xDoc.Descendants("srt").Any())
			{
				return;
			}

			// Getcolumn the keys of that table - Convert it to a list. .ToList this will result in a list of objects that you will need to change to a list of strings
			object[] keys = (object[])((object[])protocol.NotifyProtocol((int)SLNetMessages.NotifyType.NT_GET_TABLE_COLUMNS, Parameter.Srtstatustable.tablePid, new uint[] { Parameter.Srtstatustable.Idx.srtstatusinstance_32501 }))[0];
			List<string> existingKeys = Array.ConvertAll(keys, x => Convert.ToString(x)).ToList();

			var newKeys = new List<string>();
			List<object> aoKeys = new List<object>();
			List<object> streams = new List<object>();
			List<object> act = new List<object>();
			List<object> statusLock = new List<object>();
			List<object> ipLink = new List<object>();
			List<object> ipSig = new List<object>();
			List<object> netName = new List<object>();
			List<object> netID = new List<object>();
			List<object> transID = new List<object>();
			List<object> inputRate = new List<object>();
			List<object> scrambMode = new List<object>();
			List<object> lsttunerea = new List<object>();
			List<object> tunereason = new List<object>();
			List<object> freqMode = new List<object>();
			List<object> svclstmode = new List<object>();
			List<object> baten = new List<object>();
			List<object> niten = new List<object>();
			List<object> sdten = new List<object>();
			List<object> paten = new List<object>();
			List<object> acqstatus = new List<object>();
			List<object> inputID = new List<object>();
			List<object> curintf = new List<object>();
			List<object> rowStatus = new List<object>();
			List<object> cursrc = new List<object>();
			List<object> deldelay = new List<object>();
			List<object> totalLost = new List<object>();
			List<object> switchreas = new List<object>();
			List<object> switchreastime = new List<object>();
			List<object> pkterrcnt = new List<object>();
			List<object> drstate = new List<object>();
			List<object> acqMode = new List<object>();
			List<object> bissprofen = new List<object>();
			List<object> bissprof = new List<object>();
			List<object> tsPort = new List<object>();
			List<object> sendrate = new List<object>();
			List<object> recvrate = new List<object>();
			List<object> rtt = new List<object>();

			foreach (var srt in xDoc.Descendants("srt"))
			{
				string sCardId = srt.Descendants("card").First().Value;
				string sPortId = srt.Descendants("port").First().Value;
				string sStream = srt.Descendants("stream").First().Value;
				string rowKey = String.Format("{0}/{1}/{2}", sCardId, sPortId, sStream);

				aoKeys.Add(rowKey);
				streams.Add(Convert.ToInt32(sStream));
				act.Add(srt.Descendants("act").First().Value);
				statusLock.Add(srt.Descendants("lock").First().Value);
				ipLink.Add(srt.Descendants("iplinkstat").First().Value);
				ipSig.Add(srt.Descendants("ipsignal").First().Value);
				inputRate.Add(Convert.ToDouble(srt.Descendants("inputrate").First().Value));
				netName.Add(srt.Descendants("netname").First().Value);
				netID.Add(srt.Descendants("netid").First().Value);
				transID.Add(srt.Descendants("transid").First().Value);
				scrambMode.Add(srt.Descendants("scrambmode").First().Value);
				lsttunerea.Add(srt.Descendants("lsttunerea").First().Value);
				tunereason.Add(srt.Descendants("tunereason").First().Value);
				freqMode.Add(srt.Descendants("freqmode").First().Value);
				svclstmode.Add(srt.Descendants("svclstmode").First().Value);
				baten.Add(srt.Descendants("baten").First().Value);
				niten.Add(srt.Descendants("niten").First().Value);
				sdten.Add(srt.Descendants("sdten").First().Value);
				paten.Add(srt.Descendants("paten").First().Value);
				acqstatus.Add(srt.Descendants("acqstatus").First().Value);
				inputID.Add(srt.Descendants("inputid").First().Value);
				curintf.Add(srt.Descendants("curintf").First().Value);
				rowStatus.Add(srt.Descendants("rowstatus").First().Value);
				cursrc.Add(srt.Descendants("cursrc").First().Value);
				deldelay.Add(Convert.ToInt32(srt.Descendants("deldelay").First().Value));
				totalLost.Add(Convert.ToInt32(srt.Descendants("totallost").First().Value));
				switchreas.Add(srt.Descendants("switchreas").First().Value);
				switchreastime.Add(srt.Descendants("switchreas").First().Value); /*DateTime.Parse(srt.Descendants("switchreas").First().Value).ToOADate()*/
				pkterrcnt.Add(Convert.ToInt32(srt.Descendants("pkterrcnt").First().Value));
				drstate.Add(srt.Descendants("drstate").First().Value);
				acqMode.Add(srt.Descendants("acqmode").First().Value);
				bissprofen.Add(srt.Descendants("bissprofen").First().Value);
				bissprof.Add(srt.Descendants("bissprof").First().Value);
				tsPort.Add(srt.Descendants("tsprot").First().Value);
				sendrate.Add(Convert.ToDouble(srt.Descendants("sendrate").First().Value));
				recvrate.Add(Convert.ToDouble(srt.Descendants("recvrate").First().Value));
				rtt.Add(Convert.ToInt32(srt.Descendants("rtt").First().Value));

				newKeys.Add(rowKey);
			}

			object[] pids = new object[]
			{
				Parameter.Srtstatustable.tablePid,
				Parameter.Srtstatustable.Pid.srtstatusstream_32502 ,
				Parameter.Srtstatustable.Pid.srtstatusact_32503 ,
				Parameter.Srtstatustable.Pid.srtstatuslock_32504 ,
				Parameter.Srtstatustable.Pid.srtstatusiplinkstat_32505 ,
				Parameter.Srtstatustable.Pid.srtstatusipsig_32506 ,
				Parameter.Srtstatustable.Pid.srtstatusinputrate_32507 ,
				Parameter.Srtstatustable.Pid.srtstatusnetname_32508 ,
				Parameter.Srtstatustable.Pid.srtstatusnetid_32509 ,
				Parameter.Srtstatustable.Pid.srtstatustransid_32510 ,
				Parameter.Srtstatustable.Pid.srtstatusscrambmode_32511 ,
				Parameter.Srtstatustable.Pid.srtstatuslsttunerea_32512 ,
				Parameter.Srtstatustable.Pid.srtstatustunereason_32513 ,
				Parameter.Srtstatustable.Pid.srtstatusfreqmode_32514 ,
				Parameter.Srtstatustable.Pid.srtstatussvclstmode_32515 ,
				Parameter.Srtstatustable.Pid.srtstatusbaten_32516 ,
				Parameter.Srtstatustable.Pid.srtstatusniten_32517 ,
				Parameter.Srtstatustable.Pid.srtstatussdten_32518 ,
				Parameter.Srtstatustable.Pid.srtstatuspaten_32519 ,
				Parameter.Srtstatustable.Pid.srtstatusacqstatus_32520 ,
				Parameter.Srtstatustable.Pid.srtstatusinputid_32521 ,
				Parameter.Srtstatustable.Pid.srtstatuscurintf_32522 ,
				Parameter.Srtstatustable.Pid.srtstatusrowstatus_32523 ,
				Parameter.Srtstatustable.Pid.srtstatuscursrc_32524 ,
				Parameter.Srtstatustable.Pid.srtstatusdeldelay_32525 ,
				Parameter.Srtstatustable.Pid.srtstatustotallost_32526 ,
				Parameter.Srtstatustable.Pid.srtstatusswitchreas_32527 ,
				Parameter.Srtstatustable.Pid.srtstatusswitchtime_32528 ,
				Parameter.Srtstatustable.Pid.srtstatuspkterrcnt_32529 ,
				Parameter.Srtstatustable.Pid.srtstatusdrstate_32530 ,
				Parameter.Srtstatustable.Pid.srtstatusacqmode_32531 ,
				Parameter.Srtstatustable.Pid.srtstatusbissprofen_32532,
				Parameter.Srtstatustable.Pid.srtstatusbissprof_32533 ,
				Parameter.Srtstatustable.Pid.srtstatustsprot_32534 ,
				Parameter.Srtstatustable.Pid.srtstatussendrate_32535 ,
				Parameter.Srtstatustable.Pid.srtstatusrecvrate_32536 ,
				Parameter.Srtstatustable.Pid.srtstatusrtt_32537 ,
			};

			object[] values = new object[]
			{
				aoKeys.ToArray(),
				streams.ToArray(),
				act.ToArray(),
				statusLock.ToArray(),
				ipLink.ToArray(),
				ipSig.ToArray(),
				inputRate.ToArray() ,
				netName.ToArray(),
				netID.ToArray(),
				transID.ToArray(),
				scrambMode.ToArray(),
				lsttunerea.ToArray(),
				tunereason.ToArray(),
				freqMode.ToArray(),
				svclstmode.ToArray(),
				baten.ToArray(),
				niten.ToArray(),
				sdten.ToArray(),
				paten.ToArray(),
				acqstatus.ToArray(),
				inputID.ToArray(),
				curintf.ToArray(),
				rowStatus.ToArray(),
				cursrc.ToArray(),
				deldelay.ToArray(),
				totalLost.ToArray(),
				switchreas.ToArray(),
				switchreastime.ToArray(),
				pkterrcnt.ToArray(),
				drstate.ToArray(),
				acqMode.ToArray(),
				bissprofen.ToArray(),
				bissprof.ToArray(),
				tsPort.ToArray(),
				sendrate.ToArray(),
				recvrate.ToArray(),
				rtt.ToArray(),
			};

			protocol.NotifyProtocol((int)SLNetMessages.NotifyType.NT_FILL_ARRAY_WITH_COLUMN, pids, values);
			var keysToDelete = existingKeys.Except(newKeys).ToList();
			protocol.DeleteRow(Parameter.Srtstatustable.tablePid, keysToDelete.ToArray());
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;
using XP2Api;

namespace XP2Api
{
	public static class XmlLinqExtensions
	{
		public static string GetAttributeValue(this XElement element, string attributeName, int exceptionValue)
		{
			var value = Convert.ToString(exceptionValue);

			if (element == null)
			{
				return value;
			}

			var attribute = element.Attribute(attributeName);

			return attribute != null ? attribute.Value : value;
		}

		public static string GetElementValue(this XElement element, string elementName)
		{
			var value = string.Empty;

			if (element == null)
			{
				return value;
			}

			var child = element.Element(elementName);
			value = child.GetElementValue();

			return value;
		}

		private static string GetElementValue(this XElement element)
		{
			return element != null ? element.Value : string.Empty;
		}
	}
}

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string stringXML = string.Empty;

			stringXML = Convert.ToString(protocol.GetParameter(Parameter.xmlcastatus_216));  // XML Response for CA Status

			if (!string.IsNullOrEmpty(stringXML))
			{
				stringXML = Regex.Replace(stringXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				var carStatusQaRowList = new List<QActionTableRow>();

				XDocument doc = XDocument.Parse(stringXML);

				if (doc.Descendants("ca").Any())
				{
					var pvus = doc.Descendants("pvu");

					foreach (var pvu in pvus)
					{
						var castatusQaRow = new CastatusQActionRow
						{
							Castatusindex_idx__11001 = pvu.GetElementValue("iseuseradd"),
							Castatusiseuseraddress_11002 = pvu.GetElementValue("iseuseradd"),
							Castatusencdatapacketspassed_11003 = pvu.GetElementValue("encpktpass"),
							Castatusencdatapacketsreceived_11004 = pvu.GetElementValue("pubencpktrcv"),
							Castatusnon_encdatapacketspassed_11005 = pvu.GetElementValue("nencpktpass"),
							Castatusnon_encdatapacketsreceived_11006 = pvu.GetElementValue("nencpktrvc"),
						};

						carStatusQaRowList.Add(castatusQaRow);
					}

					if (carStatusQaRowList.Count > 0)
					{
						protocol.castatus.FillArray(carStatusQaRowList);
					}
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string stringXML = string.Empty;

			stringXML = Convert.ToString(protocol.GetParameter(Parameter.xmlchannelstatus_pestatus1_217));  // XML Response for Channel Status Table
			stringXML = Regex.Replace(stringXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

			int count = 0;
			if (!string.IsNullOrEmpty(stringXML))
			{
				XDocument doc = XDocument.Parse(stringXML);

				List<object[]> olchannelStatusValues = new List<object[]>();

				string stringPe = string.Empty;
				string stringInputname = string.Empty;
				string stringChannel = string.Empty;
				string stringChannelname = string.Empty;
				string stringCasystemid = string.Empty;
				string stringCanotsortable = string.Empty;
				string stringChannelencrypted = string.Empty;
				string stringChannelscramblednsort = string.Empty;
				string stringSrstatus = string.Empty;
				string sSrtype = string.Empty;
				string sSrstart = string.Empty;
				string sSrend = string.Empty;
				string sDispKey = string.Empty;

				if (doc.Descendants("record").Any())
				{
					var programs = from properties in doc.Descendants("record")
						select new
						{
							record = properties.Descendants(),
						};
					foreach (var properties in programs)
					{
						if (properties.record.Count() > 14)
						{
							stringPe = properties.record.Where(n => n.Name == "peid").First().Value;
							stringInputname = properties.record.Where(n => n.Name == "inp").First().Value;
							stringChannel = properties.record.Where(n => n.Name == "chn").First().Value;
							stringChannelname = properties.record.Where(n => n.Name == "chname").First().Value;
							stringCasystemid = properties.record.Where(n => n.Name == "caid").First().Value;
							stringCanotsortable = properties.record.Where(n => n.Name == "caauth").First().Value;
							stringChannelencrypted = properties.record.Where(n => n.Name == "caencrypt").First().Value;
							stringChannelscramblednsort = properties.record.Where(n => n.Name == "cascramble").First().Value;
							stringSrstatus = properties.record.Where(n => n.Name == "srstatus").First().Value;
							sSrtype = properties.record.Where(n => n.Name == "srtype").First().Value;
							sSrstart = properties.record.Where(n => n.Name == "srstartime").First().Value;
							sSrend = properties.record.Where(n => n.Name == "srendtime").First().Value;
							sDispKey = String.Format("{0}/{1}", stringPe, stringChannelname);

							olchannelStatusValues.Add(new object[] { stringPe, stringInputname, stringChannel, stringChannelname, stringCasystemid, stringCanotsortable, stringChannelencrypted, stringChannelscramblednsort, stringSrstatus, sSrtype, sSrstart, sSrend, sDispKey });
							count++;
						}
						else
						{
							protocol.Log("QA" + protocol.QActionID + "|Run|Expected length is 15, received: " + properties.record.Count(), LogType.Error, LogLevel.NoLogging);
						}
					}

					protocol.FillArray(Parameter.Channelstatus.tablePid, olchannelStatusValues, NotifyProtocol.SaveOption.Full);  // Sets the table 12000 parameters
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Net.Helper;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlcistatus_218));  // XML Response for CI Status Table
			int iCount = 0;
			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xdoc = XDocument.Parse(sXML);

				List<object[]> cistatusValues = new List<object[]>();

				string sCislot = string.Empty;
				string sCamstatus = string.Empty;
				string sSysname = string.Empty;
				string sCompname = string.Empty;
				string sManfcode = string.Empty;
				string sManfid = string.Empty;
				string sSerialnum = string.Empty;
				string sHardver = string.Empty;
				string sAppversion = string.Empty;

				if (xdoc.Descendants("ci").Any())
				{
					var programs = from properties in xdoc.Descendants("ci")
						select new
						{
							ci = properties.Descendants(),
						};
					// CAM name which is not null will be set to parameter ID 13250 - CAM Name for IRD Mediation
					string camNumber = String.Empty;
					foreach (var properties in programs)
					{
						if (properties.ci.Count() > 8)
						{
							// sCislot = properties.ci.ElementAt(0).Value;
							sCislot = properties.ci.Where(n => n.Name == "slot").First().Value;

							// sCamstatus = properties.ci.ElementAt(1).Value;
							sCamstatus = properties.ci.Where(n => n.Name == "camstatus").First().Value;

							// sSysname = properties.ci.ElementAt(2).Value;
							sSysname = properties.ci.Where(n => n.Name == "sysname").First().Value;

							// sCompname = properties.ci.ElementAt(3).Value;
							sCompname = properties.ci.Where(n => n.Name == "compname").First().Value;

							// sManfcode = properties.ci.ElementAt(4).Value;
							sManfcode = properties.ci.Where(n => n.Name == "mfgcode").First().Value;

							// sManfid = properties.ci.ElementAt(5).Value;
							sManfid = properties.ci.Where(n => n.Name == "mfgid").First().Value;

							// sSerialnum = properties.ci.ElementAt(6).Value;
							sSerialnum = properties.ci.Where(n => n.Name == "serialno").First().Value;

							// sAppversion = properties.ci.ElementAt(8).Value;
							sHardver = properties.ci.Where(n => n.Name == "appver").First().Value;

							if (camNumber.IsNullOrEmpty() && sSerialnum.IsNotNullOrEmpty())
							{
								camNumber = sSerialnum;
							}

							cistatusValues.Add(new object[] { sCislot, sCamstatus, sSysname, sCompname, sManfcode, sManfid, sSerialnum, sHardver, sAppversion });
							iCount++;
						}
						else
						{
							protocol.Log("QA" + protocol.QActionID + "|Run|Expected length is 9, received: " + properties.ci.Count(), LogType.Error, LogLevel.NoLogging);
						}
					}

					protocol.SetParameter(Parameter.camnumberirdmediation_13250, camNumber);

					protocol.FillArray(Parameter.Cistatus.tablePid, cistatusValues, NotifyProtocol.SaveOption.Full);  // Set table 13000 parameters
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iPos = 8000;
		int devType = Convert.ToInt32(protocol.GetParameter(Parameter.devicetype_6));
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmloutputstatus_asi_moip_1_219));  // XML Response for MOIP Output Status
			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);

				// ASI/MOIP Status
				if (xDoc.Descendants("output").Any() && devType == 1)
				{
					DefineValuesIfSS(protocol, xDoc, iPos);
				}

				if (xDoc.Descendants("output").Any() && devType == 2)
				{
					DefineValuesIfMS(protocol, xDoc, iPos);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void DefineValuesIfSS(SLProtocol protocol, XDocument xDoc, int iPos)
	{
		string sRate = xDoc.Descendants("output").Descendants("asi").Descendants("rate").Any() ? xDoc.Descendants("output").Descendants("asi").Descendants("rate").FirstOrDefault().Value : "-1";
		string sFree = xDoc.Descendants("output").Descendants("asi").Descendants("free").Any() ? xDoc.Descendants("output").Descendants("asi").Descendants("free").FirstOrDefault().Value : "-1";
		string sEnginerr = xDoc.Descendants("output").Descendants("moip").Descendants("enginerr").Any() ? xDoc.Descendants("output").Descendants("moip").Descendants("enginerr").FirstOrDefault().Value : "-1";
		string sOverflow = xDoc.Descendants("output").Descendants("moip").Descendants("overflow").Any() ? xDoc.Descendants("output").Descendants("moip").Descendants("overflow").FirstOrDefault().Value : "-1";
		string sComrate = xDoc.Descendants("output").Descendants("moip").Descendants("comrate").Any() ? xDoc.Descendants("output").Descendants("moip").Descendants("comrate").FirstOrDefault().Value : "-1";
		string sTs1rate = xDoc.Descendants("output").Descendants("moip").Descendants("ts1rate").Any() ? xDoc.Descendants("output").Descendants("moip").Descendants("ts1rate").FirstOrDefault().Value : "-1";
		string sTs2rate = xDoc.Descendants("output").Descendants("moip").Descendants("ts2rate").Any() ? xDoc.Descendants("output").Descendants("moip").Descendants("ts2rate").FirstOrDefault().Value : "-1";
		int[] paramsIds = new int[7];
		AssignIdsToSet(paramsIds, iPos);
		object[] allValues = new object[] { sRate, sFree, sEnginerr, sOverflow, sComrate, sTs1rate, sTs2rate };
		protocol.SetParameters(paramsIds, allValues);
	}

	private static void DefineValuesIfMS(SLProtocol protocol, XDocument xDoc, int iPos)
	{
		string sRate = xDoc.Descendants("output").Descendants("asi1").Descendants("rate").Any() ? xDoc.Descendants("output").Descendants("asi1").Descendants("rate").FirstOrDefault().Value : "-1";
		string sFree = xDoc.Descendants("output").Descendants("asi1").Descendants("free").Any() ? xDoc.Descendants("output").Descendants("asi1").Descendants("free").FirstOrDefault().Value : "-1";
		string sEnginerr = xDoc.Descendants("output").Descendants("moip1").Descendants("enginerr").Any() ? xDoc.Descendants("output").Descendants("moip1").Descendants("enginerr").FirstOrDefault().Value : "-1";
		string sOverflow = xDoc.Descendants("output").Descendants("moip1").Descendants("overflow").Any() ? xDoc.Descendants("output").Descendants("moip1").Descendants("overflow").FirstOrDefault().Value : "-1";
		string sComrate = xDoc.Descendants("output").Descendants("moip1").Descendants("comrate").Any() ? xDoc.Descendants("output").Descendants("moip1").Descendants("comrate").FirstOrDefault().Value : "-1";
		string sTs1rate = xDoc.Descendants("output").Descendants("moip1").Descendants("ts1rate").Any() ? xDoc.Descendants("output").Descendants("moip1").Descendants("ts1rate").FirstOrDefault().Value : "-1";
		string sTs2rate = xDoc.Descendants("output").Descendants("moip1").Descendants("ts2rate").Any() ? xDoc.Descendants("output").Descendants("moip1").Descendants("ts2rate").FirstOrDefault().Value : "-1";
		string sRate2 = xDoc.Descendants("output").Descendants("asi2").Descendants("rate").Any() ? xDoc.Descendants("output").Descendants("asi2").Descendants("rate").FirstOrDefault().Value : "-1";
		string sFree2 = xDoc.Descendants("output").Descendants("asi2").Descendants("free").Any() ? xDoc.Descendants("output").Descendants("asi2").Descendants("free").FirstOrDefault().Value : "-1";
		string sEnginerr2 = xDoc.Descendants("output").Descendants("moip2").Descendants("enginerr").Any() ? xDoc.Descendants("output").Descendants("moip2").Descendants("enginerr").FirstOrDefault().Value : "-1";
		string sOverflow2 = xDoc.Descendants("output").Descendants("moip2").Descendants("overflow").Any() ? xDoc.Descendants("output").Descendants("moip2").Descendants("overflow").FirstOrDefault().Value : "-1";
		string sComrate2 = xDoc.Descendants("output").Descendants("moip2").Descendants("comrate").Any() ? xDoc.Descendants("output").Descendants("moip2").Descendants("comrate").FirstOrDefault().Value : "-1";
		string sTs1rate2 = xDoc.Descendants("output").Descendants("moip2").Descendants("ts1rate").Any() ? xDoc.Descendants("output").Descendants("moip2").Descendants("ts1rate").FirstOrDefault().Value : "-1";
		string sTs2rate2 = xDoc.Descendants("output").Descendants("moip2").Descendants("ts2rate").Any() ? xDoc.Descendants("output").Descendants("moip2").Descendants("ts2rate").FirstOrDefault().Value : "-1";
		int[] paramsIds = new int[14];
		AssignIdsToSet(paramsIds, iPos);
		object[] allValues = new object[] { sRate, sFree, sEnginerr, sOverflow, sComrate, sTs1rate, sTs2rate, sRate2, sFree2, sEnginerr2, sOverflow2, sComrate2, sTs1rate2, sTs2rate2 };
		protocol.SetParameters(paramsIds, allValues);
	}

	private static void AssignIdsToSet(int[] paramsIds, int iPos)
	{
		for (int i = 0; i < paramsIds.Length; i++)
		{
			paramsIds[i] = iPos + i;
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	///     Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner.</param>
	public static void Run(SLProtocol protocol)
	{
		int iTriggerParameter = protocol.GetTriggerParameter();
		int iParameterID = 0;
		int iTableID = 0;

		if (iTriggerParameter == 220)
		{
			iParameterID = 220;
			iTableID = 14000;
		}
		else if (iTriggerParameter == 221)
		{
			iParameterID = 221;
			iTableID = 15000;
		}
		else if (iTriggerParameter == 231)
		{
			iParameterID = 231;
			iTableID = 14500;
		}
		else
		{
			iParameterID = 232;
			iTableID = 15600;
		}

		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(iParameterID)); // XML Response for ASI Outputs table
			int sCount = 0;
			if (String.IsNullOrEmpty(sXML))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
				return;
			}

			sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

			XDocument xdoc = XDocument.Parse(sXML);

			List<object[]> loAll = new List<object[]>();

			string sPeid = String.Empty;
			string sType = String.Empty;
			string sInpid = String.Empty;
			string sOutpid = String.Empty;
			string sPcr = String.Empty;
			string sScrambled = String.Empty;
			string sReceived = String.Empty;

			// ASI OUTPUT TABLE
			if (!xdoc.Descendants("pid").Any())
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|There are not \"pid\" tags in the current XML.", LogType.Error, LogLevel.NoLogging);
				return;
			}

			var programs = from properties in xdoc.Descendants("pid")
				select new
				{
					pid = properties.Descendants() != null ? properties.Descendants() : null,
				};

			int counter = 1;
			foreach (var properties in programs)
			{
				if (properties.pid.Count() >= 8)
				{
					sPeid = properties.pid.Where(n => n.Name == "peid").First().Value;
					sType = properties.pid.Where(n => n.Name == "type").First().Value;
					sInpid = iTableID == 15000
						? String.Format("{0}/{1}", counter, properties.pid.Where(n => n.Name == "inpid").First().Value)
						: properties.pid.Where(n => n.Name == "inpid").First().Value;
					sOutpid = properties.pid.Where(n => n.Name == "outpid").First().Value;
					sPcr = properties.pid.Where(n => n.Name == "pcr").First().Value;
					sScrambled = properties.pid.Where(n => n.Name == "scrambled").First().Value;
					sReceived = properties.pid.Where(n => n.Name == "received").First().Value;

					loAll.Add(new object[] { sPeid, sType, sInpid, sOutpid, sPcr, sScrambled, sReceived });
					sCount++;
					counter++;
				}
			}

			for (int i = 0; i < sCount; i++)
			{
				protocol.FillArray(iTableID, loAll, NotifyProtocol.SaveOption.Full); // Set table 14000 parameters
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using QAction_3;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			int iTrigger = protocol.GetTriggerParameter();
			uint[] aiCiIds = new uint[]
			{
				Parameter.cicamquerysupport_306,
				Parameter.cicamautoreset_307,
				Parameter.cilistmanagementtype_308,
				Parameter.cicamautorecovery_309,
				Parameter.citsonidcheck_310,
				Parameter.citransportid_311,
				Parameter.cionetid_312,
				Parameter.cicamtshandling_313,
				(uint)iTrigger,
			};
			int[] aoCiValues = Array.ConvertAll((object[])protocol.GetParameters(aiCiIds), Convert.ToInt32);

			string sQuerySupp = ConvertIntToStringValue(aoCiValues[0], Parameter.cicamquerysupport_306, iTrigger, aoCiValues[8]);
			string sAutoReset = ConvertIntToStringValue(aoCiValues[1], Parameter.cicamautoreset_307, iTrigger, aoCiValues[8]);
			string sMngType = ConvertIntToStringValue(aoCiValues[2], Parameter.cilistmanagementtype_308, iTrigger, aoCiValues[8]);
			string sAutoRecov = ConvertIntToStringValue(aoCiValues[3], Parameter.cicamautorecovery_309, iTrigger, aoCiValues[8]);
			string sTsOnidCheck = ConvertIntToStringValue(aoCiValues[4], Parameter.citsonidcheck_310, iTrigger, aoCiValues[8]);
			string sTsPortId = iTrigger != (Parameter.citransportid_311 + 50) ? Convert.ToString(aoCiValues[5]) : Convert.ToString(aoCiValues[8]);
			string sONetId = iTrigger != (Parameter.cionetid_312 + 50) ? Convert.ToString(aoCiValues[6]) : Convert.ToString(aoCiValues[8]);
			string sTsHandl = ConvertIntToStringValue(aoCiValues[7], Parameter.cicamtshandling_313, iTrigger, aoCiValues[8]);

			string sDataString = "<ca><ci>" +
				"<sendquery>{0}</sendquery>" +
				"<autoreset>{1}</autoreset>" +
				"<listmgmt>{2}</listmgmt>" +
				"<tsonetchk>{3}</tsonetchk>" +
				"<tsid>{4}</tsid>" +
				"<onetid>{5}</onetid>" +
				"<tsrouting>{6}</tsrouting>" +
				"<autorec>{7}</autorec>" +
				"</ci></ca>";

			string sCiSetUpData = String.Format(sDataString, sQuerySupp, sAutoReset, sMngType, sTsOnidCheck, sTsPortId, sONetId, sTsHandl, sAutoRecov);

			protocol.SetParameter(Parameter.xmlcisetupsetdata_244, sCiSetUpData);
			protocol.CheckTrigger((int)ProtocolTriggers.AfterBuildCiSetupData_9);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	public static string ConvertIntToStringValue(int iVal, int iIdentifier, int iTrigger, int iNewVal)
	{
		if (iIdentifier == Parameter.cilistmanagementtype_308)
		{
			return IdentifyValue(iIdentifier, iTrigger, iVal, iNewVal) == 1 ? "AddDel" : "UpdateAll";
		}
		else if (iIdentifier == Parameter.cicamtshandling_313)
		{
			return IdentifyValue(iIdentifier, iTrigger, iVal, iNewVal) == 1 ? "EntireTS" : "ServicesOnly";
		}
		else
		{
			return IdentifyValue(iIdentifier, iTrigger, iVal, iNewVal) == 1 ? "Enable" : "Disable";
		}
	}

	public static int IdentifyValue(int iIdentifier, int iTrigger, int iVal, int iNewVal)
	{
		if ((iIdentifier + 50) == iTrigger)
		{
			return iNewVal;
		}
		else
		{
			return iVal;
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;
using Skyline.Protocol.Logging;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iTriggerParameter = protocol.GetTriggerParameter();
		int iTableId = 0;
		int iParamerterId = 0;

		if (iTriggerParameter == 222)
		{
			iTableId = 16000;
			iParamerterId = 222;
		}
		else
		{
			iTableId = 17000;
			iParamerterId = 223;
		}

		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(iParamerterId));  // XML Response for  ASI DPM Collisions
			int iCount = 0;
			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xdoc = XDocument.Parse(sXML);
				List<object[]> loAll = new List<object[]>();
				string sType = String.Empty;
				string sPeidx1 = String.Empty;
				string sPmtrow1 = String.Empty;
				string sPeidx2 = String.Empty;
				string sPmtrow2 = String.Empty;
				string sValue = String.Empty;
				string sIdx = String.Empty;

				// ASI OUTPUT TABLE
				if (xdoc.Descendants("collision").Any())
				{
					var programs = from properties in xdoc.Descendants("collision")
								   select new
								   {
									   collision = properties.Descendants(),
								   };
					foreach (var properties in programs)
					{
						if (properties.collision.Count() > 6)
						{
							sType = properties.collision.Where(n => n.Name == "type").First().Value;
							sPeidx1 = properties.collision.Where(n => n.Name == "peidx1").First().Value;
							sPmtrow1 = properties.collision.Where(n => n.Name == "pmtrow1").First().Value;
							sPeidx2 = properties.collision.Where(n => n.Name == "peidx2").First().Value;
							sPmtrow2 = properties.collision.Where(n => n.Name == "pmtrow2").First().Value;
							sValue = properties.collision.Where(n => n.Name == "value").First().Value;
							sIdx = properties.collision.Where(n => n.Name == "idx").First().Value;

							loAll.Add(new object[] { sType, sPeidx1, sPmtrow1, sPeidx2, sPmtrow2, sValue, sIdx });
							iCount++;
						}
					}

					protocol.FillArray(iTableId, loAll, NotifyProtocol.SaveOption.Full);  // Set table 16000 parameters
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Information, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			LogHandler.LogException(protocol, ex);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocolExt protocol)
	{
		int iPos = 9000;
		try
		{
			int iCount = 0;

			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlservicesdecode_224));  // XML Response for Service Decode

			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);

				try
				{
					// ASI/MOIP Status
					if (xDoc.Descendants("decode").Any())
					{
						string sStream = xDoc.Descendants("decode").Descendants("video").Descendants("stream").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("stream").FirstOrDefault().Value : "-1";
						string sPvoformat = xDoc.Descendants("decode").Descendants("video").Descendants("pvoformat").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("pvoformat").FirstOrDefault().Value : "-1";
						string sDoformat = xDoc.Descendants("decode").Descendants("video").Descendants("sdoformat").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("sdoformat").FirstOrDefault().Value : "-1";
						string sBitrate = xDoc.Descendants("decode").Descendants("video").Descendants("bitrate").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("bitrate").FirstOrDefault().Value : "-1";
						string sPulldown32 = xDoc.Descendants("decode").Descendants("video").Descendants("pulldown32").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("pulldown32").FirstOrDefault().Value : "-1";
						string sFrmpersec = xDoc.Descendants("decode").Descendants("video").Descendants("frmpersec").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("frmpersec").FirstOrDefault().Value : "-1";
						string sSyncmode = xDoc.Descendants("decode").Descendants("video").Descendants("syncmode").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("syncmode").FirstOrDefault().Value : "-1";
						string sEncoding = xDoc.Descendants("decode").Descendants("video").Descendants("encoding").Any() ? xDoc.Descendants("decode").Descendants("video").Descendants("encoding").FirstOrDefault().Value : "-1";
						string sCcmode = xDoc.Descendants("decode").Descendants("cc").Descendants("ccoutact").Any() ? xDoc.Descendants("decode").Descendants("cc").Descendants("ccoutact").FirstOrDefault().Value : "-1";
						string sVitcstatus = xDoc.Descendants("decode").Descendants("vbi").Descendants("vitcstatus").Any() ? xDoc.Descendants("decode").Descendants("vbi").Descendants("vitcstatus").FirstOrDefault().Value : "-1";
						string sInterlaced = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("interlaced").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("interlaced").FirstOrDefault().Value : "-1";
						string sFramessec = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("framessec").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("framessec").FirstOrDefault().Value : "-1";
						string sLines = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("lines").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("lines").FirstOrDefault().Value : "-1";
						string sWordsline = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("wordsline").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("wordsline").FirstOrDefault().Value : "-1";
						string sFirstline = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("firstline").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("firstline").FirstOrDefault().Value : "-1";
						string sLastline = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("lastline").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("lastline").FirstOrDefault().Value : "-1";
						string sWitchline = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("switchline").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("switchline").FirstOrDefault().Value : "-1";
						string sMultiline = xDoc.Descendants("decode").Descendants("sdiglb").Descendants("multiline").Any() ? xDoc.Descendants("decode").Descendants("sdiglb").Descendants("multiline").FirstOrDefault().Value : "-1";
						int[] paramsIds = new int[18];

						for (int i = 0; i < paramsIds.Length; i++)
						{
							paramsIds[i] = iPos + i;
						}

						object[] allValues = new object[] { sStream, sPvoformat, sDoformat, sBitrate, sPulldown32, sFrmpersec, sSyncmode, sEncoding, sCcmode, sVitcstatus, sInterlaced, sFramessec, sLines, sWordsline, sFirstline, sLastline, sWitchline, sMultiline };
						protocol.SetParameters(paramsIds, allValues);
					}
				}
				catch (Exception ex)
				{
					protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
				}

				try
				{
					List<object[]> loAllAudio = new List<object[]>();

					string sDevice = string.Empty;
					string sPid = string.Empty;
					string sLang = string.Empty;
					string sMode = string.Empty;
					string sBitrate2 = string.Empty;
					string sSfr = string.Empty;
					string sBufferlvl = string.Empty;
					string sDdpmode = string.Empty;
					string sDualmono = string.Empty;
					int lowestPort = int.MaxValue;
					int lowestPort2 = int.MaxValue;
					int bitrate = -1;
					int bitrate2 = -1;

					if (xDoc.Descendants("audio").Any())
					{
						var programs = from properties in xDoc.Descendants("audio")
							select new
							{
								audio = properties.Descendants(),
							};
						foreach (var properties in programs)
						{
							if (properties.audio.Count() > 8)
							{
								// sDevice = properties.audio.ElementAt(0).Value;
								sDevice = properties.audio.Where(n => n.Name == "device").First().Value;

								// sPid = properties.audio.ElementAt(5).Value;
								sPid = properties.audio.Where(n => n.Name == "pid").First().Value;

								// sLang = properties.audio.ElementAt(6).Value;
								sLang = properties.audio.Where(n => n.Name == "lang").First().Value;

								// sMode = properties.audio.ElementAt(1).Value;
								sMode = properties.audio.Where(n => n.Name == "mode").First().Value;

								// sBitrate = properties.audio.ElementAt(2).Value;
								sBitrate2 = properties.audio.Where(n => n.Name == "bitrate").First().Value;

								// sSfr = properties.audio.ElementAt(4).Value;
								sSfr = properties.audio.Where(n => n.Name == "sfr").First().Value;

								// sBufferlvl = properties.audio.ElementAt(3).Value;
								sBufferlvl = properties.audio.Where(n => n.Name == "bufferlvl").First().Value;

								// sDdpmode = properties.audio.ElementAt(7).Value;
								sDdpmode = properties.audio.Where(n => n.Name == "ddpmode").First().Value;

								// sDualmono = properties.audio.ElementAt(8).Value;
								sDualmono = properties.audio.Where(n => n.Name == "dualmono").First().Value;

								int currentPort = Convert.ToInt32(sDevice);
								if (currentPort < lowestPort)
								{
									lowestPort = currentPort;
									bitrate = Convert.ToInt32(sBitrate2);
								}

								if (currentPort > lowestPort && currentPort < lowestPort2)
								{
									lowestPort2 = currentPort;
									bitrate2 = Convert.ToInt32(sBitrate2);
								}

								loAllAudio.Add(new object[] { sDevice, sPid, sLang, sMode, sBitrate2, sSfr, sBufferlvl, sDdpmode, sDualmono });
								iCount++;
							}
							else
							{
								protocol.Log("QA" + protocol.QActionID + "|Run|Expected length is 9, received: " + properties.audio.Count(), LogType.Error, LogLevel.NoLogging);
							}
						}

						protocol.FillArray(Parameter.Audiostatus.tablePid, loAllAudio, NotifyProtocol.SaveOption.Full);  // Set table 18000 parameters
						protocol.SetParameters(new[] { Parameter.audio1bitrate_18020, Parameter.audio2bitrate_18021 }, new[] { (object)bitrate, (object)bitrate2 });
					}

					int icountsdi = 0;
					List<object[]> loAllSDI = new List<object[]>();

					string serviceid = string.Empty;
					string active = string.Empty;
					string adjline = string.Empty;
					string topline = string.Empty;
					string bottomline = string.Empty;
					string maxlines = string.Empty;
					string avgdata = string.Empty;
					string avgtranpkt = string.Empty;
					string avgdroppkt = string.Empty;

					if (xDoc.Descendants("sdisvc").Any())
					{
						var programs = from properties in xDoc.Descendants("sdisvc")
									   select new
									   {
										   sdisvc = properties.Descendants() != null ? properties.Descendants() : null,
									   };
						foreach (var properties in programs)
						{
							if (properties.sdisvc.Count() > 8)
							{
								// serviceid = properties.sdisvc.ElementAt(0).Value;
								serviceid = properties.sdisvc.Where(n => n.Name == "serviceid").First().Value;

								// active = properties.sdisvc.ElementAt(1).Value;
								active = properties.sdisvc.Where(n => n.Name == "active").First().Value;

								// adjline = properties.sdisvc.ElementAt(2).Value;
								adjline = properties.sdisvc.Where(n => n.Name == "adjline").First().Value;

								// topline = properties.sdisvc.ElementAt(3).Value;
								topline = properties.sdisvc.Where(n => n.Name == "topline").First().Value;

								// bottomline = properties.sdisvc.ElementAt(4).Value;
								bottomline = properties.sdisvc.Where(n => n.Name == "bottomline").First().Value;

								// maxlines = properties.sdisvc.ElementAt(5).Value;
								maxlines = properties.sdisvc.Where(n => n.Name == "maxlines").First().Value;

								// avgdata = properties.sdisvc.ElementAt(6).Value;
								avgdata = properties.sdisvc.Where(n => n.Name == "avgdata").First().Value;

								// avgtranpkt = properties.sdisvc.ElementAt(7).Value;
								avgtranpkt = properties.sdisvc.Where(n => n.Name == "avgtranpkt").First().Value;

								// avgdroppkt = properties.sdisvc.ElementAt(8).Value;
								avgdroppkt = properties.sdisvc.Where(n => n.Name == "avgdroppkt").First().Value;

								icountsdi++;

								loAllSDI.Add(new object[] { icountsdi, serviceid, active, adjline, topline, bottomline, maxlines, avgdata, avgtranpkt, avgdroppkt });
							}
							else
							{
								protocol.Log("QA" + protocol.QActionID + "|Run|Expected length is 9, received: " + properties.sdisvc.Count(), LogType.Error, LogLevel.NoLogging);
							}
						}

						protocol.FillArray(Parameter.Sdivanctable.tablePid, loAllSDI, NotifyProtocol.SaveOption.Full);  // Set table 19000 parameters

						string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.sdivancenabledresponseget_35003));

						XDocument doc;

						if (!string.IsNullOrWhiteSpace(stringXmlResponse))
						{
							try
							{
								stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

								doc = XDocument.Parse(stringXmlResponse);
								IEnumerable<XElement> video = doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD");
								int i = 1;
								foreach (XElement record in video)
								{
									SdivanctableQActionRow row = new SdivanctableQActionRow
									{
										Sdivanckey_19010 = Convert.ToString(i),
										Sdivancenabled_19011 = Convert.ToString(record.Descendants("ITEM").Skip(1).Descendants("VALUE").FirstOrDefault().Value),
										Sdivancsdivancid_19012 = Convert.ToString(record.Descendants("ITEM").Skip(3).Descendants("VALUE").FirstOrDefault().Value),
									};

									protocol.sdivanctable.SetRow(row, true);
									i++;
								}
							}
							catch (Exception ex)
							{
								protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
							}
						}
					}
				}
				catch (Exception ex)
				{
					protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse XML: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

