public class QAction
{
	/// <summary>
	/// RF.
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner.</param>
	public static void Run(SLProtocol protocol)
	{
		// Checks which RF Port is active and copies the Port parameters to Inputs page from RF Settings page
		object[] paramValues = (object[])protocol.GetParameters(new UInt32[] { Parameter.rf1port_1000, Parameter.rf1downlinkfrequency_1001, Parameter.rf1symbolrate_1002, Parameter.rf1fec_1003, Parameter.rf1modulation_1004, Parameter.rf1rolloff_1005, Parameter.rf1i_q_1006, Parameter.rf1afclimit_1007, Parameter.rf122khz_1008, Parameter.rf1power_1009, Parameter.rf1lo1_1010, Parameter.rf1lo2_1011, Parameter.rf1crossover_1012, Parameter.rf1polarization_1013, Parameter.rf1orbitalposition_1014, Parameter.rf1east_westflag_1015, Parameter.rf1activateinput_1016, Parameter.rf1satinput_1500, Parameter.rf2port_2000, Parameter.rf2downlinkfrequency_2001, Parameter.rf2symbolrate_2002, Parameter.rf2fec_2003, Parameter.rf2modulation_2004, Parameter.rf2rolloff_2005, Parameter.rf2i_q_2006, Parameter.rf2afclimit_2007, Parameter.rf222khz_2008, Parameter.rf2power_2009, Parameter.rf2lo1_2010, Parameter.rf2lo2_2011, Parameter.rf2crossover_2012, Parameter.rf2polarization_2013, Parameter.rf2orbitalposition_2014, Parameter.rf2east_westflag_2015, Parameter.rf2activateinput_2016, Parameter.rf2satinput_2500, Parameter.rf3port_3000, Parameter.rf3downlinkfrequency_3001, Parameter.rf3symbolrate_3002, Parameter.rf3fec_3003, Parameter.rf3modulation_3004, Parameter.rf3rolloff_3005, Parameter.rf3i_q_3006, Parameter.rf3afclimit_3007, Parameter.rf322khz_3008, Parameter.rf3power_3009, Parameter.rf3lo1_3010, Parameter.rf3lo2_3011, Parameter.rf3crossover_3012, Parameter.rf3polarization_3013, Parameter.rf3orbitalposition_3014, Parameter.rf3east_westflag_3015, Parameter.rf3activateinput_3016, Parameter.rf3satinput_3500, Parameter.rf4port_4000, Parameter.rf4downlinkfrequency_4001, Parameter.rf4symbolrate_4002, Parameter.rf4fec_4003, Parameter.rf4modulation_4004, Parameter.rf4rolloff_4005, Parameter.rf4i_q_4006, Parameter.rf4afclimit_4007, Parameter.rf422khz_4008, Parameter.rf4power_4009, Parameter.rf4lo1_4010, Parameter.rf4lo2_4011, Parameter.rf4crossover_4012, Parameter.rf4polarization_4013, Parameter.rf4orbitalposition_4014, Parameter.rf4east_westflag_4015, Parameter.rf4activateinput_4016, Parameter.rf4satinput_4500, Parameter.rf1activateinput_1016, Parameter.rf2activateinput_2016, Parameter.rf3activateinput_3016, Parameter.rf4activateinput_4016 });
		string sParam1 = Convert.ToString(paramValues[72]);
		string sParam2 = Convert.ToString(paramValues[73]);
		string sParam3 = Convert.ToString(paramValues[74]);
		string sParam4 = Convert.ToString(paramValues[75]);

		var settingParamsIDs = new[] { Parameter.rfport_7000, Parameter.rfdownlinkfrequency_7001, Parameter.rfsymbolrate_7002, Parameter.rffec_7003, Parameter.rfmodulation_7004, Parameter.rfrolloff_7005, Parameter.rfi_q_7006, Parameter.rfafclimit_7007, Parameter.rf22khz_7008, Parameter.rfpower_7009, Parameter.rflo1_7010, Parameter.rflo2_7011, Parameter.rfcrossover_7012, Parameter.rfpolarization_7013, Parameter.rforbitalposition_7014, Parameter.rfeast_westflag_7015, Parameter.rfactivateinput_7016, Parameter.rfsatinput_7017 };

