		}
		else if (sParam2.Trim().Equals("Yes"))
		{
			protocol.SetParameters(settingParamsIDs, new[] { paramValues[18], paramValues[19], paramValues[20], paramValues[21], paramValues[22], paramValues[23], paramValues[24], paramValues[25], paramValues[26], paramValues[27], paramValues[28], paramValues[29], paramValues[30], paramValues[31], paramValues[32], paramValues[33], paramValues[34], paramValues[35] });
		}
		else if (sParam3.Trim().Equals("Yes"))
		{
			protocol.SetParameters(settingParamsIDs, new[] { paramValues[36], paramValues[37], paramValues[38], paramValues[39], paramValues[40], paramValues[41], paramValues[42], paramValues[43], paramValues[44], paramValues[45], paramValues[46], paramValues[47], paramValues[48], paramValues[49], paramValues[50], paramValues[51], paramValues[52], paramValues[53] });
		}
		else if (sParam4.Trim().Equals("Yes"))
		{
			protocol.SetParameters(settingParamsIDs, new[] { paramValues[54], paramValues[55], paramValues[56], paramValues[57], paramValues[58], paramValues[59], paramValues[60], paramValues[61], paramValues[62], paramValues[63], paramValues[64], paramValues[65], paramValues[66], paramValues[67], paramValues[68], paramValues[69], paramValues[70], paramValues[71] });
		}
		else
		{
			// Do nothing
		}

		if (sParam1.Trim().Equals("No") && sParam2.Trim().Equals("No") && sParam3.Trim().Equals("No") && sParam4.Trim().Equals("No"))
