}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlethernetstatuscommand_226));  // XML Response for Ethernet
			int iCount = 0;
			if (!string.IsNullOrEmpty(sXML))
			{
				XDocument xdoc = XDocument.Parse(sXML);

				List<object[]> loAll = new List<object[]>();

				string sPort = string.Empty;
				string sLink = string.Empty;
				string sSpeed = string.Empty;
				string sDuplex = string.Empty;
				string sXover = string.Empty;

				if (xdoc.Descendants("eth").Any())
				{
					// Do Nothing
				}

				var programs = from properties in xdoc.Descendants("eth")
					select new
					{
						eth = properties.Descendants() ?? null,
					};

				foreach (var properties in programs)
				{
					if (properties.eth.Count() > 4)
					{
						// sPort = properties.eth.ElementAt(0).Value;
						sPort = properties.eth.Where(n => n.Name == "port").First().Value;

						// sLink = properties.eth.ElementAt(1).Value;
						sLink = properties.eth.Where(n=>n.Name == "link").First().Value;

						// sSpeed = properties.eth.ElementAt(2).Value;
						sSpeed = properties.eth.Where(n=>n.Name == "speed").First().Value;

						// sDuplex = properties.eth.ElementAt(3).Value;
						sDuplex = properties.eth.Where(n=>n.Name == "duplex").First().Value;

						// sXover = properties.eth.ElementAt(4).Value;
						sXover = properties.eth.Where(n => n.Name == "xover").First().Value;

						loAll.Add(new object[] { sPort, sLink, sSpeed, sDuplex, sXover });
						iCount++;
					}
					else
					{
						protocol.Log("QA" + protocol.QActionID + "|Run|Expected length is 5, received: " + properties.eth.Count(), LogType.Error, LogLevel.NoLogging);
					}
				}

				protocol.FillArray(Parameter.Ethernetlinkstatus.tablePid, loAll, NotifyProtocol.SaveOption.Full);  // Set table 20000 parameters
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlpowerstatuscommand_227)); // XML Response for Power status command
			int iCount = 0;
			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xdoc = XDocument.Parse(sXML);

				List<object[]> loAll = new List<object[]>();

				string sSlot = string.Empty;
				string sGood = string.Empty;
				string sDisplayBoardName = string.Empty;
				string sStatus = string.Empty;

				if (xdoc.Descendants("power").Any())
				{
					var programs = from properties in xdoc.Descendants("power")
								   select new
								   {
									   power = properties.Descendants(),
								   };
					foreach (var properties in programs)
					{
						if (properties.power.Count() > 3)
						{
							// sSlot = properties.power.ElementAt(0).Value;
							sSlot = properties.power.Where(n => n.Name == "slot").First().Value;

							// sGood = properties.power.ElementAt(1).Value;
							sGood = properties.power.Where(n => n.Name == "good").First().Value;

							// sDisplayBoardName = properties.power.ElementAt(2).Value;
							sDisplayBoardName = properties.power.Where(n => n.Name == "displayBoardName").First().Value;

							// sStatus = properties.power.ElementAt(3).Value;
							sStatus = properties.power.Where(n => n.Name == "status").First().Value;

							loAll.Add(new object[] { sSlot, sGood, sDisplayBoardName, sStatus });
							iCount++;
						}
						else
						{
							protocol.Log("QA" + protocol.QActionID + "|Run|Expected length is 4, received: " + properties.power.Count(), LogType.Error, LogLevel.NoLogging);
						}
					}

					protocol.FillArray(Parameter.Powerstatus.tablePid, loAll, NotifyProtocol.SaveOption.Full); // Set table 21000 parameters
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using QAction_3;
using Skyline.DataMiner.Scripting;

public static class QAction
{
	/// <summary>
	/// SetXML.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iTID = protocol.GetTriggerParameter();
		int iPort = iTID / 1000;

		string sWrite = Convert.ToString(protocol.GetParameter(iTID));
		string sUrl = "ws/v2/service_cfg/input/rf?port=";
		try
		{
			// Downlink Frequency
			if (iTID == (iPort * 1000) + 51)
			{
				sUrl += iPort + "&dnlkfreq=" + sWrite;
			}

			// Symbol Rate
			if (iTID == (iPort * 1000) + 52)
			{
				sUrl += iPort + "&symrate=" + sWrite;
			}

			// FEC
			if (iTID == (iPort * 1000) + 53)
			{
				sUrl += iPort + "&fec=" + sWrite;
			}

			// Modulation
			if (iTID == (iPort * 1000) + 54)
			{
				sUrl += iPort + "&mod=" + sWrite;
			}

			// Roll-Off Factor
			if (iTID == (iPort * 1000) + 55)
			{
				sUrl += iPort + "&rolloff=" + sWrite;
			}

			// I/Q
			if (iTID == (iPort * 1000) + 56)
			{
				sUrl += iPort + "&iq=" + sWrite;
			}

			// AFC Limit
			if (iTID == (iPort * 1000) + 57)
			{
				sUrl += iPort + "&afcrange=" + sWrite;
			}

			// 22 KHz
			if (iTID == (iPort * 1000) + 58)
			{
				sUrl += iPort + "&sel22khz=" + sWrite;
			}

			// Power
			if (iTID == (iPort * 1000) + 59)
			{
				sUrl += iPort + "&pwr=" + sWrite;
			}

			// LO1
			if (iTID == (iPort * 1000) + 60)
			{
				sUrl += iPort + "&lO1=" + sWrite;
			}

			// LO2
			if (iTID == (iPort * 1000) + 61)
			{
				sUrl += iPort + "&lO2=" + sWrite;
			}

			// Crossover
			if (iTID == (iPort * 1000) + 62)
			{
				sUrl += iPort + "&xover=" + sWrite;
			}

			// Polarisation
			if (iTID == (iPort * 1000) + 63)
			{
				sUrl += iPort + "&pol=" + sWrite;
			}

			// Orbital Position
			if (iTID == (iPort * 1000) + 64)
			{
				sUrl += iPort + "&orbpos=" + sWrite;
			}

			// East/West Flag
			if (iTID == (iPort * 1000) + 65)
			{
				sUrl += iPort + "&ewflag=" + sWrite;
			}

			// Activate Input
			if (iTID == (iPort * 1000) + 66)
			{
				sUrl += iPort + "&act=" + sWrite;
			}

			protocol.SetParameters(new[] { Parameter.urlrfwrite_19 }, new object[] { sUrl });
			protocol.CheckTrigger((int)ProtocolTriggers.ExecuteWritesALLRFs_35);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml.Serialization;
using Skyline.DataMiner.Scripting;
using Skyline.Protocol.XmlClasses;

/// <summary>
/// DataMiner QAction Class: QActionName.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmltopversionresponse_111));
			if (string.IsNullOrEmpty(sXML))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
				return;
			}

			Versions versions;
			using (StringReader stringReader = new StringReader(sXML))
			{
				XmlSerializer serializer = new XmlSerializer(typeof(Versions));
				versions = (Versions)serializer.Deserialize(stringReader);
			}


			string retrievedVersion = versions.Top.Version;
			protocol.SetParameter(Parameter.firmwareversion_16604, retrievedVersion);
		}
		catch (Exception ex)
		{
			protocol.Log($"QA{protocol.QActionID}|{protocol.GetTriggerParameter()}|Run|Exception thrown:{Environment.NewLine}{ex}", LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using QAction_3;
using Skyline.DataMiner.Scripting;

public static class QAction
{
	/// <summary>
	///     SetXML.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iTID = protocol.GetTriggerParameter();
		string sWrite = Convert.ToString(protocol.GetParameter(iTID));
		string url = "ws/v2/service_cfg/input/asi?port=1&";
		string urlParameter = "act";

		protocol.SetParameter(Parameter.asi_write_744, String.Format("{0}{1}={2}", url, urlParameter, sWrite));
		protocol.CheckTrigger((int)ProtocolTriggers.WhenAsiInputParameterWritten_39);
	}
}

using System;
using System.Collections.Generic;
using QAction_3;
using Skyline.DataMiner.Scripting;

public static class QAction
{
	/// <summary>
	///     SetXML.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string key = protocol.RowKey();
			string[] split = key.Split('/');
			if (split.Length != 3)
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Set not possible.", LogType.Error, LogLevel.NoLogging);
			}

			string url = String.Format("ws/v2/service_cfg/input/moip?stream={0}&", split[2]);
			string urlParameter = String.Empty;

			int iTID = protocol.GetTriggerParameter();

			string sWrite = Convert.ToString(protocol.GetParameter(iTID));

			Dictionary<int, string> command = new Dictionary<int, string>
			{
				{ Parameter.Write.moipconfigurationmoipactivateinput_6051, "act" },
				{ Parameter.Write.moipconfigurationsourceipselectionmode_6052, "ipmode" },
				{ Parameter.Write.moipconfigurationfecmode_6053, "fecmode" },
				{ Parameter.Write.moipconfigurationenablemulticast_6054, "usemcast" },
				{ Parameter.Write.moipconfigurationmulticastaddress_6055, "mcastaddr" },
				{ Parameter.Write.moipconfigurationtsdestinationport_6056, "tsdestport" },
				{ Parameter.Write.moipconfigurationfeccolumnsport_6057, "feccolport" },
				{ Parameter.Write.moipconfigurationfecrowsport_6058, "fecrowport" },
				{ Parameter.Write.moipconfigurationsourcefiltering_6059, "srcfltmode" },
				{ Parameter.Write.moipconfigurationde_jitterconfigurationalgorithm_6060, "dejalg" },
				{ Parameter.Write.moipconfigurationinterface_6061, "ipiport" },
			};

			if (command.TryGetValue(iTID, out urlParameter))
			{
				protocol.SetParameter(Parameter.moip_write_746, String.Format("{0}{1}={2}", url, urlParameter, sWrite));
				protocol.CheckTrigger((int)ProtocolTriggers.WhenMoipParametersWritten_40);
			}
		}
		catch (Exception e)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Could not perform set on MOPI Stream.", LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocolExt protocol)
	{
		int iTriggerParameter = protocol.GetTriggerParameter();
		int iPos = 0;
		int iTableId = 0;
		object[] allValues = new object[5];

		switch (iTriggerParameter)
		{
			case Parameter.xmloutputasi_235:
				iPos = 8500;
				iTableId = 22000;
				break;
			case Parameter.xmloutputmoip_236:
				iPos = 9500;
				iTableId = 23000;
				break;
			case Parameter.xmloutputasi2_233:
				iPos = 8505;
				iTableId = 22100;
				break;
			default: // 234
				iPos = 9505;
				iTableId = 23100;
				break;
		}

		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(iTriggerParameter));  // XML Response of ASI Output Status
			if (string.IsNullOrEmpty(sXML))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
				return;
			}

			XDocument xDoc = XDocument.Parse(sXML);

			// ASI Settings
			ProcessOutput(protocol, iTriggerParameter, iPos, allValues, xDoc);

			if (!xDoc.Descendants("pe").Any())
			{
				protocol.Log("QA" + protocol.QActionID + "|" + iTriggerParameter + "|Run|There is no \"pe\" tag on the XML.", LogType.Error, LogLevel.NoLogging);
				return;
			}

			List<object[]> olasidpmValues = BuildColumnsToSet(protocol, xDoc);
			ProcesStreamsMoip2(protocol, iTriggerParameter, xDoc);

			protocol.FillArray(iTableId, olasidpmValues, NotifyProtocol.SaveOption.Full);  // Sets table 22000 parameters
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void ProcessOutput(SLProtocolExt protocol, int iTriggerParameter, int iPos, object[] allValues, XDocument xDoc)
	{
		if (xDoc.Descendants("output").Any())
		{
			string sMode = xDoc.Descendants("output").Descendants("mode").Any() ? xDoc.Descendants("output").Descendants("mode").FirstOrDefault().Value : "-1";
			string sDscrmode = xDoc.Descendants("output").Descendants("dscrmode").Any() ? xDoc.Descendants("output").Descendants("dscrmode").FirstOrDefault().Value : "-1";
			string sRatemode = xDoc.Descendants("output").Descendants("ratemode").Any() ? xDoc.Descendants("output").Descendants("ratemode").FirstOrDefault().Value : "-1";
			string sRate = xDoc.Descendants("output").Descendants("rate").Any() ? xDoc.Descendants("output").Descendants("rate").FirstOrDefault().Value : "-1";
			string sInsertnul = xDoc.Descendants("output").Descendants("insertnul").Any() ? xDoc.Descendants("output").Descendants("insertnul").FirstOrDefault().Value : "-1";

			string[] sArray = new[] { sMode, sDscrmode, sRatemode, sRate, sInsertnul };

			int[] oiparamsIds = AssignValuesToSet(iPos, iTriggerParameter, sArray, allValues);

			protocol.SetParameters(oiparamsIds, allValues);
		}
	}

	private static void ProcesStreamsMoip2(SLProtocolExt protocol, int iTriggerParameter, XDocument xDoc)
	{
		if (iTriggerParameter == Parameter.xmloutputmoip2_234)
		{
			List<QActionTableRow> rows = new List<QActionTableRow>();
			var ipos = from properties in xDoc.Descendants("ipo")
					   select new
					   {
						   ipo = properties.Descendants(),
					   };
			foreach (var p in ipos)
			{
				string id = p.ipo.Where(n => n.Name == "ipoid").FirstOrDefault().Value ?? String.Empty;

				if (!Double.TryParse(p.ipo.Where(n => n.Name == "iporate").FirstOrDefault().Value ?? "-1", NumberStyles.Float, CultureInfo.InvariantCulture, out double bitrate))
				{
					bitrate = -1;
				}

				rows.Add(new Moip2streamsQActionRow
				{
					Moip2streamsid_23201 = id,
					Moip2streamsbitrate_23202 = bitrate,
					Moip2streamsencapsulation_23203 = p.ipo.FirstOrDefault(n => n.Name == "encaps").Value ?? String.Empty,
					Moip2streamsdestinationip_23204 = p.ipo.FirstOrDefault(n => n.Name == "dest_ip").Value ?? String.Empty,
					Moip2streamsdestinationudpport_23205 = p.ipo.FirstOrDefault(n => n.Name == "dest_port").Value ?? "-1",
					Moip2streamsname_23206 = String.Format("PE{0}", id),
				});
			}

			if (rows.Count > 0)
			{
				protocol.moip2streams.FillArray(rows);
			}
		}
	}

	private static int[] AssignValuesToSet(int iPos, int iTriggerParameter, string[] sArray, object[] allValues)
	{
		string sMode = sArray[0];
		string sDscrmode = sArray[1];
		string sRatemode = sArray[2];
		string sRate = sArray[3];
		string sInsertnul = sArray[4];

		int[] oiparamsIds = new int[5];
		for (int i = 0; i < oiparamsIds.Length; i++)
		{
			oiparamsIds[i] = iPos + i;
		}

		if (iTriggerParameter == Parameter.xmloutputasi_235 || iTriggerParameter == Parameter.xmloutputasi2_233)
		{
			allValues[0] = sRatemode;
			allValues[1] = sRate;
			allValues[2] = sMode;
			allValues[3] = sDscrmode;
			allValues[4] = sInsertnul;
		}
		else
		{
			allValues[0] = sMode;
			allValues[1] = sDscrmode;
			allValues[2] = sRatemode;
			allValues[3] = sRate;
			allValues[4] = sInsertnul;
		}

		return oiparamsIds;
	}

	private static List<object[]> BuildColumnsToSet(SLProtocol protocol, XDocument xDoc)
	{
		int iCount = 0;
		List<object[]> olasidpmValues = new List<object[]>();

		string sPeid = String.Empty;
		string sAction = String.Empty;
		string sPmtpid = String.Empty;
		string sOutchan = String.Empty;

		var programs = from properties in xDoc.Descendants("pe")
					   select new
					   {
						   pe = properties.Descendants(),
					   };

		foreach (var properties in programs)
		{
			if (properties.pe.Count() > 3)
			{
				sPeid = properties.pe.Where(n => n.Name == "peid").First().Value;
				sAction = properties.pe.Where(n => n.Name == "action").First().Value;
				sPmtpid = properties.pe.Where(n => n.Name == "pmtpid").First().Value;
				sOutchan = properties.pe.Where(n => n.Name == "outchan").First().Value;

				olasidpmValues.Add(new object[] { sPeid, sAction, sPmtpid, sOutchan });
				iCount++;
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Expected length is 4, received: " + properties.pe.Count(), LogType.Error, LogLevel.NoLogging);
			}
		}

		return olasidpmValues;
	}
}

using System;
using System.Collections.Generic;
using QAction_3;
using Skyline.DataMiner.Scripting;

public static class QAction
{
	/// <summary>
	/// SetXML.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iTID = protocol.GetTriggerParameter();
		bool trigger = iTID <= Parameter.Write.asiinsertnullpacket_8554;
		string url = trigger ? String.Format("{0}?", Convert.ToString(protocol.GetParameter(Parameter.outputasiurl_26))) : "ws/v2/service_cfg/output/asi2?";

		string urlParameter = String.Empty;

		string sWrite = Convert.ToString(protocol.GetParameter(iTID));

		Dictionary<int, string> command;

		if (trigger)
		{
			command = new Dictionary<int, string>
			{
				{ Parameter.Write.asioutputratecontrolmode_8550,"ratemode" },
				{ Parameter.Write.asioutputrate_8551,"rate" },
				{ Parameter.Write.asioutputmode_8552,"mode" },
				{ Parameter.Write.asioutputdescramblingmode_8553,"dscrmode" },
				{ Parameter.Write.asiinsertnullpacket_8554, "insertnul" },
			};
		}
		else
		{
			command = new Dictionary<int, string>
			{
				{ Parameter.Write.asi2outputratecontrolmode_8555,"ratemode" },
				{ Parameter.Write.asi2outputrate_8556,"rate" },
				{ Parameter.Write.asi2outputmode_8557,"mode" },
				{ Parameter.Write.asi2outputdescramblingmode_8558,"dscrmode" },
				{ Parameter.Write.asi2insertnullpacket_8559, "insertnul" },
			};
		}

		if (command.TryGetValue(iTID, out urlParameter))
		{
			protocol.SetParameter(Parameter.asisettings_write_748, String.Format("{0}{1}={2}", url, urlParameter, sWrite));
			protocol.CheckTrigger((int)ProtocolTriggers.WhenAsiParametersWritten_41);
		}
	}
}

