using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iPos = 5500;
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xml_videosetupserviceconfig_237));  // XML Response of Video Setup
			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);
				if (xDoc.Descendants("video").Any())
				{
					string sPvformat = xDoc.Descendants("video").Descendants("pvformat").Any() ? xDoc.Descendants("video").Descendants("pvformat").FirstOrDefault().Value : "-1";
					string sSdformat = xDoc.Descendants("video").Descendants("sdformat").Any() ? xDoc.Descendants("video").Descendants("sdformat").FirstOrDefault().Value : "-1";
					int[] oiparamsIds = new int[2];
					AssignIdsToSet(oiparamsIds, iPos);
					object[] allValues = new object[] { sPvformat, sSdformat };
					protocol.SetParameters(oiparamsIds, allValues);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void AssignIdsToSet(int[] paramsIds, int iPos)
	{
		for (int i = 0; i < paramsIds.Length; i++)
		{
			paramsIds[i] = iPos + i;
		}
	}
}

