					protocol.SetParameters(new[] { Parameter.urlaudiowrite_18 }, new object[] { sUrl });
					protocol.CheckTrigger((int)ProtocolTriggers.WhenAudio4ParametersWritten_54);
					break;
				case 11455:
					sUrl += "audio?device=4&left=" + sWrite;
					protocol.SetParameters(new[] { Parameter.urlaudiowrite_18 }, new object[] { sUrl });
					protocol.CheckTrigger((int)ProtocolTriggers.WhenAudio4ParametersWritten_54);
					break;
				case 11456:
					sUrl += "audio?device=4&mode=" + sWrite;
					protocol.SetParameters(new[] { Parameter.urlaudiowrite_18 }, new object[] { sUrl });
					protocol.CheckTrigger((int)ProtocolTriggers.WhenAudio4ParametersWritten_54);
					break;
				case 11457:
					sUrl += "audio?device=4&pmtsrc=" + sWrite;
					protocol.SetParameters(new[] { Parameter.urlaudiowrite_18 }, new object[] { sUrl });
					protocol.CheckTrigger((int)ProtocolTriggers.WhenAudio4ParametersWritten_54);
					break;
				case 11458:
					sUrl += "audio?device=4&right=" + sWrite;
					protocol.SetParameters(new[] { Parameter.urlaudiowrite_18 }, new object[] { sUrl });
					protocol.CheckTrigger((int)ProtocolTriggers.WhenAudio4ParametersWritten_54);
					break;

				default: // case 15553
					string response = Convert.ToString(protocol.GetParameter(Parameter.xml_serviceconfigure_317));
					if (!string.IsNullOrEmpty(response))
					{
						xdoccha = XDocument.Parse(response);
						if (xdoccha.Descendants("pe").Any())
						{
							var valuescha = from s in xdoccha.Descendants("pe")
											select new
											{
												cha = s.Descendants("record").Descendants(),
											};
							var lastValueCha = valuescha.Last();

							if (lastValueCha.cha.ElementAt(1) != null)
							{
								lastValueCha.cha.First(n => n.Name == "chn").Value = sWrite;
							}

							protocol.SetParameters(new[] { Parameter.pesettings_write_817 }, new object[] { Convert.ToString(xdoccha) });
							protocol.CheckTrigger((int)ProtocolTriggers.WhenChannelNumberSelected_55);
						}
					}
					else
					{
						protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
					}

					break;
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iPos = 5503;
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlaspectratioserviceconfig_238));  // XML Response for AR Settings
			if (string.IsNullOrEmpty(sXML))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
				return;
			}

			sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

			XDocument xDoc = XDocument.Parse(sXML);

			if (xDoc.Descendants("ar").Any())
			{
				ProcessAr(protocol, iPos, xDoc);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void ProcessAr(SLProtocol protocol, int iPos, XDocument xDoc)
	{
		string sDar = xDoc.Descendants("ar").Descendants("sdar").Any() ? xDoc.Descendants("ar").Descendants("sdar").FirstOrDefault().Value : "-1";
		string sArselect = xDoc.Descendants("ar").Descendants("arselect").Any() ? xDoc.Descendants("ar").Descendants("arselect").FirstOrDefault().Value : "-1";
		string sWssmode = xDoc.Descendants("ar").Descendants("wssmode").Any() ? xDoc.Descendants("ar").Descendants("wssmode").FirstOrDefault().Value : "-1";
		int[] paramsIds = new int[3];
		for (int i = 0; i < paramsIds.Length; i++)
		{
			paramsIds[i] = iPos + i;
		}

		object[] allValues = new object[] { sDar, sArselect, sWssmode };
		protocol.SetParameters(paramsIds, allValues);
	}
}

