public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iPos = 5506;
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.xmlsubtserviceconfig_239));  // XML Response for SUBT settings
			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);
				if (xDoc.Descendants("subt").Any())
				{
					string sOpmode = xDoc.Descendants("subt").Descendants("opmode").Any() ? xDoc.Descendants("subt").Descendants("opmode").FirstOrDefault().Value : "-1";
					string sLanglist = xDoc.Descendants("subt").Descendants("langlist").Any() ? xDoc.Descendants("subt").Descendants("langlist").FirstOrDefault().Value : "-1";
					string sLangentry = xDoc.Descendants("subt").Descendants("langentry").Any() ? xDoc.Descendants("subt").Descendants("langentry").FirstOrDefault().Value : "-1";
					string sPmtorder = xDoc.Descendants("subt").Descendants("pmtorder").Any() ? xDoc.Descendants("subt").Descendants("pmtorder").FirstOrDefault().Value : "-1";
					string sImitext = xDoc.Descendants("subt").Descendants("imitext").Any() ? xDoc.Descendants("subt").Descendants("imitext").FirstOrDefault().Value : "-1";
					string sForegndcol = xDoc.Descendants("subt").Descendants("foregndcol").Any() ? xDoc.Descendants("subt").Descendants("foregndcol").FirstOrDefault().Value : "-1";
					string sBackgndcol = xDoc.Descendants("subt").Descendants("backgndcol").Any() ? xDoc.Descendants("subt").Descendants("backgndcol").FirstOrDefault().Value : "-1";

					int[] paramsIds = new int[7];
					for (int i = 0; i < paramsIds.Length; i++)
					{
						paramsIds[i] = iPos + i;
					}

					// one missed langmenu
					object[] allValues = new object[] { sOpmode, sImitext, sForegndcol, sBackgndcol, sLanglist, sLangentry, sPmtorder };
					protocol.SetParameters(paramsIds, allValues);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

