using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse Host Name.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			object[] paramValues = (object[])protocol.GetParameters(new uint[] { Parameter.identityresponse_314, Parameter.identitystatuscode_315 });
			string response = Convert.ToString(paramValues[0]);
			string code = Convert.ToString(paramValues[1]);
			if (String.IsNullOrEmpty(response) || !code.Contains("200"))
			{
				return;
			}

			XDocument xDoc = XDocument.Parse(response);

			string host = xDoc.Root.Element("hostname").Value;
			string trackingid = xDoc.Root.Element("trackingid").Value;

			protocol.SetParameters(new[] { Parameter.hostname_18501, Parameter.prodtrackingid_18509 }, String.IsNullOrEmpty(host) ? new object[] { "-1", "-1" } : new object[] { host, trackingid });
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;
using XP2Api;

namespace XP2Api
{
	public static class XmlLinqExtensions
	{
		public static string GetAttributeValue(this XElement element, string attributeName, int exceptionValue)
		{
			var value = Convert.ToString(exceptionValue);

			if (element == null)
			{
				return value;
			}

			var attribute = element.Attribute(attributeName);

			return attribute != null ? attribute.Value : value;
		}

		public static string GetElementValue(this XElement element, string elementName)
		{
			var value = string.Empty;

			if (element == null)
			{
				return value;
			}

			var child = element.Element(elementName);
			value = child.GetElementValue();
			return value;
		}

		private static string GetElementValue(this XElement element)
		{
			return element != null ? element.Value : string.Empty;
		}
	}
}

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		int iTriggerParameter = protocol.GetTriggerParameter();
		int iPos = 0;
		switch (iTriggerParameter)
		{
			case 300:
				iPos = 11100;
				break;
			case 303:
				iPos = 11200;
				break;
			case 304:
				iPos = 11300;
				break;
			default: // case 305:
				iPos = 11400;
				break;
		}

		try
		{
			string sXML = String.Empty;

			sXML = Convert.ToString(protocol.GetParameter(iTriggerParameter));  // XML Response for Audio 1 Settings

			if (!string.IsNullOrEmpty(sXML))
			{
				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);

				if (xDoc.Descendants("audio").Any())
				{
					CheckXml(protocol, xDoc, iPos);
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void CheckXml(SLProtocol protocol, XDocument xDoc, int iPos)
	{
		string sDevice = xDoc.Descendants("audio").Descendants("device").Any() ? xDoc.Descendants("audio").Descendants("device").FirstOrDefault().Value : "-1";
		string sAc3comp = xDoc.Descendants("audio").Descendants("ac3comp").Any() ? xDoc.Descendants("audio").Descendants("ac3comp").FirstOrDefault().Value : "-1";
		string sDdpmode = xDoc.Descendants("audio").Descendants("ddpmode").Any() ? xDoc.Descendants("audio").Descendants("ddpmode").FirstOrDefault().Value : "-1";
		string sLang = xDoc.Descendants("audio").Descendants("lang").Any() ? xDoc.Descendants("audio").Descendants("lang").FirstOrDefault().Value : "-1";
		string sLangmenu = xDoc.Descendants("audio").Descendants("langmenu").Any() ? xDoc.Descendants("audio").Descendants("langmenu").FirstOrDefault().Value : "-1";
		string sLeft = xDoc.Descendants("audio").Descendants("left").Any() ? xDoc.Descendants("audio").Descendants("left").FirstOrDefault().Value : "-1";
		string sMode = xDoc.Descendants("audio").Descendants("mode").Any() ? xDoc.Descendants("audio").Descendants("mode").FirstOrDefault().Value : "-1";
		string sPmtsrc = xDoc.Descendants("audio").Descendants("pmtsrc").Any() ? xDoc.Descendants("audio").Descendants("pmtsrc").FirstOrDefault().Value : "-1";
		string sRight = xDoc.Descendants("audio").Descendants("right").Any() ? xDoc.Descendants("audio").Descendants("right").FirstOrDefault().Value : "-1";

		int[] paramsIds = new int[9];
		for (int i = 0; i < paramsIds.Length; i++)
		{
			paramsIds[i] = iPos + i;
		}

		object[] allValues = new object[] { sDevice, sAc3comp, sDdpmode, sLang, sLangmenu, sLeft, sMode, sPmtsrc, sRight };
		protocol.SetParameters(paramsIds, allValues);
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse General Parameters.
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			object[] paramValues = (object[])protocol.GetParameters(new uint[] { Parameter.aboutdataresponse_228, Parameter.aboutdatacode_229 });
			string response = Convert.ToString(paramValues[0]);
			string code = Convert.ToString(paramValues[1]);

			if (String.IsNullOrEmpty(response) || !code.Contains("200"))
			{
				return;
			}

			response = response.Replace("<br>", String.Empty);
			response = Regex.Replace(response, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");
			ProcessResponse(protocol, response);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void ProcessResponse(SLProtocol protocol, string response)
	{
		XDocument xDoc = XDocument.Parse(response);

		string modelNum = xDoc.Descendants("device").Descendants("about").Descendants("modelnum").Any() ? xDoc.Descendants("modelnum").First().Value : "-1";
		string modelName = xDoc.Descendants("device").Descendants("about").Descendants("modelname").Any() ? xDoc.Descendants("modelname").First().Value : "-1";
		string catalogNum = xDoc.Descendants("device").Descendants("about").Descendants("catalognum").Any() ? xDoc.Descendants("catalognum").First().Value : "-1";
		string serialnum = xDoc.Descendants("device").Descendants("about").Descendants("serialnum").Any() ? xDoc.Descendants("serialnum").First().Value : "-1";
		string proddate = xDoc.Descendants("device").Descendants("about").Descendants("proddate").Any() ? xDoc.Descendants("proddate").First().Value : "-1";
		string pwrupdate = xDoc.Descendants("device").Descendants("about").Descendants("pwrupdate").Any() ? xDoc.Descendants("pwrupdate").First().Value : "-1";
		string lifepower = xDoc.Descendants("device").Descendants("about").Descendants("pwrupdate").Any() ? xDoc.Descendants("pwrupdate").First().Value : "-1";
		string boardid = xDoc.Descendants("device").Descendants("about").Descendants("boardid").Any() ? xDoc.Descendants("boardid").First().Value : "-1";
		string fpgatype = xDoc.Descendants("device").Descendants("about").Descendants("fpgatype").Any() ? xDoc.Descendants("fpgatype").First().Value : "-1";
		string about1 = xDoc.Descendants("device").Descendants("about").Descendants("about1").Any() ? xDoc.Descendants("about1").First().Value : "-1";

		int[] ids = new[]
		{
			Parameter.modelnumber_16605,
			Parameter.devicemodelnumber_18503,
			Parameter.modelname_16606,
			Parameter.devicemodelname_18504,
			Parameter.catalog_16607,
			Parameter.prodcatalogno_18510,
			Parameter.serialnumber_16608,
			Parameter.productiondateandtimestring_16609,
			Parameter.lastpowerondatestring_16610,
			Parameter.lifepower_16611,
			Parameter.deviceboardid_18505,
			Parameter.deviceboardfpgatype_18506,
			Parameter.currentappversion_18508,
		};

		object[] values = new object[]
		{
			modelNum,
			modelNum,
			modelName,
			modelName,
			catalogNum,
			catalogNum,
			serialnum,
			proddate,
			pwrupdate,
			lifepower,
			boardid,
			fpgatype,
			about1,
		};

		protocol.SetParameters(ids, values);
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Text;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// ParseDateTime.
	/// </summary>
	/// <param name="protocol">Link with Skyline DataMiner.</param>
	public static void Run(SLProtocol protocol)
	{
		int iTrigger = protocol.GetTriggerParameter();
		int	iSetParameterID=0;
		string sDateValue = string.Empty;
		switch (iTrigger)
		{
			case Parameter.productiondateandtimestring_16609:
				iSetParameterID = Parameter.productiondate_16612;
				sDateValue = Convert.ToString( protocol.GetParameter(Parameter.productiondateandtimestring_16609));
				break;

			case Parameter.lastpowerondatestring_16610:
				iSetParameterID = Parameter.lastpowerondate_16613;
				sDateValue = Convert.ToString(protocol.GetParameter(Parameter.lastpowerondatestring_16610));
				break;

			default:
				// Do nothing
				break;
		}

		string sDate = Convert.ToString(sDateValue);
		try
		{
			DateTime dt = DateTime.Parse(sDate);
			Double dOADate = dt.ToOADate();
			protocol.SetParameter(iSetParameterID, dOADate);
		}
		catch (Exception ex)
		{
			protocol.SetParameter(iSetParameterID, -1);
			protocol.Log("QA" + protocol.QActionID + "|Run|Error Trying to parse date with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

