namespace XP2Api
{
	public static class XmlLinqExtensions
	{
		public static string GetAttributeValue(this XElement element, string attributeName, int exceptionValue)
		{
			var value = Convert.ToString(exceptionValue);

			if (element == null)
			{
				return value;
			}

			var attribute = element.Attribute(attributeName);

			return attribute != null ? attribute.Value : value;
		}

		public static string GetElementValue(this XElement element, string elementName)
		{
			var value = string.Empty;

			if (element == null)
			{
				return value;
			}

			var child = element.Element(elementName);
			value = child.GetElementValue();
			return value;
		}

		private static string GetElementValue(this XElement element)
		{
			return element != null ? element.Value : string.Empty;
		}
	}
}

/// <summary>
/// DataMiner QAction Class: QActionName.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string sXML = string.Empty;

			sXML = Convert.ToString(protocol.GetParameter(Parameter.abrinputstatusresponse_460));  // XML response for ABR input status

			if (!string.IsNullOrEmpty(sXML))
			{
				var abrInputStatusQaRowList = new List<QActionTableRow>();

				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);

				if (xDoc.Descendants("input").Any())
				{
					var abrInputStatusXML = xDoc.Descendants("abr");

					foreach (var abr in abrInputStatusXML)
					{
						var abrQaRow = new AbrinputstatustableQActionRow
						{
							Abrinputstatusstream_24001 = abr.GetElementValue("stream"),
							Abrinputstatus_24002 = abr.GetElementValue("act"),
							Abrinputstatusasisignallock_24003 = abr.GetElementValue("lock"),
							Abrinputstatusabrethlinkstatus_24004 = abr.GetElementValue("iplinkstat"),
							Abrinputstatusabrsignalencapsulationlockstatus_24005 = abr.GetElementValue("ipsignal"),
							Abrinputstatusinputrate_24006 = abr.GetElementValue("inputrate"),
							Abrinputstatusnetname_24007 = abr.GetElementValue("netname"),
							Abrinputstatusnetid_24008 = abr.GetElementValue("netid"),
							Abrinputstatustransportid_24031 = abr.GetElementValue("transid"),
							Abrinputstatusscrambmode_24009 = abr.GetElementValue("scrambmode"),
							Abrinputstatuslasttunereason_24010 = abr.GetElementValue("lsttunerea"),
							Abrinputstatustunereason_24011 = abr.GetElementValue("tunereason"),
							Abrinputstatusfrequencytunningmode_24012 = abr.GetElementValue("freqmode"),
							Abrinputstatusservicelistmode_24013 = abr.GetElementValue("svclstmode"),
							Abrinputstatusbatreceptionstatus_24014 = abr.GetElementValue("baten"),
							Abrinputstatusnitreceptionstatus_24015 = abr.GetElementValue("niten"),
							Abrinputstatussdtreceptionstatus_24016 = abr.GetElementValue("sdten"),
							Abrinputstatuspatreceptionstatus_24017 = abr.GetElementValue("paten"),
							Abrinputstatusrate_24018 = abr.GetElementValue("rate"),
							Abrinputstatusdownloadbitrate_24019 = abr.GetElementValue("dlrate"),
							Abrinputstatusbufferlevelcurrentqueue1_24020 = abr.GetElementValue("buflvlcurq1"),
							Abrinputstatusbufferlevelcurrentqueue2_24021 = abr.GetElementValue("buflvlcurq2"),
							Abrinputstatusmanifestduration_24022 = abr.GetElementValue("manidur"),
							Abrinputstatusmanifestpassingerrors_24023 = abr.GetElementValue("manierr"),
							Abrinputstatusmanifestdownloadcount_24024 = abr.GetElementValue("dlmanicnt"),
							Abrinputstatusmanifestdownloaderrors_24025 = abr.GetElementValue("dlmanierr"),
							Abrinputstatusdownloadfragmentcount_24026 = abr.GetElementValue("dlfragcnt"),
							Abrinputstatusdownloadfragmenterrors_24027 = abr.GetElementValue("dlfragerr"),
							Abrinputstatusfragmentpassingerrors_24028 = abr.GetElementValue("fragerr"),
							Abrinputstatusdownloadstoptimefromend_24029 = abr.GetElementValue("dlcurminlat"),
							Abrinputstatusdownloadstoptimefrombeginning_24030 = abr.GetElementValue("dlcurtrglat"),
						};

						abrInputStatusQaRowList.Add(abrQaRow);
					}

					if (abrInputStatusQaRowList.Count > 0)
					{
						protocol.abrinputstatustable.FillArray(abrInputStatusQaRowList);
					}
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;
using XP2Api;

namespace XP2Api
{
	using System;
	using System.Xml.Linq;

	public static class XmlLinqExtensions
	{
		public static string GetAttributeValue(this XElement element, string attributeName, int exceptionValue)
		{
			var value = Convert.ToString(exceptionValue);

			if (element == null)
			{
				return value;
			}

			var attribute = element.Attribute(attributeName);

			return attribute != null ? attribute.Value : value;
		}

		public static string GetElementValue(this XElement element, string elementName)
		{
			var value = String.Empty;

			if (element == null)
			{
				return value;
			}

			var child = element.Element(elementName);
			value = child.GetElementValue();
			return value;
		}

		private static string GetElementValue(this XElement element)
		{
			return element != null ? element.Value : String.Empty;
		}
	}
}

/// <summary>
///     DataMiner QAction Class: QActionName.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string sXML = Convert.ToString(protocol.GetParameter(Parameter.zixiinputstatusresponse_462));
			string response = Convert.ToString(protocol.GetParameter(Parameter.zixiinputconfigurationresponse_472));

			if (String.IsNullOrEmpty(sXML) && String.IsNullOrEmpty(response))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
				return;
			}

			Dictionary<string, ZixiinputstatustableQActionRow> actionRows = new Dictionary<string, ZixiinputstatustableQActionRow>();

			sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");
			response = Regex.Replace(response, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

			XDocument xDoc = XDocument.Parse(sXML);
			XDocument xDoc2 = XDocument.Parse(response);

			if (xDoc.Descendants("input").Any())
			{
				var zixiInputStatusXML = xDoc.Descendants("zixi");

				foreach (var zixi in zixiInputStatusXML)
				{
					string rowKey = zixi.GetElementValue("stream");
					var zixiQaRow = new ZixiinputstatustableQActionRow
					{
						Zixiinputstatusstream_25001 = rowKey,
						Zixiinputstatus_25002 = zixi.GetElementValue("act"),
						Zixiinputstatuszixisignallock_25003 = zixi.GetElementValue("lock"),
						Zixiinputstatuszixiethlinkstatus_25004 = zixi.GetElementValue("iplinkstat"),
						Zixiinputstatuszixisignalencapsulationlockstatus_25005 = zixi.GetElementValue("ipsignal"),
						Zixiinputstatusinputrate_25006 = zixi.GetElementValue("inputrate"),
						Zixiinputstatusnetworkname_25007 = zixi.GetElementValue("netname"),
						Zixiinputstatusnetworkid_25008 = zixi.GetElementValue("netid"),
						Zixiinputstatustransportid_25026 = zixi.GetElementValue("transid"),
						Zixiinputstatusscrambmode_25009 = zixi.GetElementValue("scrambmode"),
						Zixiinputstatuslasttunereason_25010 = zixi.GetElementValue("lsttunerea"),
						Zixiinputstatustunereason_25011 = zixi.GetElementValue("tunereason"),
						Zixiinputstatusfrequencytunningmode_25012 = zixi.GetElementValue("freqmode"),
						Zixiinputstatusservicelistmode_25013 = zixi.GetElementValue("svclstmode"),
						Zixiinputstatusbatreceptionstatus_25014 = zixi.GetElementValue("baten"),
						Zixiinputstatusnitreceptionstatus_25015 = zixi.GetElementValue("niten"),
						Zixiinputstatussdtreceptionstatus_25016 = zixi.GetElementValue("sdten"),
						Zixiinputstatuspatreceptionstatus_25017 = zixi.GetElementValue("paten"),
						Zixiinputstatusacquisitionstate_25018 = zixi.GetElementValue("acqstatus"),
						Zixiinputstatusinputid_25019 = zixi.GetElementValue("inputid"),
						Zixiinputstatusreconnections_25020 = zixi.GetElementValue("reconn"),
						Zixiinputstatuspacketsrecoveredbyarq_25021 = zixi.GetElementValue("arqrec"),
						Zixiinputstatuspacketsrecoveredbyfec_25022 = zixi.GetElementValue("fecrec"),
						Zixiinputstatuspacketsnotrecovered_25023 = zixi.GetElementValue("notrec"),
						Zixiinputstatusrowstatus_25024 = zixi.GetElementValue("rowstatus"),
						Zixiinputstatussrccurrentlyconnected_25025 = zixi.GetElementValue("cursrc"),
						Zixiinputstatustype_25027 = zixi.GetElementValue("type"),
						Zixiinputstatussource_25028 = zixi.GetElementValue("source"),
						Zixiinputstatusport_25029 = zixi.GetElementValue("port"),
					};
					actionRows.Add(rowKey, zixiQaRow);
				}
			}

			if (xDoc2.Descendants("input").Any())
			{
				var zixiInputStatusXML = xDoc2.Descendants("zixi");

				foreach (var zixi in zixiInputStatusXML)
				{
					string rowKey = zixi.GetElementValue("stream");
					actionRows[rowKey].Zixiinputstatustype_25027 = zixi.GetElementValue("type");
					actionRows[rowKey].Zixiinputstatussource_25028 = zixi.GetElementValue("source");
					actionRows[rowKey].Zixiinputstatusport_25029 = zixi.GetElementValue("port");
				}
			}

			var zixiInputStatusQaRowList = actionRows.Values.Select(row => (QActionTableRow)row).ToList();

			if (zixiInputStatusQaRowList.Count > 0)
			{
				protocol.zixiinputstatustable.FillArray(zixiInputStatusQaRowList);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

