/// <summary>
/// DataMiner QAction Class: QActionName.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string sXML = string.Empty;

			sXML = Convert.ToString(protocol.GetParameter(Parameter.audiostc302decodestatusresponse_464));  // XML response for ABR input status

			if (!string.IsNullOrEmpty(sXML))
			{
				var audioSTC302DecodeStatusQaRowList = new List<QActionTableRow>();

				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);

				if (xDoc.Descendants("decode").Any())
				{
					var stc302InputStatusXML = xDoc.Descendants("aud_st302");

					foreach (var device in stc302InputStatusXML)
					{
						var stc302QaRow = new Audiostc302tableQActionRow
						{
							Audiostc302device_26001 = device.GetElementValue("device"),
							Audiostc302streamformat_26002 = device.GetElementValue("streamformat"),
							Audiostc302numberofst302stream_26003 = device.GetElementValue("numstreams"),
							Audiostc302packetsize_26004 = device.GetElementValue("packetsize"),
							Audiostc302dolbyconfiguration_26005 = device.GetElementValue("dolbyconfig"),
						};

						audioSTC302DecodeStatusQaRowList.Add(stc302QaRow);
					}

					if (audioSTC302DecodeStatusQaRowList.Count > 0)
					{
						protocol.audiostc302table.FillArray(audioSTC302DecodeStatusQaRowList);
					}
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

