public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string sXML = string.Empty;

			sXML = Convert.ToString(protocol.GetParameter(Parameter.abrinputconfigurationresponse_470));  // XML response for ABR input config

			if (!string.IsNullOrEmpty(sXML))
			{
				var abrInputCOnfigQaRowList = new List<QActionTableRow>();

				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);

				if (xDoc.Descendants("input").Any())
				{
					var abrInputConfigXML = xDoc.Descendants("abr");

					foreach (var input in abrInputConfigXML)
					{
						var abrInputConfigQaRow = new AbrinputconfigurationtableQActionRow
						{
							Abrinputconfigurationstream_27001 = input.GetElementValue("stream"),
							Abrinputconfigurationinputstatus_27002 = input.GetElementValue("act"),
							Abrinputconfigurationnetworkidentifier_27003 = input.GetElementValue("netid"),
							Abrinputconfigurationacqmode_27004 = input.GetElementValue("acqmode"),
							Abrinputconfigurationcamode_27005 = input.GetElementValue("camode"),
