							Abrinputconfigurationfrequencymode_27007 = input.GetElementValue("freqmode"),
							Abrinputconfigurationservicelistmode_27008 = input.GetElementValue("svclstmode"),
							Abrinputconfigurationbatreceptionstatus_27009 = input.GetElementValue("baten"),
							Abrinputconfigurationnitreceptionstatus_27010 = input.GetElementValue("niten"),
							Abrinputconfigurationsdtreceptionstatus_27011 = input.GetElementValue("sdten"),
							Abrinputconfigurationpatreceptionstatus_27012 = input.GetElementValue("paten"),
							Abrinputconfigurationsettingname_27013 = input.GetElementValue("name"),
							Abrinputconfigurationurl_27014 = input.GetElementValue("url"),
							Abrinputconfigurationlatency_27015 = input.GetElementValue("latency"),
							Abrinputconfigurationphysicalport_27016 = input.GetElementValue("phyport"),
							Abrinputconfigurationcommandrow_27017 = input.GetElementValue("cmdrow"),
							Abrinputconfigurationminimumlatency_27018 = input.GetElementValue("minlatency"),
