using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;
using XP2Api;

namespace XP2Api
{
	public static class XmlLinqExtensions
	{
		public static string GetAttributeValue(this XElement element, string attributeName, int exceptionValue)
		{
			var value = Convert.ToString(exceptionValue);

			if (element == null)
			{
				return value;
			}

			var attribute = element.Attribute(attributeName);

			return attribute != null ? attribute.Value : value;
		}

		public static string GetElementValue(this XElement element, string elementName)
		{
			var value = string.Empty;

			if (element == null)
			{
				return value;
			}

			var child = element.Element(elementName);
			value = child.GetElementValue();
			return value;
		}

		private static string GetElementValue(this XElement element)
		{
			return element != null ? element.Value : string.Empty;
		}
	}
}

/// <summary>
/// DataMiner QAction Class: QActionName.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string sXML = string.Empty;

			sXML = Convert.ToString(protocol.GetParameter(Parameter.moipoutputstreamconfigurationresponse_474));  // XML response for moip output input config

			if (!string.IsNullOrEmpty(sXML))
			{
				var moipOutputConfigQaRowList = new List<QActionTableRow>();

				sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument xDoc = XDocument.Parse(sXML);

				if (xDoc.Descendants("moip1").Any())
				{
					var moipOutputConfigXML = xDoc.Descendants("ipo");

					foreach (var output in moipOutputConfigXML)
					{
						var moipOutputConfigQaRow = new MoipoutputstreamconfigurationQActionRow
						{
							Moipoutputstreamconfigurationipoid_29001 = output.GetElementValue("ipoid"),
							Moipoutputstreamconfigurationiporate_29002 = output.GetElementValue("iporate"),
							Moipoutputstreamconfigurationencaps_29003 = output.GetElementValue("encaps"),
							Moipoutputstreamconfigurationdestip_29004 = output.GetElementValue("dest_ip"),
							Moipoutputstreamconfigurationdestport_29005 = output.GetElementValue("dest_port"),
							Moipoutputstreamconfigurationtoc_29006 = output.GetElementValue("toc"),
							Moipoutputstreamconfigurationttl_29007 = output.GetElementValue("ttl"),
							Moipoutputstreamconfigurationsrcport_29008 = output.GetElementValue("src_port"),
							Moipoutputstreamconfigurationanntype_29009 = output.GetElementValue("ann_type"),
							Moipoutputstreamconfigurationannsrc_29010 = output.GetElementValue("ann_src"),
							Moipoutputstreamconfigurationanntitle_29011 = output.GetElementValue("ann_title"),
							Moipoutputstreamconfigurationfecmode_29012 = output.GetElementValue("fec_mode"),
							Moipoutputstreamconfigurationfeccol_29013 = output.GetElementValue("fec_col"),
							Moipoutputstreamconfigurationfecrow_29014 = output.GetElementValue("fec_row"),
							Moipoutputstreamconfigurationfeccolport_29015 = output.GetElementValue("fec_colport"),
							Moipoutputstreamconfigurationfecrowport_29016 = output.GetElementValue("fec_rowport"),
						};

						moipOutputConfigQaRowList.Add(moipOutputConfigQaRow);
					}

					if (moipOutputConfigQaRowList.Count > 0)
					{
						protocol.moipoutputstreamconfiguration.FillArray(moipOutputConfigQaRowList);
					}
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using Newtonsoft.Json;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Parse BER.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string BERJson = Convert.ToString(protocol.GetParameter(Parameter.berresponse_64));
			BER jsonData = JsonConvert.DeserializeObject<BER>(BERJson);
			string BERString = jsonData.HDR.TABLE.ITEM.VALUE;

			double adjustedValueToE6 = GetBERValue(protocol, BERString);
			protocol.SetParameter(Parameter.berrf1_31485, adjustedValueToE6);
		}
		catch (Exception ex)
		{
			protocol.Log($"QA{protocol.QActionID}|{protocol.GetTriggerParameter()}|Run|Exception thrown:{Environment.NewLine}{ex}", LogType.Error, LogLevel.NoLogging);
		}
	}

	private static double GetBERValue(SLProtocol protocol, string BERString)
	{
		if (BERString.Contains("--") || BERString == "N/A")
		{
			return -3;
		}

		if (BERString.Contains("<") || BERString.Contains(">"))
		{
			if (BERString == "<0.01e-6")
			{
				return -1;
			}

			if (BERString == ">999999.99e-6")
			{
				return -2;
			}

			if (BERString == "<1.0e-8")
			{
				return -1;
			}

			return -4;
		}

		if (!Double.TryParse(BERString, out double parsedValue))
		{
			protocol.Log($"QA{protocol.QActionID}|Unable to parse string BER to double", LogType.Error, LogLevel.NoLogging);
			return -3;
		}

		return parsedValue * Math.Pow(10, -6 + GetExponent(parsedValue));
	}

	private static int GetExponent(double value)
	{
		if (value == 0)
		{
			return 0;
		}

		return (int)Math.Floor(Math.Log10(Math.Abs(value)));
	}

	public class BER
	{
		public HDR HDR { get; set; }
	}

	public class HDR
	{
		public TABLE TABLE { get; set; }
	}

	public class TABLE
	{
		public ITEM ITEM { get; set; }
	}

	public class ITEM
	{
		public string VALUE { get; set; }
	}
}

