/// <summary>
///     DataMiner QAction Class: Setting ABR Input Configuration Table Via POST.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			var stream = protocol.RowKey();
			var triggerParam = protocol.GetTriggerParameter();

			var parameterName = String.Empty;

			if (!BuildABRDictPairs().TryGetValue(triggerParam, out parameterName))
			{
				return;
			}

			var value = Convert.ToString(protocol.GetParameter(triggerParam));

			var deviceIPs = Convert.ToString(protocol.GetParameter(8)).Split(';');

			string url = "https://" + deviceIPs[0] + "/ws/v2/service_cfg/input/abr?stream=" + stream + "&" + parameterName + "=" + value;

			protocol.SetParameter(Parameter.inputsconfigurationurlpost_900, url);

			protocol.CheckTrigger((int)ProtocolTriggers.RunAbrConfigPostAction_227);
		}
		catch (Exception e)
		{
			protocol.Log("QA" + protocol.QActionID + "|Setting ABR Input Via Post|Failed because " + e, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static Dictionary<int, string> BuildABRDictPairs()
	{
		var abrDictPairs = new Dictionary<int, string>
		{
			{ Parameter.Write.abrinputconfigurationinputstatus_27102, "act" },
			{ Parameter.Write.abrinputconfigurationnetworkidentifier_27103, "netid" },
			{ Parameter.Write.abrinputconfigurationacqmode_27104, "acqmode" },
			{ Parameter.Write.abrinputconfigurationcamode_27105, "camode" },
			{ Parameter.Write.abrinputconfigurationinputselection_27106, "inputsel" },
			{ Parameter.Write.abrinputconfigurationfrequencymode_27107, "freqmode" },
			{ Parameter.Write.abrinputconfigurationservicelistmode_27108, "svclstmode" },
			{ Parameter.Write.abrinputconfigurationbatreceptionstatus_27109, "baten" },
			{ Parameter.Write.abrinputconfigurationnitreceptionstatus_27110, "niten" },
			{ Parameter.Write.abrinputconfigurationsdtreceptionstatus_27111, "sdten" },
			{ Parameter.Write.abrinputconfigurationpatreceptionstatus_27112, "paten" },
			{ Parameter.Write.abrinputconfigurationsettingname_27113, "name" },
			{ Parameter.Write.abrinputconfigurationurl_27114, "url" },
			{ Parameter.Write.abrinputconfigurationlatency_27115, "latency" },
			{ Parameter.Write.abrinputconfigurationphysicalport_27116, "phyport" },
			{ Parameter.Write.abrinputconfigurationphysicalport_27116, "cmdrow" },
