/// <summary>
/// DataMiner QAction Class: Add Item To Source Filter.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		// int iTrigger = protocol.GetTriggerParameter();
		object[] aoParameters = (object[])protocol.GetParameters(new uint[] { Parameter.sourcefilteringlist_30005, Parameter.newitemfiltersourcelist_30008 });
		string sSourceFilterList = Convert.ToString(aoParameters[0]);
		string sNewItem = Convert.ToString(aoParameters[1]);
		protocol.Log("QA" + protocol.QActionID + "|Run|sSourceFilterList: " + sSourceFilterList, LogType.DebugInfo, LogLevel.NoLogging);
		protocol.Log("QA" + protocol.QActionID + "|Run|sSourceFilterList + sNewItem: " + sSourceFilterList + ";" + sNewItem, LogType.DebugInfo, LogLevel.NoLogging);

		protocol.SetParameter(Parameter.sourcefilteringlist_30005, sSourceFilterList + ";" + sNewItem);
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse Source Filter List.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		string sXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.sourcefilterlistresponseget_30112));

		XDocument doc = new XDocument();

		List<QActionTableRow> listRows = new List<QActionTableRow>();

		if (!string.IsNullOrEmpty(sXmlResponse))
		{
			try
			{
				doc = XDocument.Parse(sXmlResponse);
				IEnumerable<XElement> records = doc.Descendants("record");
				foreach (XElement item in records)
				{
					var sourceFilterRow = new SourcefilterlistQActionRow
					{
						Sourcefilterlistkey_idx_30501 = Convert.ToString(item.Descendants("idx").FirstOrDefault().Value),
						Sourcefilterlistip_30502 = Convert.ToString(item.Descendants("ip").FirstOrDefault().Value),
						Sourcefilterlistoperation_30503 = Convert.ToString(item.Descendants("oper").FirstOrDefault().Value),
					};

					listRows.Add(sourceFilterRow);
				}

				protocol.sourcefilterlist.FillArray(listRows);
			}
			catch (Exception e)
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + e, LogType.Error, LogLevel.NoLogging);
			}
		}
	}
}

