using System;
using System.Linq;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse Audio Decode Configuration SDI.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = String.Empty;
			string sSD1 = String.Empty;
			string sSD2 = String.Empty;

			sXML = Convert.ToString(protocol.GetParameter(Parameter.audiodecodeconfigurationsdiresponseget_30116));  // XML Response for Audio 1 Settings

			if (string.IsNullOrEmpty(sXML))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
				return;
			}

			XDocument xDoc = XDocument.Parse(sXML);

			if (xDoc.Descendants("audio").Any())
			{
				string sDevice = xDoc.Descendants("audio").Descendants("device").Any() ? xDoc.Descendants("audio").Descendants("device").FirstOrDefault().Value : "-1";

				if (sDevice.Equals("1"))
				{
					sSD1 = xDoc.Descendants("audio").Descendants("sdi1output").Any() ? xDoc.Descendants("audio").Descendants("sdi1output").FirstOrDefault().Value : "-1";
					sSD2 = xDoc.Descendants("audio").Descendants("sdi2output").Any() ? xDoc.Descendants("audio").Descendants("sdi2output").FirstOrDefault().Value : "-1";

					int[] paramsIds = new[] { Parameter.audio1sdi1_11109, Parameter.audio1sdi2_11110 };
					object[] alValues = new object[] { sSD1, sSD2 };

					protocol.SetParameters(paramsIds, alValues);
				}
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Linq;
using System.Xml.Linq;
using QAction_3;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse Audio SDI Write.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string sXML = String.Empty;
			sXML = Convert.ToString(protocol.GetParameter(Parameter.audiodecodeconfigurationsdiresponseget_30116));  // XML Response for Audio 1 Settings

			int iTID = protocol.GetTriggerParameter();
			string sWrite = Convert.ToString(protocol.GetParameter(iTID));
			XDocument xdoc = null;

			// "ws/v2/service_cfg/decode/audio?sdi2output=PVO"
			string sUrl = "ws/v2/service_cfg/decode/";

			if (!string.IsNullOrEmpty(sXML))
			{
				xdoc = XDocument.Parse(sXML);

				if (xdoc.Descendants("decode").Any())
				{
					var values = from s in xdoc.Descendants("decode")
								 select new
								 {
									 aud = s.Descendants("audio").Descendants(),
								 };

					var lastValue = values.Last();

					if (iTID == Parameter.Write.audio1sdi1_11159)
					{
						lastValue.aud.Where(n => n.Name == "sdi1output").First().Value = sWrite;
						lastValue.aud.Where(n => n.Name != "sdi1output").Where(n => n.Name != "device").Remove();
						sUrl += "audio?sdi1output=" + sWrite;
					}
					else
					{
						lastValue.aud.Where(n => n.Name == "sdi2output").First().Value = sWrite;
						lastValue.aud.Where(n => n.Name != "sdi2output").Where(n => n.Name != "device").Remove();

						sUrl += "audio?sdi2output=" + sWrite;
					}

					int[] aiParamIds = new[] { Parameter.urlaudiodecodeconfigurationsdipost_30117};
					object[] aoValues = new object[] { sUrl};
					protocol.SetParameters(aiParamIds, aoValues);
					protocol.CheckTrigger((int)ProtocolTriggers.WhenAudio1SdiParametersWritten_58);
				}
			}
		}
		catch (Exception e)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to parse write with error: " + e, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse MOIP Streams Table Response.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		string sXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.mopistreamstableresponseget_30121)); // MOIP Streams Table

		XDocument doc = new XDocument();

		List<QActionTableRow> listRows = new List<QActionTableRow>();

		if (!string.IsNullOrEmpty(sXmlResponse))
		{
			try
			{
				doc = XDocument.Parse(sXmlResponse);
				IEnumerable<XElement> iPos = doc.Descendants("output").Descendants("moip1").Descendants("ipo");
				foreach (XElement item in iPos)
				{
					var moipStreamsTableRow = new MoipstreamstableQActionRow
					{
						Moipstreamsstream_30601 = Convert.ToString(item.Descendants("ipoid").FirstOrDefault().Value),
						Moipstreamsbitrate_30602 = Convert.ToDouble(item.Descendants("iporate").FirstOrDefault().Value, CultureInfo.InvariantCulture),
						Moipstreamsencapsulation_30603 = Convert.ToString(item.Descendants("encaps").FirstOrDefault().Value),
						Moipstreamsdestinationip_30604 = Convert.ToString(item.Descendants("dest_ip").FirstOrDefault().Value),
						Moipstreamsdestinationudpport_30605 = Convert.ToString(item.Descendants("dest_port").FirstOrDefault().Value),
						Moipstreamstrafficclass_30606 = Convert.ToInt32(item.Descendants("toc").FirstOrDefault().Value),
						Moipstreamstimetolive_30607 = Convert.ToInt32(item.Descendants("ttl").FirstOrDefault().Value),
						Moipstreamssourceudpport_30608 = Convert.ToInt32(item.Descendants("src_port").FirstOrDefault().Value),
					};

					listRows.Add(moipStreamsTableRow);
				}

				protocol.moipstreamstable.FillArray(listRows);
			}
			catch (Exception e)
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to parse response with error: " + e, LogType.Error, LogLevel.NoLogging);
			}
		}
	}
}

using System;
using System.Collections.Generic;
using QAction_3;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Set Configuration MOIP2.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			var ipoid = protocol.RowKey();
			var triggerParam = protocol.GetTriggerParameter();

			var parameterName = String.Empty;

			if (!BuildDictionaryMoip2().TryGetValue(triggerParam, out parameterName))
			{
				return;
			}

			var value = Convert.ToString(protocol.GetParameter(triggerParam));

			string url = "ws/v2/service_cfg/output/moip2/ipo?ipoid=" + ipoid + "&" + parameterName + "=" + value;
			protocol.SetParameter(Parameter.inputsconfigurationurlpost_900, url);

			protocol.CheckTrigger((int)ProtocolTriggers.OnSetMoip2Stream_60);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static Dictionary<int, string> BuildDictionaryMoip2()
	{
		var pairs = new Dictionary<int, string>
		{
			{ Parameter.Write.moip2streamsbitrate_23302, "iporate" },
			{ Parameter.Write.moip2streamsencapsulation_23303, "encaps" },
			{ Parameter.Write.moip2streamsdestinationip_23304, "dest_ip" },
			{ Parameter.Write.moip2streamsdestinationudpport_23305, "dest_port" },
		};

		return pairs;
	}
}

using System;
using System.Collections.Generic;
using System.Text;
using Skyline.DataMiner.Scripting;
using Skyline.Protocol.Logging;
using Skyline.Protocol.XmlClasses;

/// <summary>
/// Parses the fault data received from the device, into the faults table present in the faults page. Data obtained through the /ws/v2/status/faults/status command.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		object[] dataFromProtocol = (object[])protocol.GetParameters(new uint[] { Parameter.xmlfaultstatuscommandstatuscode_106, Parameter.xmlfaultstatuscommandresponse_103 });
		string faultStatusCode = Convert.ToString(dataFromProtocol[0]);
		string faultsStatusXml = Convert.ToString(dataFromProtocol[1]);

		if (!faultStatusCode.Contains("200 OK")
			|| string.IsNullOrWhiteSpace(faultsStatusXml))
		{
			return;
		}

		if (faultsStatusXml.Contains("No status records found"))
		{
			string[] undeletedFaultsStatus = protocol.faultsstatustable.Keys;

			if (undeletedFaultsStatus.Length > 0)
				protocol.faultsstatustable.DeleteRow(undeletedFaultsStatus);
			return;
		}

		// Issue correction: the device can send the ampersand symbol unescaped inside the fault's text. As such, we need to clean up the text before serialization
		faultsStatusXml = faultsStatusXml.Replace("&", "&amp;");

		try
		{
			Faults faultList = SerializationEngine.DeserializeIntoObject<Faults>(protocol, faultsStatusXml);

			if (faultList == default(Faults) || faultList.Status == null)
			{
				protocol.faultsstatustable.FillArray(new List<QActionTableRow>());
				return;
			}

			List<QActionTableRow> faultsTableUpdate = new List<QActionTableRow>(faultList.Status.Length * 2);
			string faultsTableKey = string.Empty;
			FaultsstatustableQActionRow temp;
			StringBuilder builder = new StringBuilder();

			foreach (FaultsStatus status in faultList.Status)
			{
				builder.Append(status.SetSinceText);
				builder.Append(" / ");
				builder.Append(status.Name.TrimEnd());
				builder.Append(" / ");
				builder.Append(status.Details);

				faultsTableKey = builder.ToString();
				builder.Clear();

				temp = new FaultsstatustableQActionRow
				{
					Faultsstatustableinstance_7101 = faultsTableKey,
					Faultsstatustablename_7102 = status.Name,
					Faultsstatustablesetsince_7103 = status.SetSince == default(DateTime) ? -1 : status.SetSince.Value.ToOADate(),
					Faultsstatustabletype_7104 = status.Type,
					Faultsstatustabledetails_7105 = status.Details,
				};

				faultsTableUpdate.Add(temp);
			}

			protocol.faultsstatustable.FillArray(faultsTableUpdate);
		}
		catch (Exception e)
		{
			LogHandler.LogException(protocol, e);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Text;
using System.Text.RegularExpressions;
using Skyline.DataMiner.Scripting;
using Skyline.Protocol.Logging;
using Skyline.Protocol.XmlClasses;

/// <summary>
/// Parses the ECC data received from the device, into the ECC tables present on the ECC page. Data obtained through the /ws/v2/status/device/ecc command.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		object[] dataFromProtocol = (object[]) protocol.GetParameters(new uint[] { Parameter.xmlecccommandstatuscode_107, Parameter.xmlecccommandresponse_104 });
		string eccDevicesStatusCode = Convert.ToString(dataFromProtocol[0]);
		string eccDevicesListXml = Convert.ToString(dataFromProtocol[1]);

		if (!eccDevicesStatusCode.Contains("200 OK") || string.IsNullOrWhiteSpace(eccDevicesListXml))
		{
			return;
		}

		try
		{
			EccData eccData = SerializationEngine.DeserializeIntoObject<EccData>(protocol, eccDevicesListXml);

			if (eccData == default(EccData) || eccData.EccSensors == null)
			{
				FillEmptyTables(protocol);
				return;
			}

			FillTablesWithData(protocol, eccData);
		}
		catch (Exception e)
		{
			LogHandler.LogException(protocol, e);
		}
	}

	private static void FillTablesWithData(SLProtocolExt protocol, EccData eccData)
	{
		List<QActionTableRow> eccTemperatureTableUpdate = new List<QActionTableRow>(eccData.EccSensors.Length);
		List<QActionTableRow> eccVoltageTableUpdate = new List<QActionTableRow>(eccData.EccSensors.Length);
		List<QActionTableRow> eccFansTableUpdate = new List<QActionTableRow>(eccData.EccSensors.Length);
		List<QActionTableRow> eccOtherTableUpdate = new List<QActionTableRow>(eccData.EccSensors.Length);

		string deviceKey = string.Empty;
		EcctemperaturestableQActionRow temperatureRow;
		EccvoltagetableQActionRow voltageRow;
		EccfanstableQActionRow fansRow;
		EccotherdatatableQActionRow otherRow;
		double convert;
		dynamic value;

		foreach (EccSensor sensor in eccData.EccSensors)
		{
			StringBuilder builder = new StringBuilder();

			builder.Append(sensor.DisplayItemName);
			builder.Append(" / Row");
			builder.Append(sensor.RowId);
			builder.Append(" / Slot");
			builder.Append(sensor.Slot);

			deviceKey = builder.ToString();
			builder.Clear();

			if (sensor.Type != EccType.NA &&
				double.TryParse(Regex.Replace(sensor.Value, "[A-Za-z ]", string.Empty), NumberStyles.AllowDecimalPoint | NumberStyles.AllowLeadingSign, CultureInfo.InvariantCulture, out convert))
			{
				value = convert;
			}
			else
			{
				value = sensor.Value;
			}

			switch (sensor.Type)
			{
				case EccType.NA:
					otherRow = new EccotherdatatableQActionRow
					{
						Eccotherdatatableinstance_7501 = deviceKey,
						Eccotherdatatableslot_7502 = sensor.Slot,
						Eccotherdatatablerow_7503 = sensor.RowId,
						Eccotherdatatablecard_7504 = sensor.Card,
						Eccotherdatatabledisplayboardname_7505 = sensor.DisplayBoardName,
						Eccotherdatatablelocation_7506 = sensor.Location,
						Eccotherdatatabledisplayitemname_7507 = sensor.DisplayItemName,
						Eccotherdatatablevalue_7508 = value,
						Eccotherdatatablecardslot_7509 = sensor.CardSlot,
						Eccotherdatatabledisplayorderflag_7510 = sensor.DisplayOrderFlag,
					};

					eccOtherTableUpdate.Add(otherRow);

					break;
				case EccType.Temperature:
					temperatureRow = new EcctemperaturestableQActionRow
					{
						Ecctemperaturestableinstance_7201 = deviceKey,
						Ecctemperaturestableslot_7202 = sensor.Slot,
						Ecctemperaturestablerow_7203 = sensor.RowId,
						Ecctemperaturestablecard_7204 = sensor.Card,
						Ecctemperaturestabledisplayboardname_7205 = sensor.DisplayBoardName,
						Ecctemperaturestablelocation_7206 = sensor.Location,
						Ecctemperaturestabledisplayitemname_7207 = sensor.DisplayItemName,
						Ecctemperaturestabletemperature_7208 = value,
						Ecctemperaturestablecardslot_7209 = sensor.CardSlot,
						Ecctemperaturestabledisplayorderflag_7210 = sensor.DisplayOrderFlag,
					};

					eccTemperatureTableUpdate.Add(temperatureRow);

					break;
				case EccType.FanStatusSpeed:
					fansRow = new EccfanstableQActionRow
					{
						Eccfanstableinstance_7401 = deviceKey,
						Eccfanstableslot_7402 = sensor.Slot,
						Eccfanstablerow_7403 = sensor.RowId,
						Eccfanstablecard_7404 = sensor.Card,
						Eccfanstabledisplayboardname_7405 = sensor.DisplayBoardName,
						Eccfanstablelocation_7406 = sensor.Location,
						Eccfanstabledisplayitemname_7407 = sensor.DisplayItemName,
						Eccfanstablespeed_7408 = value,
						Eccfanstablecardslot_7409 = sensor.CardSlot,
						Eccfanstabledisplayorderflag_7410 = sensor.DisplayOrderFlag,
					};

					eccFansTableUpdate.Add(fansRow);

					break;
				case EccType.Voltage:
					voltageRow = new EccvoltagetableQActionRow
					{
						Eccvoltagetableinstance_7301 = deviceKey,
						Eccvoltagetableslot_7302 = sensor.Slot,
						Eccvoltagetablerow_7303 = sensor.RowId,
						Eccvoltagetablecard_7304 = sensor.Card,
						Eccvoltagetabledisplayboardname_7305 = sensor.DisplayBoardName,
						Eccvoltagetablelocation_7306 = sensor.Location,
						Eccvoltagetabledisplayitemname_7307 = sensor.DisplayItemName,
						Eccvoltagetablevoltage_7308 = value,
						Eccvoltagetablecardslot_7309 = sensor.CardSlot,
						Eccvoltagetabledisplayorderflag_7310 = sensor.DisplayOrderFlag,
					};

					eccVoltageTableUpdate.Add(voltageRow);

					break;
				default:
					// Do Nothing
					break;
			}
		}

		protocol.ecctemperaturestable.FillArray(eccTemperatureTableUpdate);
		protocol.eccvoltagetable.FillArray(eccVoltageTableUpdate);
		protocol.eccfanstable.FillArray(eccFansTableUpdate);
		protocol.eccotherdatatable.FillArray(eccOtherTableUpdate);
	}

	private static void FillEmptyTables(SLProtocolExt protocol)
	{
		protocol.ecctemperaturestable.FillArray(new List<QActionTableRow>());
		protocol.eccvoltagetable.FillArray(new List<QActionTableRow>());
		protocol.eccfanstable.FillArray(new List<QActionTableRow>());
		protocol.eccotherdatatable.FillArray(new List<QActionTableRow>());
	}
}

using System;
using System.Collections.Generic;
using System.Text;
using Skyline.DataMiner.Scripting;
using Skyline.Protocol.Logging;
using Skyline.Protocol.XmlClasses;

/// <summary>
/// DataMiner QAction Class: Parse RF Status All Ports Response.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		object[] dataFromProtocol = (object[])protocol.GetParameters(new uint[] { Parameter.xmlrfstatusallportscommandstatuscode_108, Parameter.xmlrfstatusallportscommandresponse_105 });
		string radioFreqStatusCode = Convert.ToString(dataFromProtocol[0]);
		string radioFreqStatusXml = Convert.ToString(dataFromProtocol[1]);

		if (!radioFreqStatusCode.Contains("200 OK") || string.IsNullOrWhiteSpace(radioFreqStatusXml))
		{
			return;
		}

		try
		{
			RfInputs radioFreqStatusList = SerializationEngine.DeserializeIntoObject<RfInputs>(protocol, radioFreqStatusXml);

			if (radioFreqStatusList == default(RfInputs) || radioFreqStatusList.DeviceRfInputs == null)
			{
				protocol.rfunitsstatustable.FillArray(new List<QActionTableRow>());
				return;
			}

			List<QActionTableRow> radioFreqTableUpdate = new List<QActionTableRow>(radioFreqStatusList.DeviceRfInputs.Length * 2);
			string radioFreqUnitKey = string.Empty;
			RfunitsstatustableQActionRow temp;
			StringBuilder builder = new StringBuilder();

			foreach (RfElement element in radioFreqStatusList.DeviceRfInputs)
			{
				builder.Append("Card ");
				builder.Append(element.Card);
				builder.Append(" / Port");
				builder.Append(element.Port);

				radioFreqUnitKey = builder.ToString();
				builder.Clear();

				temp = new RfunitsstatustableQActionRow
				{
					Rfunitsstatustableinstance_7601 = radioFreqUnitKey,
					Rfunitsstatustablecard_7602 = element.Card,
					Rfunitsstatustableport_7603 = element.Port,
					Rfunitsstatustablestream_7604 = element.StreamId,
					Rfunitsstatustabledownlinkfrequency_7605 = element.DownlinkFrequency,
					Rfunitsstatustablesymbolrate_7606 = element.SymbolRate,
					Rfunitsstatustablefecmode_7607 = element.FecMode,
					Rfunitsstatustablemodulation_7608 = element.ModulationType,
					Rfunitsstatustablerolloff_7609 = element.RolloffFactor,
					Rfunitsstatustableiqmode_7610 = element.IqMode,
					Rfunitsstatustablelnbpowermode_7611 = element.LnbPower,
					Rfunitsstatustable22khzstatus_7612 = element.TwentyTwoKHzSelection,
					Rfunitsstatustablelo1_7613 = element.LoSelect1,
					Rfunitsstatustablelo2_7614 = element.LoSelect2,
					Rfunitsstatustablecrossoverfrequency_7615 = element.CrossOverFrequency,
					Rfunitsstatustablepolarization_7616 = element.Polarization == PolarizationMode.Automatic ? PolarizationMode.Auto : element.Polarization,
					Rfunitsstatustableorbitalposition_7617 = element.OrbitalPosition,
					Rfunitsstatustableeastwestflag_7618 = element.EastWestFlag,
					Rfunitsstatustableafcrangelimit_7619 = element.AfcRange,
					Rfunitsstatustablepsiacquisitionmode_7620 = element.PsiAcquisitionMode,
					Rfunitsstatustablesatellitelockstatus_7621 = element.SatelliteLock,
					Rfunitsstatustableinputrate_7622 = element.InputRate,
					Rfunitsstatustablenetworkname_7623 = element.NetworkName,
					Rfunitsstatustablenetworkid_7624 = string.IsNullOrWhiteSpace(element.NetworkId) ? -1 : Convert.ToInt32(element.NetworkId),
					Rfunitsstatustabletransportid_7625 = string.IsNullOrWhiteSpace(element.TransportId) ? -1 : Convert.ToInt32(element.TransportId),
					Rfunitsstatustablelasttunecode_7626 = element.LastTuneCode,
					Rfunitsstatustablelasttunereason_7627 = element.LastTuneReason,
					Rfunitsstatustablefrequencytuningmode_7628 = element.FrequencyTuningMode,
					Rfunitsstatustableservicelistmode_7629 = element.ServiceListMode,
					Rfunitsstatustablebatreception_7630 = element.BatReception,
					Rfunitsstatustablenitreception_7631 = element.NitReception,
					Rfunitsstatustablesdtreception_7632 = element.SdtReception,
					Rfunitsstatustablepatreception_7633 = element.PatReception,
					Rfunitsstatustablesignallevel_7634 = element.SignalLevel,
					Rfunitsstatustablecarriertonoiseratio_7635 = element.CarrierToNoiseRatio,
					Rfunitsstatustablecarriertonoisemargin_7636 = element.CarrierToNoiseMargin,
				};

				radioFreqTableUpdate.Add(temp);
			}

			double linkMargin = -9998;
			double signalLevel = -9998;
			double carrierToNoiseRatio = -1;
			string writeRfLock = "-1";

			if (radioFreqStatusList.DeviceRfInputs != null && radioFreqStatusList.DeviceRfInputs.Length > 0)
			{
				linkMargin = ConvertToDouble(radioFreqStatusList.DeviceRfInputs[0].CarrierToNoiseMargin);
				signalLevel = ConvertToDouble(radioFreqStatusList.DeviceRfInputs[0].SignalLevel);
				carrierToNoiseRatio = ConvertToDouble(radioFreqStatusList.DeviceRfInputs[0].CarrierToNoiseRatio);
				writeRfLock = Convert.ToString(radioFreqStatusList.DeviceRfInputs[0].SatelliteLock);
			}

			protocol.SetParameters(
				new[] { Parameter.linkmarginrf1_31480, Parameter.signallevelrf1_31482, Parameter.carriertonoiseratio_31483, Parameter.rflockstatus_31484 },
				new object[] { linkMargin, signalLevel, carrierToNoiseRatio, writeRfLock });

			protocol.rfunitsstatustable.FillArray(radioFreqTableUpdate);
		}
		catch (Exception e)
		{
			LogHandler.LogException(protocol, e);
		}
	}

	private static double ConvertToDouble(object value)
	{
		if (value == null)
		{
			return -9999; // or throw an exception based on your requirements
		}

		string valueAsString = Convert.ToString(value);

		if (string.IsNullOrEmpty(valueAsString))
		{
			return -9998;
		}

		if (double.TryParse(valueAsString, out double result))
		{
			return result;
		}

		return -9999; // or throw an exception based on your requirements
	}
}

