/// <summary>
/// DataMiner QAction Class: Parse Video Decode Status Response.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.videodecodestatustableresponseget_32011));

		XDocument doc;

		List<QActionTableRow> listRows = new List<QActionTableRow>();

		if (!string.IsNullOrWhiteSpace(stringXmlResponse))
		{
			try
			{
				stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				doc = XDocument.Parse(stringXmlResponse);
				IEnumerable<XElement> video = doc.Descendants("decode").Descendants("video");
				foreach (XElement item in video)
				{
					var videoDecodeStatusTableRow = new VideodecodestatusQActionRow
					{
						Videodecodestatusinstance_32001 = "1",
						Videodecodestatusinputstreamformat_32002 = Convert.ToString(item.Descendants("stream").FirstOrDefault().Value),
						Videodecodestatuspvoutputformat_32003 = Convert.ToString(item.Descendants("pvoformat").FirstOrDefault().Value, CultureInfo.InvariantCulture),
						Videodecodestatussdvideooutputformat_32004 = Convert.ToString(item.Descendants("sdoformat").FirstOrDefault().Value),
						Videodecodestatusbitrate_32005 = Convert.ToDouble(item.Descendants("bitrate").FirstOrDefault().Value),
						Videodecodestatuspulldown32_32006 = Convert.ToString(item.Descendants("pulldown32").FirstOrDefault().Value),
						Videodecodestatusframes_32007 = Convert.ToDouble(item.Descendants("frmpersec").FirstOrDefault().Value),
						Videodecodestatussynchronizationmode_32008 = Convert.ToString(item.Descendants("syncmode").FirstOrDefault().Value),
						Videodecodestatusencodingstandard_32009 = Convert.ToString(item.Descendants("encoding").FirstOrDefault().Value),
					};

					listRows.Add(videoDecodeStatusTableRow);
				}

				protocol.videodecodestatus.FillArray(listRows);
			}
			catch (Exception e)
			{
				LogHandler.LogException(protocol, e);
			}
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;
using Skyline.Protocol.Logging;

/// <summary>
/// DataMiner QAction Class: Parse Audio Decode Status Response.
/// </summary>
public class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.audiodecodestatustableresponseget_32061));

		XDocument doc;

		List<QActionTableRow> listRows = new List<QActionTableRow>();

		if (!string.IsNullOrEmpty(stringXmlResponse))
		{
			try
			{
				doc = XDocument.Parse(stringXmlResponse);
				IEnumerable<XElement> audio = doc.Descendants("decode").Descendants("audio");
				foreach (XElement item in audio)
				{
					var audioDecodeStatusTableRow = new AudiodecodestatusQActionRow
					{
						Audiodecodestatusdevice_32051= Convert.ToString(item.Descendants("device").FirstOrDefault().Value),
						Audiodecodestatusmode_32052 = Convert.ToString(item.Descendants("mode").FirstOrDefault().Value, CultureInfo.InvariantCulture),
						Audiodecodestatusbitrate_32053= Convert.ToInt32(item.Descendants("bitrate").FirstOrDefault().Value),
						Audiodecodestatusbufferlevel_32054= Convert.ToInt32(item.Descendants("bufferlvl").FirstOrDefault().Value),
						Audiodecodestatussamplerate_32055= Convert.ToDouble(item.Descendants("sfr").FirstOrDefault().Value),
						Audiodecodestatusdolbydigitalmode_32056= Convert.ToString(item.Descendants("ddpmode").FirstOrDefault().Value),
						Audiodecodestatusaudiodualmonomode_32057= Convert.ToString(item.Descendants("dualmono").FirstOrDefault().Value),
						Audiodecodestatuslanguage_32058 = Convert.ToString(item.Descendants("lang").FirstOrDefault().Value),
					};

					listRows.Add(audioDecodeStatusTableRow);
				}

				protocol.audiodecodestatus.FillArray(listRows);
			}
			catch (Exception e)
			{
				LogHandler.LogException(protocol, e);
			}
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse Disaster Recovery Response.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.disasterrecoveryresponseget_32101));

			List<QActionTableRow> listBackupRows = new List<QActionTableRow>();
			List<QActionTableRow> listSearchPathRows = new List<QActionTableRow>();

			if (!String.IsNullOrEmpty(stringXmlResponse))
			{
				XDocument doc = XDocument.Parse(stringXmlResponse);
				if (doc.Descendants("dr").Any())
				{
					XElement dr = doc.Descendants("dr").FirstOrDefault();
					int drstate = RetrieveDRState(dr);
					int drsignalLockTimer = dr.Descendants("global").Descendants("siglock").Any() ? Convert.ToInt32(dr.Descendants("global").Descendants("siglock").FirstOrDefault().Value) : -1;
					int drsignalLossTimer = dr.Descendants("global").Descendants("siglost").Any() ? Convert.ToInt32(dr.Descendants("global").Descendants("siglost").FirstOrDefault().Value) : -1;
					int drverificationTimer = dr.Descendants("global").Descendants("verify").Any() ? Convert.ToInt32(dr.Descendants("global").Descendants("verify").FirstOrDefault().Value) : -1;
					string drconfSrc = dr.Descendants("global").Descendants("cfgsource").Any() ? dr.Descendants("global").Descendants("cfgsource").FirstOrDefault().Value : "Unknown";
					string drspInput = dr.Descendants("searchstate").Descendants("actinpname").Any() ? dr.Descendants("searchstate").Descendants("actinpname").FirstOrDefault().Value : "Unknown";
					string drspTunningSrc = dr.Descendants("searchstate").Descendants("tuningsrc").Any() ? dr.Descendants("searchstate").Descendants("tuningsrc").FirstOrDefault().Value : "Unknown";
					string drspState = dr.Descendants("searchstate").Descendants("state").Any() ? dr.Descendants("searchstate").Descendants("state").FirstOrDefault().Value : "Unknown";
					int drspPendingInstances = dr.Descendants("searchstate").Descendants("numpendinp").Any() ? Convert.ToInt32(dr.Descendants("searchstate").Descendants("numpendinp").FirstOrDefault().Value) : -1;
					string drspNextInstance = dr.Descendants("searchstate").Descendants("nextinpname").Any() ? dr.Descendants("searchstate").Descendants("nextinpname").FirstOrDefault().Value : "Unknown";

					int[] paramsIds = new[]
					{
						Parameter.drstate_32104,
						Parameter.drsignallocktimer_32105,
						Parameter.drsignallosstimer_32106,
						Parameter.drverificationtimer_32107,
						Parameter.drconfsrc_32108,
						Parameter.drsearchpathinputname_32110,
						Parameter.drsearchpathtunningsrc_32111,
						Parameter.drserchpathstate_32112,
						Parameter.drsearchpathpendinginstances_32113,
						Parameter.drsearchpathnextinstance_32114,
					};

					object[] paramsVals = new object[]
					{
						drstate,
						drsignalLossTimer,
						drsignalLockTimer,
						drverificationTimer,
						drconfSrc,
						drspInput,
						drspTunningSrc,
						drspState,
						drspPendingInstances,
						drspNextInstance,
					};

					IEnumerable<XElement> backupInfo = dr.Descendants("backupsglb");
					FillBackupTableRows(backupInfo, listBackupRows);

					IEnumerable<XElement> searchPathInfo = dr.Descendants("origchans");
					FillSearchPathTableRows(searchPathInfo, listSearchPathRows);

					protocol.SetParameters(paramsIds, paramsVals);
					protocol.backuptable.FillArray(listBackupRows.ToArray());
					protocol.searchpathtable.FillArray(listSearchPathRows.ToArray());
				}
			}
			else
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Empty XML", LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	public static int RetrieveDRState(XElement dr)
	{
		int drstate;

		if (dr.Descendants("global").Descendants("status").Any())
		{
			switch (dr.Descendants("global").Descendants("status").FirstOrDefault().Value.Trim())
			{
				case "No Disaster - D/R Ready":
					drstate = 1;
					break;

				case "D/R in progress":
					drstate = 2;
					break;

				case "D/R disabled - Maintenance Mode":
					drstate = 3;
					break;

				case "NIT Retune Recovery in progress":
					drstate = 4;
					break;

				default:
					drstate = 5;
					break;
			}
		}
		else
		{
			drstate = 5;
		}

		return drstate;
	}

	public static void FillBackupTableRows(IEnumerable<XElement> backupInfo, List<QActionTableRow> listBackupRows)
	{
		foreach (XElement item in backupInfo)
		{
			if (item.Descendants("rowstatus").FirstOrDefault().Value.Equals("Inactive"))
			{
				continue;
			}

			BackuptableQActionRow backupTable = new BackuptableQActionRow
			{
				Backupinstance_32201 = item.Descendants("recid").FirstOrDefault().Value,
				Backupinputtype_32202 = RetrieveInputType(item),
			};

			listBackupRows.Add(backupTable);
		}
	}

	public static int RetrieveInputType(XElement item)
	{
		int intype;

		switch (item.Descendants("inputtype").FirstOrDefault().Value.Trim())
		{
			case "RF":
				intype = 1;
				break;

			case "MOIP":
				intype = 2;
				break;

			case "ASI":
				intype = 3;
				break;

			case "ABR":
				intype = 4;
				break;

			case "Zixi":
				intype = 5;
				break;

			case "SRT":
				intype = 6;
				break;

			default:
				intype = 7;
				break;
		}

		return intype;
	}

	public static void FillSearchPathTableRows(IEnumerable<XElement> searchPathInfo, List<QActionTableRow> listSearchPathRows)
	{
		foreach (XElement item in searchPathInfo)
		{
			SearchpathtableQActionRow searchPathTable = new SearchpathtableQActionRow
			{
				Searchpathinstance_32211 = item.Descendants("peid").FirstOrDefault().Value,
				Searchpathorigin_32212 = item.Descendants("inputname").FirstOrDefault().Value,
				Searchpathoriginchann_32213 = Convert.ToInt32(item.Descendants("origchan").FirstOrDefault().Value),
			};

			listSearchPathRows.Add(searchPathTable);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse SDI Audio Slot.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.sdiaudioslotresponseget_35001));

			XDocument doc;

			if (!string.IsNullOrWhiteSpace(stringXmlResponse))
			{
				try
				{
					stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

					doc = XDocument.Parse(stringXmlResponse);
					IEnumerable<XElement> video = doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD");
					int i = 1;
					foreach (XElement record in video)
					{
						IEnumerable<XElement> items = record.Descendants("ITEM");
						object[] itemsValues = new object[items.Count()];
						int j = 0;
						foreach (XElement item in items)
						{
							itemsValues[j] = Convert.ToString(item.Descendants("VALUE").FirstOrDefault().Value);
							j++;
						}

						SdiaudiogroupsetupQActionRow row = new SdiaudiogroupsetupQActionRow
						{
							Sdiaudiogroupsetupinstance_19501 = Convert.ToString(i),
							Sdiaudiogroupsetupaudiogroup_19502 = itemsValues[0],
							Sdiaudiogroupsetupgroupslot_19503 = itemsValues[1],
							Sdiaudiogroupsetupaudioinput_19504 = itemsValues[2],
							Sdiaudiogroupsetupchannelorder_19505 = itemsValues[3],
							Sdiaudiogroupsetupsdiid_19506 = itemsValues[4],
						};

						protocol.sdiaudiogroupsetup.SetRow(row, true);
						i++;
					}
				}
				catch (Exception ex)
				{
					protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
				}
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse CI Program Description.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.ciprogramdescriptionresponseget_35007));
			if (string.IsNullOrWhiteSpace(stringXmlResponse))
			{
				return;
			}

			var channelStatusTableRows = new List<ChannelstatusQActionRow>();

			var channelStatusColumns = (object[])protocol.NotifyProtocol(
				321,
				Parameter.Channelstatus.tablePid,
				new uint[]
				{
					Parameter.Channelstatus.Idx.channelstatuspeid_idx__12001,
					Parameter.Channelstatus.Idx.channelstatusinputname_12002,
					Parameter.Channelstatus.Idx.channelstatuschannel_12003,
					Parameter.Channelstatus.Idx.channelstatuschannelname_12004,
				});

			for (var i = 0; i < ((object[])channelStatusColumns[0]).Length; i++)
			{
				channelStatusTableRows.Add(new ChannelstatusQActionRow
				{
					Channelstatuspeid_idx__12001 = ((object[])channelStatusColumns[0])[i],
					Channelstatusinputname_12002 = ((object[])channelStatusColumns[1])[i],
					Channelstatuschannel_12003 = ((object[])channelStatusColumns[2])[i],
					Channelstatuschannelname_12004 = ((object[])channelStatusColumns[3])[i],
				});
			}

			try
			{
				stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				List<object[]> cipdRows = new List<object[]>();

				XDocument doc = XDocument.Parse(stringXmlResponse);
				foreach (var cdataNode in doc.DescendantNodes().OfType<XCData>().ToList())
				{
					cdataNode.ReplaceWith(new XText(cdataNode.Value));
				}

				IEnumerable<XElement> ciprRecords = doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD");
				foreach (XElement record in ciprRecords)
				{
					IEnumerable<XElement> items = record.Descendants("ITEM");
					object[] itemValues = new object[items.Count()];
					int j = 0;
					foreach (XElement item in items)
					{
						itemValues[j] = Convert.ToString(item.Descendants("VALUE").FirstOrDefault().Value ?? string.Empty);
						j++;
					}

					var currentChannelStatusRow = channelStatusTableRows.FirstOrDefault(row => Convert.ToString(row.Channelstatuspeid_idx__12001).Equals(Convert.ToString(itemValues[0])))
												  ?? throw new InvalidOperationException("No row with ID " + Convert.ToString(itemValues[0]));

					if (Convert.ToString(itemValues[0]).Equals("1"))
					{
						cipdRows.Add(
							new CommoninterfaceprogramdescriptionQActionRow
							{
								Commoninterfaceprogramdescriptionpeid_12501 = itemValues[0],
								Commoninterfaceprogramdescriptioninputname_12502 = currentChannelStatusRow.Channelstatusinputname_12002,
								Commoninterfaceprogramdescriptionchannel_12503 = Convert.ToString(currentChannelStatusRow.Channelstatuschannel_12003) + ":" + Convert.ToString(currentChannelStatusRow.Channelstatuschannelname_12004),
								Commoninterfaceprogramdescriptioncislot_12504 = itemValues[3],
								Commoninterfaceprogramdescriptiondecryptionmode_12505 = itemValues[1],
								Commoninterfaceprogramdescriptiondisplaykey_12506 = itemValues[0] + "/" + Convert.ToString(currentChannelStatusRow.Channelstatuschannel_12003) + ":" + Convert.ToString(currentChannelStatusRow.Channelstatuschannelname_12004),
								Commoninterfaceprogramdescriptioncislotdiscreetlist_12507 = "TOP;BOTTOM;AUTO",
							}.ToObjectArray()
						);

						continue;
					}

					cipdRows.Add(
						new CommoninterfaceprogramdescriptionQActionRow
						{
							Commoninterfaceprogramdescriptionpeid_12501 = itemValues[0],
							Commoninterfaceprogramdescriptioninputname_12502 = currentChannelStatusRow.Channelstatusinputname_12002,
							Commoninterfaceprogramdescriptionchannel_12503 = Convert.ToString(currentChannelStatusRow.Channelstatuschannel_12003) + ":" + Convert.ToString(currentChannelStatusRow.Channelstatuschannelname_12004),
							Commoninterfaceprogramdescriptioncislot_12504 = itemValues[2],
							Commoninterfaceprogramdescriptiondecryptionmode_12505 = itemValues[1],
							Commoninterfaceprogramdescriptiondisplaykey_12506 = itemValues[0] + "/" + Convert.ToString(currentChannelStatusRow.Channelstatuschannel_12003) + ":" + Convert.ToString(currentChannelStatusRow.Channelstatuschannelname_12004),
							Commoninterfaceprogramdescriptioncislotdiscreetlist_12507 = "TOP;BOTTOM",
						}.ToObjectArray()
					);
				}

				protocol.FillArray(Parameter.Commoninterfaceprogramdescription.tablePid, cipdRows, NotifyProtocol.SaveOption.Full);
			}
			catch (Exception ex)
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Parse Available Channels.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.availablechannelsresponseget_35009));
			string stringXmlCode = Convert.ToString(protocol.GetParameter(Parameter.availablechannelscodeget_35008));

			if (!String.IsNullOrWhiteSpace(stringXmlResponse) && stringXmlCode.Contains("200"))
			{
				StringBuilder availableChannelsDiscreets = new StringBuilder();

				stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument doc = XDocument.Parse(stringXmlResponse);
				IEnumerable<XElement> channels = doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD");

				foreach (XElement channel in channels)
				{
					IEnumerable<XElement> items = channel.Descendants("ITEM");
					object[] itemsValues = new object[items.Count()];
					int j = 0;
					foreach (XElement item in items)
					{
						itemsValues[j] = Convert.ToString(item.Descendants("VALUE").FirstOrDefault().Value ?? String.Empty);
						j++;
					}

					availableChannelsDiscreets.Append(itemsValues[2] + ":" + itemsValues[4] + ";");
				}

				if (availableChannelsDiscreets.Length > 0)
				{
					availableChannelsDiscreets.Remove(availableChannelsDiscreets.Length - 1, 1);
				}

				protocol.SetParameter(Parameter.channelstatuschannelnamelist_11998, availableChannelsDiscreets.ToString());
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Error trying to Parse with exception:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Xml;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Channel Status Table Standalone Changes.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		var trigger = protocol.GetTriggerParameter();
		var rowKey = protocol.RowKey();

		try
		{
			var selectedChannel = Convert.ToString(protocol.GetParameter(trigger));
			selectedChannel = selectedChannel.Split(new[] { ':' })[0];

			XmlDocument payload = new XmlDocument();
			XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
			XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
			XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
			XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
			XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
			XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
			XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
			XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

			payloadChannelID.InnerText = "0x002D0852";
			payloadChannelValue.InnerText = selectedChannel;
			payloadStreamID.InnerText = "0x002D0851";
			payloadStreamValue.InnerText = "PE" + rowKey;
			payloadIndex.AppendChild(payloadStreamID);
			payloadIndex.AppendChild(payloadStreamValue);
			payloadItem.AppendChild(payloadChannelID);
			payloadItem.AppendChild(payloadChannelValue);
			payloadItem.AppendChild(payloadIndex);
			payloadConfig.AppendChild(payloadItem);
			payloadHdr.AppendChild(payloadConfig);
			payload.AppendChild(payloadHdr);

			protocol.SetParameter(Parameter.channelselectionpayloadpost_35012, payload.OuterXml);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Xml;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: CI Slot Configuration Change.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		var trigger = protocol.GetTriggerParameter();
		var rowKey = protocol.RowKey();

		try
		{
			string selectedColumn = Convert.ToString(protocol.GetParameter(trigger));
			XmlDocument payload = new XmlDocument();
			int payloadID;

			switch (trigger)
			{
				case Parameter.Write.commoninterfaceprogramdescriptionchannel_12553:

					selectedColumn = selectedColumn.Split(new[] { ':' })[0];
					payloadID = FillChannelSelectionPayload(payload, selectedColumn, rowKey);
					break;

				case Parameter.Write.commoninterfaceprogramdescriptioncislot_12554:

					payloadID = FillCISlotPayload(payload, selectedColumn, rowKey);
					break;

				case Parameter.Write.commoninterfaceprogramdescriptiondecryptionmode_12555:

					payloadID = FillDecryptionModePayload(payload, selectedColumn, rowKey);
					break;

				default:
					protocol.Log("QA" + protocol.QActionID + "|Run|Unknown trigger.", LogType.Error, LogLevel.NoLogging);
					return;
			}

			protocol.SetParameter(payloadID, payload.OuterXml);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static int FillChannelSelectionPayload(XmlDocument payload, string selectedChannel, string rowId)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadChannelID.InnerText = "0x002D0852";
		payloadChannelValue.InnerText = selectedChannel;
		payloadStreamID.InnerText = "0x002D0851";
		payloadStreamValue.InnerText = "PE" + rowId;
		payloadIndex.AppendChild(payloadStreamID);
		payloadIndex.AppendChild(payloadStreamValue);
		payloadItem.AppendChild(payloadChannelID);
		payloadItem.AppendChild(payloadChannelValue);
		payloadItem.AppendChild(payloadIndex);
		payloadConfig.AppendChild(payloadItem);
		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);

		return Parameter.channelselectionpayloadpost_35012;
	}

	private static int FillCISlotPayload(XmlDocument payload, string selectedCISlotMode, string rowId)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadCISlotID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadCISlotValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadCISlotModeID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadCISlotModeValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadCISlotModeID.InnerText = "0x002D0FE3";
		payloadCISlotModeValue.InnerText = rowId;
		payloadIndex.AppendChild(payloadCISlotModeID);
		payloadIndex.AppendChild(payloadCISlotModeValue);
		payloadCISlotID.InnerText = "0x002D0FE5";
		payloadCISlotValue.InnerText = selectedCISlotMode;
		payloadItem.AppendChild(payloadCISlotID);
		payloadItem.AppendChild(payloadCISlotValue);
		payloadItem.AppendChild(payloadIndex);
		payloadConfig.AppendChild(payloadItem);
		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);

		return Parameter.cislotpayloadpost_35015;
	}

	private static int FillDecryptionModePayload(XmlDocument payload, string selectedDecryptionMode, string rowId)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadDecryptionID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadDecryptionValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadDecryptionModeID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadDecryptionModeValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadDecryptionModeID.InnerText = "0x002D0FE3";
		payloadDecryptionModeValue.InnerText = rowId;
		payloadIndex.AppendChild(payloadDecryptionModeID);
		payloadIndex.AppendChild(payloadDecryptionModeValue);
		payloadDecryptionID.InnerText = "0x002D0FE4";
		payloadDecryptionValue.InnerText = selectedDecryptionMode;
		payloadItem.AppendChild(payloadDecryptionID);
		payloadItem.AppendChild(payloadDecryptionValue);
		payloadItem.AppendChild(payloadIndex);
		payloadConfig.AppendChild(payloadItem);
		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);

		return Parameter.decryptionmodepayloadpost_35018;
	}
}

