using System;
using System.Xml;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: SDI Audio Group Table Standalone Change.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		int trigger = protocol.GetTriggerParameter();
		string rowKey = protocol.RowKey();

		try
		{
			string sdiSelection = Convert.ToString(protocol.GetParameter(trigger));
			SdiaudiogroupsetupQActionRow sdiAudioGroupRow = new SdiaudiogroupsetupQActionRow((object[])protocol.GetRow(Parameter.Sdiaudiogroupsetup.tablePid, rowKey));
			XmlDocument payload = new XmlDocument();
			int payloadID;

			switch (trigger)
			{
				case Parameter.Write.sdiaudiogroupsetupaudioinput_19554:

					payloadID = FillSdiAudioInputPayload(payload, sdiSelection, sdiAudioGroupRow);
					break;

				case Parameter.Write.sdiaudiogroupsetupchannelorder_19555:

					payloadID = FillSdiChannelOrderPayload(payload, sdiSelection, sdiAudioGroupRow);
					break;

				default:
					protocol.Log("QA" + protocol.QActionID + "|Run|Unknown trigger.", LogType.Error, LogLevel.NoLogging);
					return;
			}

			protocol.SetParameter(payloadID, payload.OuterXml);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static int FillSdiAudioInputPayload(XmlDocument payload, string selectedAudioInput, SdiaudiogroupsetupQActionRow sdiAudioGroupRow)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadRequestID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadRequestValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadSdiResIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadSdiResID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadSdiResValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadAudioGroupIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadAudioGroupID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadAudioGroupValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadGroupSlotIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadGroupSlotID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadGroupSlotValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadSdiResID.InnerText = "0x002D0CEA";
		payloadSdiResValue.InnerText = Convert.ToString(sdiAudioGroupRow.Sdiaudiogroupsetupsdiid_19506);
		payloadSdiResIndex.AppendChild(payloadSdiResID);
		payloadSdiResIndex.AppendChild(payloadSdiResValue);
		payloadItem.AppendChild(payloadSdiResIndex);

		payloadAudioGroupID.InnerText = "0x002D0CE6";
		payloadAudioGroupValue.InnerText = Convert.ToString(sdiAudioGroupRow.Sdiaudiogroupsetupaudiogroup_19502);
		payloadAudioGroupIndex.AppendChild(payloadAudioGroupID);
		payloadAudioGroupIndex.AppendChild(payloadAudioGroupValue);
		payloadItem.AppendChild(payloadAudioGroupIndex);

		payloadGroupSlotID.InnerText = "0x002D0CE7";
		payloadGroupSlotValue.InnerText = Convert.ToString(sdiAudioGroupRow.Sdiaudiogroupsetupgroupslot_19503);
		payloadGroupSlotIndex.AppendChild(payloadGroupSlotID);
		payloadGroupSlotIndex.AppendChild(payloadGroupSlotValue);
		payloadItem.AppendChild(payloadGroupSlotIndex);

		payloadRequestID.InnerText = "0x002D0CE8";
		payloadRequestValue.InnerText = selectedAudioInput;
		payloadItem.AppendChild(payloadRequestID);
		payloadItem.AppendChild(payloadRequestValue);
		payloadConfig.AppendChild(payloadItem);
		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);

		return Parameter.sdiaudiogroupaudioinputchangepayloadpost_35027;
	}

