using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse Audio Decoding Audio Selection.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.audiodecodingaudioselectionresponseget_35032));
			string stringXmlCode = Convert.ToString(protocol.GetParameter(Parameter.audiodecodingaudioselectioncodeget_35031));

			if (!string.IsNullOrWhiteSpace(stringXmlResponse) && stringXmlCode.Contains("200"))
			{
				stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument doc = XDocument.Parse(stringXmlResponse);
				IEnumerable<XElement> audio = doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD");
				List<object[]> audioSelectionList = new List<object[]>();

				foreach (XElement entry in audio)
				{
					IEnumerable<XElement> items = entry.Descendants("ITEM");
					HandleAudioEntry(protocol, audioSelectionList, items);
				}

				protocol.FillArray(Parameter.Audioselectiontable.tablePid, audioSelectionList, NotifyProtocol.SaveOption.Full);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Error trying to Parse with exception:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void HandleAudioEntry(SLProtocol protocol, List<object[]> audioSelectionList, IEnumerable<XElement> items)
	{
		object[] itemsValues = new object[items.Count()];
		int j = 0;
		foreach (XElement item in items)
		{
			itemsValues[j] = Convert.ToString(item.Descendants("VALUE").FirstOrDefault().Value ?? string.Empty);
			j++;
		}

		if (itemsValues[0].Equals("1"))
		{
			protocol.SetParameters(
				new int[]
				{
					Parameter.audioselectionpmtsourcelist_32998,
					Parameter.audioselectionlanguagediscreetlist_32997,
				},
				new object[]
				{
					GetEnums(items, 5),
					GetEnums(items, 8),
				}
			);
		}

		audioSelectionList.Add(
			new AudioselectiontableQActionRow
			{
				Audioselectiontableaudiodecodeid_33001 = itemsValues[0],
				Audioselectionselectby_33002 = itemsValues[7],
				Audioselectionpmtsource_33003 = itemsValues[5],
				Audioselectionlanguagelist_33004 = itemsValues[8],
				Audioselectionmanualentry_33005 = itemsValues[9],
			}.ToObjectArray()
		);
	}

	private static string GetEnums(IEnumerable<XElement> items, int id)
	{
		IEnumerable<XElement> enumsSource = items.ToList()[id].Descendants("ENUM_VALUE");
		StringBuilder discreetList = new StringBuilder();
		foreach (XElement discreet in enumsSource)
		{
			discreetList.Append(Convert.ToString(discreet.Value) + ";");
		}

		discreetList.Remove(discreetList.Length - 1, 1);

		return discreetList.ToString();
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse Audio Decoding Channel Selection.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.audiodecodingchannelselectionresponseget_35034));
			string stringXmlCode = Convert.ToString(protocol.GetParameter(Parameter.audiodecodingchannelselectioncodeget_35033));

			if (!string.IsNullOrWhiteSpace(stringXmlResponse) && stringXmlCode.Contains("200"))
			{
				stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument doc = XDocument.Parse(stringXmlResponse);
				if (!doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD").Descendants("ITEM").Any())
				{
					return;
				}

				IEnumerable<XElement> items = doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD").First().Descendants("ITEM");
				object[] itemsValues = new object[items.Count()];
				int j = 0;
				foreach (XElement item in items)
				{
					itemsValues[j] = Convert.ToString(item.Descendants("VALUE").FirstOrDefault().Value ?? string.Empty);
					j++;
				}
				string channelSelection = itemsValues[1] + ":" + itemsValues[2];

				protocol.SetParameter(Parameter.singlechannelselection_9020, channelSelection);
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Error trying to Parse with exception:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Text;
using System.Xml;
using Skyline.DataMiner.Net;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Audio Decoding Channel Selection Change.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		var trigger = protocol.GetTriggerParameter();

		try
		{
			var selectedChannel = Convert.ToString(protocol.GetParameter(trigger));
			selectedChannel = selectedChannel.Split(new[] { ':' })[0];

			XmlDocument payload = new XmlDocument();
			XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
			XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
			XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
			XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
			XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
			XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
			XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
			XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

			payloadChannelID.InnerText = "0x002D0852";
			payloadChannelValue.InnerText = selectedChannel;
			payloadStreamID.InnerText = "0x002D0851";
			payloadStreamValue.InnerText = "PE1";
			payloadIndex.AppendChild(payloadStreamID);
			payloadIndex.AppendChild(payloadStreamValue);
			payloadItem.AppendChild(payloadChannelID);
			payloadItem.AppendChild(payloadChannelValue);
			payloadItem.AppendChild(payloadIndex);
			payloadConfig.AppendChild(payloadItem);
			payloadHdr.AppendChild(payloadConfig);
			payload.AppendChild(payloadHdr);

			protocol.SetParameter(Parameter.channelselectionpayloadpost_35012, payload.OuterXml);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Xml;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Audio Decoding Table Changes.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		var trigger = protocol.GetTriggerParameter();
		var rowKey = protocol.RowKey();

		try
		{
			var audioSelectionChange = Convert.ToString(protocol.GetParameter(trigger));
			var audioDecodingRows = new List<AudioselectiontableQActionRow>();

			var audioDecodingColumns = (object[])protocol.NotifyProtocol(
				321,
				Parameter.Audioselectiontable.tablePid,
				new uint[]
				{
						Parameter.Audioselectiontable.Idx.audioselectiontableaudiodecodeid_33001,
						Parameter.Audioselectiontable.Idx.audioselectionselectby_33002,
						Parameter.Audioselectiontable.Idx.audioselectionpmtsource_33003,
						Parameter.Audioselectiontable.Idx.audioselectionlanguagelist_33004,
						Parameter.Audioselectiontable.Idx.audioselectionmanualentry_33005,
				});

			for (var i = 0; i < ((object[])audioDecodingColumns[0]).Length; i++)
			{
				audioDecodingRows.Add(new AudioselectiontableQActionRow
				{
					Audioselectiontableaudiodecodeid_33001 = ((object[])audioDecodingColumns[0])[i],
					Audioselectionselectby_33002 = ((object[])audioDecodingColumns[1])[i],
					Audioselectionpmtsource_33003 = ((object[])audioDecodingColumns[2])[i],
					Audioselectionlanguagelist_33004 = ((object[])audioDecodingColumns[3])[i],
					Audioselectionmanualentry_33005 = ((object[])audioDecodingColumns[4])[i],
				});
			}

			var audioDecodingRow = audioDecodingRows.FirstOrDefault(row => Convert.ToString(row.Audioselectiontableaudiodecodeid_33001).Equals(rowKey))
												?? throw new InvalidOperationException("No row with ID " + rowKey);

			XmlDocument payload = new XmlDocument();

			switch (trigger)
			{
				case Parameter.Write.audioselectionselectby_33052:

					if (( audioSelectionChange.Contains("PMT Order") && audioDecodingRows.FirstOrDefault(row => Convert.ToString(row.Audioselectionselectby_33002).Contains("Language")) != null )
						|| (audioSelectionChange.Contains("Language") && audioDecodingRows.FirstOrDefault(row => Convert.ToString(row.Audioselectionselectby_33002).Contains("PMT Order")) != null))
					{
						protocol.ShowInformationMessage("Cannot set selection to a combination of PMT Order and Language types. To change type please use the Mass Selection Change option.");
						return;
					}

					FillSelectByPayload(payload, audioSelectionChange, rowKey);
					break;

				case Parameter.Write.audioselectionpmtsource_33053:


					if (!Convert.ToString(audioDecodingRow.Audioselectionselectby_33002).Equals("PMT Order"))
					{
						protocol.ShowInformationMessage("The PMT Source column can only be edited with PMT Order selected.");
						return;
					}

					FillPmtOrderPayload(payload, audioSelectionChange, rowKey);
					break;

				case Parameter.Write.audioselectionlanguagelist_33054:

					if (!Convert.ToString(audioDecodingRow.Audioselectionselectby_33002).Equals("Language List"))
					{
						protocol.ShowInformationMessage("The Language List column can only be edited with Language List selected.");
						return;
					}

					FillLanguageListPayload(payload, audioSelectionChange, rowKey);
					break;

				case Parameter.Write.audioselectionmanualentry_33055:

					if (!Convert.ToString(audioDecodingRow.Audioselectionselectby_33002).Equals("Language Entry"))
					{
						protocol.ShowInformationMessage("The Manual Entry column can only be edited with Language Entry selected.");
						return;
					}

					if (audioSelectionChange.Length > 3)
					{
						protocol.ShowInformationMessage("A Manual Entry cannot be more than 3 characters long.");
						return;
					}

					FillLanguageEntryPayload(payload, audioSelectionChange, rowKey);
					break;

				default:
					protocol.Log("QA" + protocol.QActionID + "|Run|Unknown trigger.", LogType.Error, LogLevel.NoLogging);
					return;
			}

			protocol.SetParameter(Parameter.audiodecodingtablechangepayloadpost_35037, payload.OuterXml);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void FillSelectByPayload(XmlDocument payload, string decodingSelection, string rowID)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadStreamID.InnerText = "0x002D0422";
		payloadStreamValue.InnerText = rowID;
		payloadIndex.AppendChild(payloadStreamID);
		payloadIndex.AppendChild(payloadStreamValue);
		payloadItem.AppendChild(payloadIndex);

		payloadChannelID.InnerText = "0x002D0429";
		payloadChannelValue.InnerText = decodingSelection;
		payloadItem.AppendChild(payloadChannelID);
		payloadItem.AppendChild(payloadChannelValue);
		payloadConfig.AppendChild(payloadItem);

		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);
	}

	private static void FillPmtOrderPayload(XmlDocument payload, string pmtOrder, string rowID)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadStreamID.InnerText = "0x002D0422";
		payloadStreamValue.InnerText = rowID;
		payloadIndex.AppendChild(payloadStreamID);
		payloadIndex.AppendChild(payloadStreamValue);
		payloadItem.AppendChild(payloadIndex);

		payloadChannelID.InnerText = "0x002D0427";
		payloadChannelValue.InnerText = pmtOrder;
		payloadItem.AppendChild(payloadChannelID);
		payloadItem.AppendChild(payloadChannelValue);
		payloadConfig.AppendChild(payloadItem);

		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);
	}

	private static void FillLanguageListPayload(XmlDocument payload, string languageSelection, string rowID)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadStreamID.InnerText = "0x002D0422";
		payloadStreamValue.InnerText = rowID;
		payloadIndex.AppendChild(payloadStreamID);
		payloadIndex.AppendChild(payloadStreamValue);
		payloadItem.AppendChild(payloadIndex);

		payloadChannelID.InnerText = "0x002D042A";
		payloadChannelValue.InnerText = languageSelection;
		payloadItem.AppendChild(payloadChannelID);
		payloadItem.AppendChild(payloadChannelValue);
		payloadConfig.AppendChild(payloadItem);

		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);
	}

	private static void FillLanguageEntryPayload(XmlDocument payload, string languageEntry, string rowID)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadStreamID.InnerText = "0x002D0422";
		payloadStreamValue.InnerText = rowID;
		payloadIndex.AppendChild(payloadStreamID);
		payloadIndex.AppendChild(payloadStreamValue);
		payloadItem.AppendChild(payloadIndex);

		payloadChannelID.InnerText = "0x002D042B";
		payloadChannelValue.InnerText = languageEntry;
		payloadItem.AppendChild(payloadChannelID);
		payloadItem.AppendChild(payloadChannelValue);
		payloadConfig.AppendChild(payloadItem);

		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Text;
using System.Xml;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Audio Decoding Mass Selection Change.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		var trigger = protocol.GetTriggerParameter();

		try
		{
			var audioSelectionChange = Convert.ToString(protocol.GetParameter(trigger));
			var audioDecodingRows = new List<AudioselectiontableQActionRow>();

			var audioDecodingColumns = (object[])protocol.NotifyProtocol(
				321,
				Parameter.Audioselectiontable.tablePid,
				new uint[]
				{
						Parameter.Audioselectiontable.Idx.audioselectiontableaudiodecodeid_33001,
						Parameter.Audioselectiontable.Idx.audioselectionselectby_33002,
						Parameter.Audioselectiontable.Idx.audioselectionpmtsource_33003,
						Parameter.Audioselectiontable.Idx.audioselectionlanguagelist_33004,
						Parameter.Audioselectiontable.Idx.audioselectionmanualentry_33005,
				});

			for (var i = 0; i < ((object[])audioDecodingColumns[0]).Length; i++)
			{
				audioDecodingRows.Add(new AudioselectiontableQActionRow
				{
					Audioselectiontableaudiodecodeid_33001 = ((object[])audioDecodingColumns[0])[i],
					Audioselectionselectby_33002 = ((object[])audioDecodingColumns[1])[i],
					Audioselectionpmtsource_33003 = ((object[])audioDecodingColumns[2])[i],
					Audioselectionlanguagelist_33004 = ((object[])audioDecodingColumns[3])[i],
					Audioselectionmanualentry_33005 = ((object[])audioDecodingColumns[4])[i],
				});
			}

			XmlDocument payload = new XmlDocument();

			audioSelectionChange = audioSelectionChange.Equals("Language List/Entry") ? "Language List" : audioSelectionChange;

			FillSelectAllPayload(payload, audioSelectionChange, audioDecodingRows);

			protocol.SetParameter(Parameter.audiodecodingtablechangepayloadpost_35037, payload.OuterXml);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void FillSelectAllPayload(XmlDocument payload, string decodingSelection, IEnumerable<AudioselectiontableQActionRow> audioDecodingRows)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);

		foreach (var row in audioDecodingRows)
		{
			XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
			XmlElement payloadChannelID = payload.CreateElement(string.Empty, "ID", string.Empty);
			XmlElement payloadChannelValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
			XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
			XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
			XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

			payloadStreamID.InnerText = "0x002D0422";
			payloadStreamValue.InnerText = Convert.ToString(row.Audioselectiontableaudiodecodeid_33001);
			payloadIndex.AppendChild(payloadStreamID);
			payloadIndex.AppendChild(payloadStreamValue);
			payloadItem.AppendChild(payloadIndex);

			payloadChannelID.InnerText = "0x002D0429";
			payloadChannelValue.InnerText = decodingSelection;
			payloadItem.AppendChild(payloadChannelID);
			payloadItem.AppendChild(payloadChannelValue);
			payloadConfig.AppendChild(payloadItem);
		}

		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Parse HDMI Status Values.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string stringXmlResponse = Convert.ToString(protocol.GetParameter(Parameter.hdmistatusvaluesresponseget_35039));
			string stringXmlCode = Convert.ToString(protocol.GetParameter(Parameter.hdmistatusvaluescodeget_35038));

			if (!string.IsNullOrWhiteSpace(stringXmlResponse) && stringXmlCode.Contains("200"))
			{
				stringXmlResponse = Regex.Replace(stringXmlResponse, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

				XDocument doc = XDocument.Parse(stringXmlResponse);
				XElement hdmiStatus = doc.Descendants("HDR").Descendants("TABLE").Descendants("RECORD").First();

				IEnumerable<XElement> items = hdmiStatus.Descendants("ITEM");
				object[] itemsValues = new object[items.Count()];
				int j = 0;
				foreach (XElement item in items)
				{
					itemsValues[j] = Convert.ToString(item.Descendants("VALUE").FirstOrDefault().Value ?? string.Empty);
					j++;
				}

				protocol.SetParameters(
					new int[]
					{
						Parameter.hdmistatusconnected_33500,
						Parameter.hdmistatuspowered_33501,
						Parameter.hdmistatusmonitorname_33502,
						Parameter.hdmistatuscolorspace_33503,
						Parameter.hdmistatuscolorrange_33504,
						Parameter.hdmistatuscolordepth_33505,
						Parameter.hdmistatusmatrixcoeff_33506,
						Parameter.hdmistatuseotf_33507,
						Parameter.hdmistatusconnected_discreetlist_33600,
						Parameter.hdmistatuspowered_discreetlist_33601,
						Parameter.hdmistatuscolorspace_discreetlist_33603,
						Parameter.hdmistatuscolorrange_discreetlist_33604,
						Parameter.hdmistatuscolordepth_discreetlist_33605,
						Parameter.hdmistatusmatrixcoeff_discreetlist_33606,
						Parameter.hdmistatuseotf_discreetlist_33607,
					},
					new object[]
					{
						itemsValues[1],
						itemsValues[2],
						itemsValues[3],
						itemsValues[4],
						itemsValues[5],
						itemsValues[6],
						itemsValues[7],
						itemsValues[8],
						GetEnums(items, 1),
						GetEnums(items, 2),
						GetEnums(items, 4),
						GetEnums(items, 5),
						GetEnums(items, 6),
						GetEnums(items, 7),
						GetEnums(items, 8),
					});
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Error trying to Parse with exception:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static string GetEnums(IEnumerable<XElement> items, int id)
	{
		IEnumerable<XElement> enumsSource = items.ToList()[id].Descendants("ENUM_VALUE");
		StringBuilder discreetList = new StringBuilder();
		foreach (XElement discreet in enumsSource)
		{
			discreetList.Append(Convert.ToString(discreet.Value) + ";");
		}

		discreetList.Remove(discreetList.Length - 1, 1);

		return discreetList.ToString();
	}
}

