using System;
using System.Collections.Generic;
using System.Globalization;
using System.Text;
using System.Xml;
using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: HDMI Setup Standalone Changes.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		var trigger = protocol.GetTriggerParameter();
		try
		{
			var hdmiSetupChange = Convert.ToString(protocol.GetParameter(trigger));
			XmlDocument payload = new XmlDocument();

			switch (trigger)
			{
				case Parameter.Write.hdmisetuppreferredmode_33561:

					FillPayload(payload, hdmiSetupChange, "0x002D2072");
					break;

				case Parameter.Write.hdmisetupcolorspace_33562:

					FillPayload(payload, hdmiSetupChange, "0x002D206D");
					break;

				case Parameter.Write.hdmisetupcolorrange_33563:

					FillPayload(payload, hdmiSetupChange, "0x002D206F");
					break;

				case Parameter.Write.hdmisetupcolordepth_33564:

					FillPayload(payload, hdmiSetupChange, "0x002D206E");
					break;

				case Parameter.Write.hdmisetupmatrixcoefficients_33565:

					FillPayload(payload, hdmiSetupChange, "0x002D2070");
					break;

				case Parameter.Write.hdmisetupeotf_33566:

					FillPayload(payload, hdmiSetupChange, "0x002D2071");
					break;

				case Parameter.Write.hdmisetupforceedid_33567:

					FillPayload(payload, hdmiSetupChange, "0x002D2073");
					break;

				default:
					protocol.Log("QA" + protocol.QActionID + "|Run|Unknown trigger.", LogType.Error, LogLevel.NoLogging);
					return;
			}

			protocol.SetParameter(Parameter.hdmisetupchangepayloadpost_35044, payload.OuterXml);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void FillPayload(XmlDocument payload, string selection, string configID)
	{
		XmlElement payloadHdr = payload.CreateElement(string.Empty, "HDR", string.Empty);
		XmlElement payloadConfig = payload.CreateElement(string.Empty, "CONFIG", string.Empty);
		XmlElement payloadItem = payload.CreateElement(string.Empty, "ITEM", string.Empty);
		XmlElement payloadID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);
		XmlElement payloadIndex = payload.CreateElement(string.Empty, "INDEX", string.Empty);
		XmlElement payloadStreamID = payload.CreateElement(string.Empty, "ID", string.Empty);
		XmlElement payloadStreamValue = payload.CreateElement(string.Empty, "VALUE", string.Empty);

		payloadStreamID.InnerText = "0x002D206C";
		payloadStreamValue.InnerText = "1";
		payloadIndex.AppendChild(payloadStreamID);
		payloadIndex.AppendChild(payloadStreamValue);
		payloadItem.AppendChild(payloadIndex);

		payloadID.InnerText = configID;
		payloadValue.InnerText = selection;
		payloadItem.AppendChild(payloadID);
		payloadItem.AppendChild(payloadValue);
		payloadConfig.AppendChild(payloadItem);

		payloadHdr.AppendChild(payloadConfig);
		payload.AppendChild(payloadHdr);
	}
}

