using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Text;

using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Export Config File.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string[] asHttpParams = Array.ConvertAll((object[])protocol.GetParameters(new uint[] { Parameter.exportpath_35503, Parameter.filetoexport_35513, Parameter.exportstatuscode_35505, Parameter.exportresponse_35506 }), Convert.ToString);
			string sExportPath = asHttpParams[0].EndsWith("\\") ? asHttpParams[0] : string.Format("{0}\\", asHttpParams[0]);
			string sExportFile = asHttpParams[1].EndsWith(".xml") ? asHttpParams[1] : string.Format("{0}.xml", asHttpParams[1]);
			string sHttpCode = asHttpParams[2];
			string sHttpResp = asHttpParams[3];

			if (!sHttpCode.Contains("200"))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|HTTP status code: " + sHttpCode, LogType.Error, LogLevel.NoLogging);
				protocol.SetParameter(Parameter.exportstatus_35511, 2);
				return;
			}

			if (!Directory.Exists(sExportPath))
			{
				Directory.CreateDirectory(sExportPath);
			}

			File.WriteAllText(string.Format("{0}{1}", sExportPath, sExportFile), sHttpResp);
			protocol.SetParameter(Parameter.exportstatus_35511, 1);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using System.Globalization;
using System.Text;

using Skyline.DataMiner.Scripting;

/// <summary>
/// DataMiner QAction Class: Verify Import Process.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string[] asHttpParams = Array.ConvertAll((object[])protocol.GetParameters(new uint[] { Parameter.importstatuscode_35509, Parameter.importresponse_35510 }), Convert.ToString);
			string sHttpStatus = asHttpParams[0];
			string sHttpResp = asHttpParams[1];

			if (!sHttpStatus.Contains("200"))
			{
				protocol.Log("QA" + protocol.QActionID + "|Run|HTTP status code = " + sHttpStatus, LogType.Error, LogLevel.NoLogging);
				protocol.SetParameter(Parameter.importstatus_35512, 2);
				return;
			}

			protocol.Log("QA" + protocol.QActionID + "|Run|HTTP response = " + sHttpResp, LogType.DebugInfo, LogLevel.Level3);
			protocol.SetParameter(Parameter.importstatus_35512, 1);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using QAction_3;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Set Full Import PAth.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			string[] asPathSections = Array.ConvertAll((object[])protocol.GetParameters(new uint[] { Parameter.filetoimport_35507, Parameter.importpath_35514 }), Convert.ToString);
			string sFile = asPathSections[0].EndsWith(".xml") ? asPathSections[0] : String.Format("{0}.xml", asPathSections[0]);
			string sPath = asPathSections[1].EndsWith("\\") ? asPathSections[1] : String.Format("{0}\\", asPathSections[1]);
			string sFullPath = String.Format("{0}{1}", sPath, sFile);

			protocol.SetParameter(Parameter.importfullpath_35515, sFullPath);
			protocol.CheckTrigger((int)ProtocolTriggers.AfterPressImportButton_7);
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

