using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Text;
using System.Xml.Linq;

using Skyline.DataMiner.Scripting;

using SLNetMessages = Skyline.DataMiner.Net.Messages;

/// <summary>
/// DataMiner QAction Class: Parse SRT Config.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			object[] paramValues = (object[])protocol.GetParameters(new uint[] { Parameter.srtinputconfigresponseget_35062, Parameter.srtinputconfigcodeget_35061});
			string response = Convert.ToString(paramValues[0]);
			string code = Convert.ToString(paramValues[1]);

			if (String.IsNullOrEmpty(response) || !code.Contains("200"))
			{
				return;
			}

			XDocument xDoc = XDocument.Parse(response);

			if (!xDoc.Descendants("srt").Any())
			{
				return;
			}

			object[] keys = (object[])((object[])protocol.NotifyProtocol((int)SLNetMessages.NotifyType.NT_GET_TABLE_COLUMNS, Parameter.Srtstatustable.tablePid, new uint[] { Parameter.Srtstatustable.Idx.srtstatusinstance_32501 }))[0];
			List<string> existingKeys = Array.ConvertAll(keys, x => Convert.ToString(x)).ToList();

			var newKeys = new List<string>();
			List<object> aoKeys = new List<object>();
			List<object> stream = new List<object>();
			List<object> type = new List<object>();
			List<object> remotePort = new List<object>();
			List<object> listeningPort = new List<object>();

			foreach (var srt in xDoc.Descendants("srt"))
			{
				string sCardId = srt.Descendants("card").First().Value;
				string sPortId = srt.Descendants("port").First().Value;
				string sStream = srt.Descendants("stream").First().Value;
				string rowKey = String.Format("{0}/{1}/{2}", sCardId, sPortId, sStream);

				if (!existingKeys.Contains(rowKey))
				{
					continue;
				}

				aoKeys.Add(rowKey);
				stream.Add(Convert.ToInt32(sStream));
				type.Add(srt.Descendants("type").First().Value);
				remotePort.Add(srt.Descendants("remoteport").First().Value);
				listeningPort.Add(srt.Descendants("listenport").First().Value);

				newKeys.Add(rowKey);
			}

			object[] pids = new object[]
			{
				Parameter.Srtstatustable.tablePid,
				Parameter.Srtstatustable.Pid.srtstatusstream_32502,
				Parameter.Srtstatustable.Pid.srtstatustype_32538,
				Parameter.Srtstatustable.Pid.srtstatusremoteport_32539,
				Parameter.Srtstatustable.Pid.srtstatuslisteningport_32540,
			};

			object[] values = new object[]
			{
				aoKeys.ToArray(),
				stream.ToArray(),
				type.ToArray(),
				remotePort.ToArray(),
				listeningPort.ToArray(),
			};

			protocol.NotifyProtocol((int)SLNetMessages.NotifyType.NT_FILL_ARRAY_WITH_COLUMN, pids, values);

		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|" + protocol.GetTriggerParameter() + "|Run|Exception thrown:" + Environment.NewLine + ex, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
