/// <summary>
/// DataMiner QAction Class: QActionName.
/// </summary>
public static class QAction
{
	/// <summary>
	/// The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocolExt protocol)
	{
		try
		{
			object[] paramValues = (object[])protocol.GetParameters(new uint[] { Parameter.ipsettingsconfigcodeget_35066, Parameter.ipsettingsconfigresponseget_35067 });
			string response = Convert.ToString(paramValues[1]);
			string code = Convert.ToString(paramValues[0]);

			if (String.IsNullOrEmpty(response) || !code.Contains("200"))
			{
				return;
			}

			var ipSettingsConfigQaRowList = new List<QActionTableRow>();

			response = Regex.Replace(response, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

			XDocument xDoc = XDocument.Parse(response);

			if (!xDoc.Descendants("eth").Any())
			{
				return;
			}

			var ipSettingsXML = xDoc.Descendants("port");

			foreach (var port in ipSettingsXML)
			{
				var ipSettingsConfigQaRow = new IpsettingstableQActionRow
				{
					Ipsettingstableportid_40001 = Convert.ToString(port.Element("portid").Value),
					Ipsettingstablename_40002 = Convert.ToString(port.Element("name")?.Value),
					Ipsettingstableipversion_40003 = Convert.ToString(port.Element("ipver")?.Value),
					Ipsettingstableipv4addr_40004 = Convert.ToString(port.Element("ipv4addr")?.Value),
					Ipsettingstableipv4mask_40005 = Convert.ToString(port.Element("ipv4mask")?.Value),
					Ipsettingstableipv4defgw_40006 = Convert.ToString(port.Element("ipv4defgw")?.Value),
					Ipsettingstablephymode_40007 = Convert.ToString(port.Element("phymode")?.Value),
					Ipsettingstableipv4dns1_40008 = Convert.ToString(port.Element("ipv4dns1")?.Value),
					Ipsettingstableipv4dns2_40009 = Convert.ToString(port.Element("ipv4dns2")?.Value),
					Ipsettingstableipv4dns3_40010 = Convert.ToString(port.Element("ipv4dns3")?.Value),
					Ipsettingstabledhcpsettings_40011 = Convert.ToString(port.Element("dhcpEnable")?.Value),
				};

				ipSettingsConfigQaRowList.Add(ipSettingsConfigQaRow);
			}

			if (ipSettingsConfigQaRowList.Count > 0)
			{
				protocol.ipsettingstable.FillArray(ipSettingsConfigQaRowList);
			}
		}
		catch (Exception ex)
		{
			protocol.Log($"QA{protocol.QActionID}|{protocol.GetTriggerParameter()}|Run|Exception thrown:{Environment.NewLine}{ex}", LogType.Error, LogLevel.NoLogging);
		}
	}
}

