using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Setting SRT Table.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			var stream = protocol.RowKey();
			var triggerParam = protocol.GetTriggerParameter();

			var parameterName = String.Empty;

			if (!SrtDictionaryPairs().TryGetValue(triggerParam, out parameterName))
			{
				return;
			}

			var value = Convert.ToString(protocol.GetParameter(triggerParam));

			string url = "ws/v2/service_cfg/input/srt?stream=" + stream + "&" + parameterName + "=" + value;

			protocol.SetParameter(Parameter.inputsconfigurationurlpost_900, url);

			protocol.CheckTrigger((int)ProtocolTriggers.SrtConfigPostTrigger_225);
		}
		catch (Exception e)
		{
			protocol.Log("QA" + protocol.QActionID + "|Setting Zixi Input Via Post|Failed because " + e, LogType.Error, LogLevel.NoLogging);
		}
	}

