using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Xml;
using System.Xml.Linq;
using Skyline.DataMiner.Scripting;

public class QAction
{
	/// <summary>
	/// Parse.
	/// </summary>
	/// <param name="protocol">Link with Skyline Dataminer.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			int iTriggerParameter = protocol.GetTriggerParameter();
			string sXML = Convert.ToString(protocol.GetParameter(iTriggerParameter));  // XML Response for Channel Settings
			if (string.IsNullOrEmpty(sXML))
			{
				return;
			}

			sXML = Regex.Replace(sXML, "&(?!(amp|apos|quot|lt|gt);)", "&amp;");

			XDocument xDoc = XDocument.Parse(sXML);
			switch (iTriggerParameter)
			{
				case 317:
					ProcessRecordChanNumber(protocol, xDoc);
					break;

				case 517:
					ProcessRecordNamePmtPcr(protocol, xDoc);
					break;
				default:
					// Do nothing
					break;
			}
		}
		catch (Exception ex)
		{
			protocol.Log("QA" + protocol.QActionID + "|Run|Error trying to Parse with exception: " + ex, LogType.Error, LogLevel.NoLogging);
		}
	}

	private static void ProcessRecordNamePmtPcr(SLProtocol protocol, XDocument xDoc)
	{
		if (xDoc.Descendants("record").Any())
		{
			string sChname = xDoc.Descendants("record").Descendants("chname").Any() ? xDoc.Descendants("record").Descendants("chname").FirstOrDefault().Value : "-1";
			string sPmtpid = xDoc.Descendants("record").Descendants("pmtpid").Any() ? xDoc.Descendants("record").Descendants("pmtpid").FirstOrDefault().Value : "-1";
			string sPcrpid = xDoc.Descendants("record").Descendants("pcrpid").Any() ? xDoc.Descendants("record").Descendants("pcrpid").FirstOrDefault().Value : "-1";

			object[] allValues5 = new object[] { sChname, sPmtpid, sPcrpid };
			int[] paramsIds5 = new[] { Parameter.channelname_15500, Parameter.pmt_15501, Parameter.pcr_15502 };
			protocol.SetParameters(paramsIds5, allValues5);
		}
	}

	private static void ProcessRecordChanNumber(SLProtocol protocol, XDocument xDoc)
	{
		if (xDoc.Descendants("record").Any())
		{
			object sChn = xDoc.Descendants("record").Descendants("chn").Any() ? xDoc.Descendants("record").Descendants("chn").FirstOrDefault().Value : "-1";
			protocol.SetParameter(Parameter.channelnumber_15503, sChn);
		}
	}
}

using System;
using QAction_3;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Parse Response Write MOIP.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			int trigger = 0;

			switch (protocol.GetTriggerParameter())
			{
				case Parameter.rf1response_write_737:
					trigger = (int)ProtocolTriggers.OnRfResponse_168;
					break;

				case Parameter.asiresponse_write_745:
					trigger = (int)ProtocolTriggers.OnAsiSettingsResponse_178;
					break;

				case Parameter.moipresponse_write_747:
					trigger = (int)ProtocolTriggers.OnMoipServiceSettingsResponse_177;
					break;

				case Parameter.asisettings_writeresponse_749:
					trigger = (int)ProtocolTriggers.OnAsiOutputSettingsResponse_179;
					break;

				case Parameter.moipsettings_writeresponse_751:
					trigger = (int)ProtocolTriggers.OnMoipSettingsResponse_156;
					break;

				case Parameter.videosetupsettings_writeresponse_753:
					trigger = (int)ProtocolTriggers.OnVideoSetupResponse_157;
					break;

				case Parameter.arsettings_writeresponse_755:
					trigger = (int)ProtocolTriggers.OnAspectRatioSettingsResponse_158;
					break;

				case Parameter.subtitlesettings_writeresponse_757:
					trigger = (int)ProtocolTriggers.OnSubtitleSettingsResponse_159;
					break;

				case Parameter.ccsettings_writeresponse_759:
					trigger = (int)ProtocolTriggers.OnClosedCaptionResponse_160;
					break;

				case Parameter.vbisettings_writeresponse_761:
					trigger = (int)ProtocolTriggers.OnVbiSettingsResponse_161;
					break;

				case Parameter.vbisettings_writeresponse1_763:
					trigger = (int)ProtocolTriggers.OnCaSettingsResponse_162;
					break;

				case Parameter.aud1settings_writeresponse_802:
					trigger = (int)ProtocolTriggers.OnAudio1SettingsResponse_163;
					break;

				case Parameter.aud2settings_writeresponse_804:
					trigger = (int)ProtocolTriggers.OnAudio2SettingsResponse_164;
					break;

				case Parameter.aud3settings_writeresponse_806:
					trigger = (int)ProtocolTriggers.OnAudio3SettingsResponse_165;
					break;

				case Parameter.aud4settings_writeresponse_808:
					trigger = (int)ProtocolTriggers.OnAudio4SettingsResponse_166;
					break;

				case Parameter.pesettings_writeresponse_818:
					trigger = (int)ProtocolTriggers.OnPeSettingsResponse_167;
					break;

				case Parameter.abrinputconfigurationresponsepost_925:
					trigger = (int)ProtocolTriggers.OnAbrSettingsResponse_172;
					break;

				case Parameter.zixiinputconfigurationresponsepost_927:
					trigger = (int)ProtocolTriggers.OnZixiSettingsResponse_174;
					break;

				case Parameter.moip2outputstreamconfigurationresponsepost_931:
					trigger = (int)ProtocolTriggers.OnMoip2OutputStreamSettingsResponse_180;
					break;

				case Parameter.xmlcisetupsetresp_243:
					trigger = (int)ProtocolTriggers.Cfgcaci_1001;
					break;

				case Parameter.srtinputconfigurationresponsepost_35064:
					trigger = (int)ProtocolTriggers.OnSrtConfigResponse_175;
					break;

				case Parameter.moipoutputstreamconfigurationresponsepost_929:
					trigger = (int)ProtocolTriggers.OnMoipOutputStreamSettingsResponse_176;
					break;

				case Parameter.ipsettingsconfigresponsepost_35069:
					trigger = (int)ProtocolTriggers.OnIpSettingResponse_173;
					break;
				default:
					// Do nothing
					break;
			}

			if (trigger < 1)
			{
				return;
			}

			protocol.CheckTrigger(trigger);
		}
		catch (Exception e)
		{
			protocol.Log("QA" + protocol.QActionID + "|Parsing Response Write|Failed because " + e, LogType.Error, LogLevel.NoLogging);
		}
	}
}

using System;
using System.Collections.Generic;
using QAction_3;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: write IP Settings Table.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			var port = protocol.RowKey();
			var triggerParam = protocol.GetTriggerParameter();

			var parameterName = String.Empty;

			if (!IPDictionaryPairs().TryGetValue(triggerParam, out parameterName))
			{
				return;
			}

			var value = Convert.ToString(protocol.GetParameter(triggerParam));

			string url = "ws/v2/device_ctl/eth/port?portid=" + port + "&" + parameterName + "=" + value;

			protocol.SetParameter(Parameter.inputsconfigurationurlpost_900, url);

			protocol.CheckTrigger((int)ProtocolTriggers.IpSettingsPostTrigger_231);
		}
		catch (Exception ex)
		{
			protocol.Log($"QA{protocol.QActionID}|{protocol.GetTriggerParameter()}|Run|Exception thrown:{Environment.NewLine}{ex}", LogType.Error, LogLevel.NoLogging);
		}
	}

	private static Dictionary<int, string> IPDictionaryPairs()
	{
		var ipDictPairs = new Dictionary<int, string>
		{
			{ Parameter.Write.ipsettingstableipversion_40053, "ipver" },
			{ Parameter.Write.ipsettingstableipv4addr_40054, "ipv4addr" },
			{ Parameter.Write.ipsettingstableipv4mask_40055, "ipv4mask" },
			{ Parameter.Write.ipsettingstableipv4defgw_40056, "ipv4defgw" },
			{ Parameter.Write.ipsettingstablephymode_40057, "phymode" },
			{ Parameter.Write.ipsettingstableipv4dns1_40058, "ipv4dns1" },
			{ Parameter.Write.ipsettingstableipv4dns2_40059, "ipv4dns2" },
			{ Parameter.Write.ipsettingstableipv4dns3_40060, "ipv4dns3" },
			{ Parameter.Write.ipsettingstabledhcpsettings_40061, "dhcpEnable" },
		};
		return ipDictPairs;
	}
}

using System;
using Skyline.DataMiner.PollingManager;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Polling Manager - Process.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			int triggerID = protocol.GetTriggerParameter();
			if (triggerID == 60091)
			{
				PollingManagerContainer.GetManager(protocol, true);
			}
			else
			{
				PollingManagerContainer
					.GetManager(protocol)
					.CheckForUpdate();
			}
		}
		catch (Exception ex)
		{
			protocol.Log(
				$"QA{protocol.QActionID}|{protocol.GetTriggerParameter()}|Polling Manager - Process|Exception thrown:{Environment.NewLine}{ex}",
				LogType.Error,
				LogLevel.NoLogging);
		}
	}
}

using System;
using Skyline.DataMiner.PollingManager;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Polling Manager - ContextMenu.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	/// <param name="contextMenu">The contextMenu data.</param>
	public static void Run(SLProtocol protocol, object contextMenu)
	{
		try
		{
			// Handles ContextMenu.
			PollingManagerContainer
				.GetManager(protocol)
				.HandleContextMenu(contextMenu);
		}
		catch (Exception ex)
		{
			protocol.Log(
				$"QA{protocol.QActionID}|{protocol.GetTriggerParameter()}|Polling Manager - Context Menu|Exception thrown:" +
				$"{Environment.NewLine}{ex}",
				LogType.Error,
				LogLevel.NoLogging);
		}
	}
}

using System;
using Skyline.DataMiner.PollingManager;
using Skyline.DataMiner.Scripting;

/// <summary>
///     DataMiner QAction Class: Polling Manager - Sets.
/// </summary>
public static class QAction
{
	/// <summary>
	///     The QAction entry point.
	/// </summary>
	/// <param name="protocol">Link with SLProtocol process.</param>
	public static void Run(SLProtocol protocol)
	{
		try
		{
			// Get trigger row key.
			Trigger trigger = (Trigger)protocol.GetTriggerParameter();
			string rowId = protocol.RowKey();

			// Updates row with specific key that was triggered by specific column.
			PollingManagerContainer
				.GetManager(protocol)
				.HandleRowUpdate(rowId, trigger.ToColumn());
		}
		catch (Exception ex)
		{
			protocol.Log(
				$"QA{protocol.QActionID}|{protocol.GetTriggerParameter()}|Polling Manager - Sets|Exception thrown:{Environment.NewLine}{ex}",
				LogType.Error,
				LogLevel.NoLogging);
		}
	}
}
